import React, {PropTypes} from 'react';
import {bibToSchema} from './../../core/utils/microDataUtils';

/**
 * dumb component and placeholder for rendering the structured representation of an entity long citation (in a glossary for example)
 */
export default class StaticEntityBlock extends React.Component {

  /**
   * propTypes
   * @property {object} entity - the entity resource to contextualize
   * @property {object} contextualizer - the contextualizer params to use for contextualization
   * @property {object} contextualization - the contextualization object
   * @property {object} settings - the set of settings to use for rendering
   */
  static propTypes = {
    entity: PropTypes.object,
    contextualizer: PropTypes.object,
    contextualization: PropTypes.object,
    settings: PropTypes.object
  };

  static defaultProps = {
    contextualizer: {
      showdescription: 'yes'
    }
  };

  /**
   * render a structured representation of the entities mentions, sorted by aliases
   * @return {ReactElement} markup
   */
  renderMentions() {
    const self = this;
    return Object.keys(this.props.entity.aliases).map(function(alias, aliasIndex) {
      return (<p key={alias} className="peritext-static-entity-block-page-mentions-container">
        <span>{alias === 'no-alias' ? '' : alias + ' : '}</span>
        {self.props.entity.aliases[alias].map((entry, index)=> {
          return (<span key={entry.mentionId}>
              p. <a className="peritext-static-entity-block-page-pointer" href={entry.mentionId}></a>
            </span>);
        }).reduce((accu, elem) => {
          return accu === null ? [elem] : [...accu, ', ', elem];
        }, null)}
      </p>);
    });
  }

  /**
   * render component
   * @return {ReactElement} markup
   */
  render() {
    const itemType = bibToSchema(this.props.entity.bibType);
    return (
      <section
        className="peritext-static-entity-block-container"
        name={'peritext-static-entity-block-' + this.props.entity.id}
        id={'peritext-static-entity-block-' + this.props.entity.id}
        itemProp="mentions"
        value="mentions"
        itemScope
        itemType={'http://schema.org/' + itemType}
        typeof={itemType}
        resource={this.props.entity.id}
      >
        <h5
          className="peritext-static-entity-block-name">
          {itemType === 'Person' ?
            [<span
              itemProp="familyName"
              property="familyName"
              key="familyName"
            >{this.props.entity.lastname}</span>,
            <span key="separator1"> (</span>,
            <span
              itemProp="givenName"
              property="givenName"
              key="givenName"
            >{this.props.entity.firstname}</span>,
            <span key="separator2">)</span>]
            :
            <span
              property="name"
              itemProp="name"
            >{this.props.entity.name}</span>
          }
        </h5>

        {this.props.entity.aliases ?
            this.renderMentions()
            : ''}

        {this.props.entity.description && this.props.contextualizer.showdescription === 'yes' ?
          <p className="peritext-static-entity-block-description">
            {this.props.entity.description}
          </p>
          : ''}
      </section>
    );
  }
}

import React, {PropTypes} from 'react';

import {bibToSchema} from './../../core/utils/microDataUtils';
import renderContents from './../../core/utils/componentsFactory';

/**
 * dumb component and placeholder for rendering the structured representation of an entity citation
 */
export default class StaticEntityInline extends React.Component {

  /**
   * propTypes
   * @property {object} entity - the entity resource to contextualize
   * @property {string} sectionId - the host section id (used for identifying the element)
   * @property {object} contextualization - the contextualization object
   * @property {array} contents - the pseudo-dom js representation of contextualization's pointer contents
   */
  static propTypes = {
    entity: PropTypes.object,
    sectionId: PropTypes.string,
    contextualization: PropTypes.object,
    contents: PropTypes.array
  };

  static defaultProps = {
  };

  /**
   * render
   * @return {ReactElement} markup
   */
  render() {
    const itemType = bibToSchema(this.props.entity.bibType);
    return (
      <a
        className="peritext-static-entity-container-inline"
        name={'peritext-content-entity-inline-' + this.props.sectionId + '-' + this.props.contextualization.id}
        id={'peritext-static-entity-inline-' + this.props.sectionId + '-' + this.props.contextualization.id}
        href={'#peritext-static-entity-block-' + this.props.entity.id}
        itemProp="mentions"
        value="mentions"
        itemScope
        itemType={'http://schema.org/' + itemType}
        typeof={itemType}
        resource={this.props.entity.id}
      >
        {itemType === 'Person' ?
          [<span
            style={{display: 'none'}}
            itemProp="givenName"
            property="givenName"
            key="givenName"
          >{this.props.entity.firstname}</span>,
          <span
            style={{display: 'none'}}
            itemProp="familyName"
            property="familyName"
            key="familyName"
          >{this.props.entity.lastname}</span>]
          :
          <span
            style={{display: 'none'}}
            property="name"
            itemProp="name"
          >{this.props.entity.name}</span>
        }
        {<span>{renderContents(this.props.contents)}</span>}
      </a>
    );
  }
}

/**
 * Glossary contextualizer that resolve sections data according to contextualization+settings params
 * @module contextualizers/glossary
 */
import { get as getByPath } from 'object-path';

import StaticEntityInline from './StaticEntityInline';
import StaticEntityBlock from './StaticEntityBlock';

/**
 * Handle an inline contextualization for static outputs
 * @param {Object} inputDocument - The representation of the peritext document to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newDocument - the updated representation of the peritext document in which the contextualization was made
 */
export const contextualizeInlineStatic = (inputDocument, inputContextualization, settings) => {
  const document = Object.assign({}, inputDocument);
  const contextualization = Object.assign({}, inputContextualization);
  const sectionId = contextualization.nodePath[0];
  const path = ['sections', ...contextualization.nodePath.slice()];
  const node = getByPath(document, path);
  const entity = document.resources[contextualization.resources[0]];
  let contents = node.children;
  if (!contents || (contents[0] && contents[0].text.trim().length === 0)) {
    contents = document.contextualizers[contextualization.contextualizer].alias
              || entity.name
              || entity.firstname + ' ' + entity.lastname;
    contents = [{
      node: 'text',
      text: contents
    }];
  }
  node.props = {
    contextualization,
    entity,
    contents,
    sectionId
  };
  node.special = true;
  node.tag = StaticEntityInline;
  return document;
};

/**
 * Handle a block contextualization for static outputs
 * @param {Object} inputDocument - The representation of the peritext document to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newDocument - the updated representation of the peritext document in which the contextualization was made
 */
export const contextualizeBlockStatic = (inputDocument, inputContextualization, settings) => {
  const document = Object.assign({}, inputDocument);
  const contextualization = Object.assign({}, inputContextualization);
  const path = ['sections', ...contextualization.nodePath.slice()];
  const node = getByPath(document, path);
  const entity = contextualization.resources[0];
  node.props = {
    entity,
    contextualization,
    settings
  };
  node.special = true;
  node.tag = StaticEntityBlock;
  return document;
};

/**
 * Handle an inline contextualization for dynamic outputs
 * @param {Object} inputDocument - The representation of the peritext document to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newDocument - the updated representation of the peritext document in which the contextualization was made
 */
export const contextualizeInlineDynamic = contextualizeInlineStatic;
// export const contextualizeInlineDynamic = (inputDocument, contextualization, settings) => {
//   return inputDocument;
// };

/**
 * Handle a block contextualization for dynamic outputs
 * @param {Object} inputDocument - The representation of the peritext document to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newDocument - the updated representation of the peritext document in which the contextualization was made
 */
export const contextualizeBlockDynamic = contextualizeBlockStatic;
