       captionContent,
       figureNumber: contextualization.figureNumber,
       id: figureId
     }
   };

   if (settings.figuresPosition === 'inline') {
     const nodeBlockIndex = path[3];
     section.contents[nodeBlockIndex] = figure;
   } else {
