/**
 * Assets controller - a set of pure functions for interacting with an assets source (through a connector)
 * @module controllers/assetsController
 */

let connector;
let connectorName;
let tempConnectorName;

/**
* Updates the active connector module according to connection params
* @param {Object} params - connection params
*/
const updateConnector = (params)=> {
  tempConnectorName = params.connector;
  if (tempConnectorName !== connectorName) {
    connectorName = tempConnectorName;
    connector = require('./../../connectors/' + params.connector);
  }
};

/**
 * Provides the URI of a specific assets file from connector
 * @param {array|string} path - the path of the assets file to look for
 * @param {Object} params - connection params
 * @return {function(connectorError: error, uri: string)} callback - possible errors and the uri
*/
export const getAssetUri = (path, params, callback)=> {
  updateConnector(params);
  connector.getAssetUri({path, params}, callback);
};

/**
* Returns an appropriate connector function for directly reading a file from assets source
* @param {Object} params - connection params
* @return {function(err: error, res: Object)} readFunction - the connector function for directly reading a file contents from assets source
*/
export const getReader = (params) =>{
  updateConnector(params);
  return connector.readFromPath;
};

/**
 * Contents controller - a set of pure functions for parsing and serializing fsTree representations to peritextSections representations
 * @module controllers/contentsController
 */

import {
  waterfall,
  mapSeries,
  ensureAsync
} from 'async';
import {
  parseDocument,
  serializeDocument
} from '../converters/documentConverter';
import { diff } from 'deep-diff';
import * as filesystem from '../../connectors/filesystem';
let connector;
let connectorName;
let tempConnectorName;

/**
* Updates the active connector module according to connection params
* @param {Object} params - connection params
*/
const updateConnector = (params) => {
  tempConnectorName = params.connector;
  if (tempConnectorName !== connectorName) {
    connectorName = tempConnectorName;
    if ( connectorName === 'filesystem' ) {
      connector = filesystem;
    }
    // this method is better but does not work when integrating the lib with webpack-based app (to investigate)
    // connector = require('./../../../connectors/' + params.connector);
  }
};

/**
* Resolves a difference object through Create/Update/Delete operations
* @param {Object} difference - the difference object specifying type of difference and details
* @param {Object} params - connection params
* @param {Object} connect - connector module
* @param {function(connectorError: error, connectorResult)} callback - provided by connector
*/
const applyDifference = (difference, params, connect, callback) => {
  let item;
  switch (difference.kind) {
  // new element
  case 'N':
    item = difference.rhs;
    return connect.createFromPath({
      path: item.path,
      params,
      type: item.type,
      overwrite: true,
      stringContents: (item.stringContents || '')
    }, callback);
  // delete element
  case 'D':
    return connect.deleteFromPath({
      path: difference.lhs.path,
      params
    }, callback);
  // edit element
  case 'E':
    if (difference.path.pop() === 'path') {
      return callback();
    }
    console.log('unhandled edit difference ', difference);
    break;
  // change in array
  case 'A':
    switch (difference.item.kind) {
    case 'N':
      item = difference.item.rhs;
      return waterfall([
        (containerCb) => {
          connect.createFromPath({
            path: item.path,
            params,
            type: item.type,
            overwrite: true,
            stringContents: (item.stringContents || '')
          }, containerCb);
        },
        (childrenCb)=> {
          if (item.children) {
            return mapSeries(item.children, ensureAsync((child, childCb)=>{
              return connect.createFromPath({
                path: child.path,
                params,
                type: child.type,
                overwrite: true,
                stringContents: (child.stringContents || '')
              }, childCb);
            }), childrenCb);
          }
          // default callback
          return childrenCb(null);
        }
      ], callback);

    case 'D':
      item = difference.item.lhs;
      return connect.deleteFromPath({
        path: item.path,
        params
      }, callback);

    case 'E':
      item = difference.item.rhs;
      return waterfall([
        // create root
        (cb)=> {
          connect.updateFromPath({
            path: item.path,
            params,
            stringContents: (item.stringContents || '')
          }, cb);
        },
        // update children
        (cb)=> {
          if (item.children) {
            mapSeries(item.children, ensureAsync((child, cb2)=> {
              connect.updateFromPath({path: child.path, params, stringContents: (child.stringContents || '')}, cb2);
            }), cb);
          } else cb();
        }
      ], callback);

    default:
      console.log('unhandled difference ', difference);
      break;
    }
    break;
  default:
    console.log('unhandled difference ', difference);
    break;
  }
  callback();
};

/**
  * @param {Object} params - connection params
  * @param {Object} models - peritext models to use
  * @param {Object} parametesr - language templates parameters - with which expressions are templates or includes wrapped ?
  * @param {function(parsingErrors: error, results:Object)} callback - block errors and a result object containing section and an array of warnings+parsing errors
  */
export const updateFromSource = (params, models, parameters, callback) => {
  updateConnector(params);
  waterfall([
    (cb)=> {
      connector.readFromPath({
        params, path: [],
        depth: true,
        parseFiles: true
      }, (err, results)=> {
        cb(err, results);
      });
    },
    (tree, cb)=> {
      parseDocument({tree, models, parameters}, cb);
    }
  ],
  (err, results)=> {
    callback(err, results);
  });
};

/**
* Serializes the representation of a series of peritext sections to an fsTree representation by "diffing" new fsTree with previous fsTree
* then makes a diff list with deep-diff
* then monitor source tree updating (with C.U.D. operations) accordingly
* @param {Object} params - connection params
* @param {array} document - the peritext document to serialize
* @param {Object} models - peritext models to use
* @param {Object} oldFsTree - the previous fsTree representation to compare with the new
* @param {function(serializingErrors: error, sections:array)} callback - errors and input sections
*/
export const updateToSource = (params, document, models, oldFsTree, callback) => {
  updateConnector(params);
  const newFsTree = serializeDocument({
    document,
    models,
    basePath: params.basePath
  });
  const differences = diff(oldFsTree, newFsTree);
  mapSeries(differences, ensureAsync((difference, cb)=> {
    applyDifference(difference, params, connector, cb);
  }), (errors)=> {
    callback(errors, newFsTree);
  });
};

/**
 * This module parses and serializes bibTex objects
 * @module converters/bibTexConverter
 */

/**
 * homemade bibTeX syntax parser (performance could be improved I guess)
 * it needs a second pass to parse {} in values and analyze them against a model - that should be done elsewhere, as this converter just deals with syntax-to-object conversion
 */
class bibTexParser {
  /**
   * constructor
   */
  constructor() {
    this.STATES = ['bibType', 'id', 'properties'];
    this.testing = false;
  }

  /**
   * Adds a key+value prop to the resource's javascript representation object
   * @param {object} obj - the resource representation
   * @param {string} key - the key to populate
   * @param {string} value - the value to set
   * @return {object} newObj - the updated resource representation
   */
  addValue(obj, key, value) {
    if (obj[key]) {

      if (Array.isArray(obj[key])) {
        obj[key].push(value);
      }else obj[key] = [obj[key], value];

    }else obj[key] = value;

    return obj;
  }

  /**
   * Consumes a string (stored in this.consumable) representation of a bibTeX expression
   * @return {boolean} exit - a simple exit trigger (but internal props will have been mutated)
   */
  consume() {
    const matchBibType = /^@([^{]+)/;
    const matchId = /^{([^,]+),/;
    const wrappers = [
      ['{', '}'],
      ['"', '"']
    ];

    if (this.testing) {
      console.log('current state: ', this.currentState);
    }

    let match;
    if (this.currentState === 'bibType') {
      match = matchBibType.exec(this.consumable);
      if (match) {
        this.currentObject.bibType = match[1];
        this.consumable = this.consumable.substr(match[1].length + 1);
        this.currentState = this.STATES[1];
        return true;
      }
      this.error = {
        type: 'error',
        preciseType: 'bibParsingError',
        message: 'could not find bibType',
        initialString: this.initialStr
      };
      return true;

    } else if (this.currentState === 'id') {
      match = matchId.exec(this.consumable);
      if (match) {
        this.currentObject.id = match[1];
        this.consumable = this.consumable.substr(match[1].length + 2);
        this.currentState = this.STATES[2];
        return true;
      }
      this.error = {
        type: 'error',
        preciseType: 'bibParsingError',
        message: 'could not find citekey',
        initialString: this.initialStr
      };
      return true;

    /*
     * ``key = value`` structure
     * value is always wrapped inside character couples that vary depending on bibtex implementation (" { ')
     * possibility of nested wrapping (eg {Name {{S}urname}})
     */
    }else if (this.currentState === 'properties') {
      const wrapped = [wrappers[0]];
      let index = 0;
      let mode = 'key';
      let temp = '';
      let tempKey = '';
      let trespassing;
      let character;
      let entering;

      if (this.testing) {
        console.log('initial parser mode: ', mode, ' || stored wrapped pairs : ', wrapped);
      }

      while (wrapped.length > 0) {

        trespassing = index > this.consumable.length - 1;
        character = this.consumable.charAt(index);
        if (this.testing) {
          console.log('===\nNEW PARSING STATE \n===');
          console.log('actual char', character);
          console.log('actual mode: ', mode);
          console.log('actual wrapped pairs: ', wrapped);
        }

        if (trespassing) {
          this.error = {
            type: 'error',
            preciseType: 'bibParsingError',
            message: 'finished to parse bibtex string without finding closing character ' + wrapped[wrapped.length - 1][1],
            initialString: this.initialStr
          };
          if (this.testing)console.log('trespassing');
          return true;
        // end of wrapped expression - if matches with last recorded wrapper's closing character
        } else if (character === wrapped[wrapped.length - 1][1]) {
          wrapped.pop();
          if (wrapped.length > 1) {
            temp += character;
          }
          index = 1;
          if (this.testing)console.log('poped a wrapping level');
        // end of key specification, record tempkey and wait to have found value
        } else if (mode === 'key' && character === '=') {
          tempKey = temp.trim();
          if (this.testing)console.log('encountered a = in key mode, storing key ', tempKey);
          temp = '';
          mode = 'value';
          index = 1;
        // end of value specification - add value and reboot temp
        }else if (mode === 'value' && wrapped.length < 2 && character === ',') {
          if (this.testing)console.log('end of line, storing prop');
          this.addValue(this.currentObject, tempKey, temp.trim());
          temp = '';
          mode = 'key';
          index = 1;
        // in the middle of some key or value = continue
        } else if (mode === 'value') {
          entering = false;
          // catch wrapper char
          wrappers.some((wrapper) => {
            if (this.consumable.charAt(index) === wrapper[0]) {
              entering = true;
              return wrapped.push(wrapper);
            }
          });
          if (!(entering && wrapped.length <= 2)) {
            temp += character;
          }
          if (this.testing)console.log('continuing in value ', temp);
          index = 1;
        // default, by security
        } else {
          if (this.testing)console.log('default exit doing nothing');
          // if(this.testing)console.log('current string: ', this. consumable.substr(index));
          temp += character;
          index = 1;
        }
        this.consumable = this.consumable.substr(index);
        if (this.testing)console.log(this.currentObject);

      }
      this.addValue(this.currentObject, tempKey.trim(), temp.trim());

      // add if not empty
      if (Object.keys(this.currentObject).length) {
        this.results.push(Object.assign({}, this.currentObject));
      }
      this.currentObject = {};
      this.consumable = this.consumable.substr(index).trim();
      this.currentState = this.STATES[0];
      return true;
    }
  }


  /**
   * Parses a bibTeX string and callbacks a javascript objet
   * @param {string} str - the string to parse
   * @param {function(error:error, results:Object)} callback
   */
  parse(str, callback) {
    this.currentObject = {};
    this.currentState = this.STATES[0];
    this.results = [];
    this.consumable = str.trim();
    this.initialStr = str.trim();
    this.error = null;
    while (this.error === null && this.consumable.trim().length > 0) {
      this.consume();
    }
    return callback(this.error, this.results);
  }

}

const parser = new bibTexParser();


/**
 * Parses a bibTeX string and returns an object
 * @param {string} str - the bibTeX string to parse
 * @return {function(error: error, result: Object)} callback - error and the resulting object
 */
export const parseBibTexStr = (str, callback) => {
  if (typeof str === 'string') {
    return parser.parse(str, callback);
  }
  return callback(new Error('must input a string'), undefined);
};

const validateBibObject = function(bibObject) {
  if (bibObject.id === undefined) {
    return {
      type: 'error',
      preciseType: 'bibObjectValidationError',
      message: 'bibObject must have a id property',
      bibObject: bibObject
    };
  } else if (bibObject.bibType === undefined) {
    return {
      type: 'error',
      preciseType: 'bibObjectValidationError',
      message: 'bibObject must have a bibType property',
      bibObject: bibObject
    };
  }

  for (const key in bibObject) {
    if (typeof bibObject[key] === 'object' && !Array.isArray(bibObject[key])) {
      return {
        type: 'error',
        preciseType: 'bibObjectValidationError',
        message: 'bibObject cannot contain nested objects',
        bibObject: bibObject
      };
    }
  }

  return true;
};

/**
 * Serializes a javascript object representing a resource or a contextualizer, to a bibTeX-formatted string
 * @param {object} bibObject - the resource/contextualizer representation
 * @return {string} bibStr - the bibTeX mention as a string
 */
export const serializeBibTexObject = (bibObject) => {
  const validated = validateBibObject(bibObject);
  if (validated.type === 'error') {
    return validated;
  }
  let str = '';
  let val;
  for (const key in bibObject) {
    if (bibObject[key]) {
      val = bibObject[key];
      if (Array.isArray(val)) {
        val.forEach((value)=>{
          str += '\t' + key + ' = {' + value + '},\n';
        });
        //  val = val.join(',');
      }else if (key !== 'id' && key !== 'bibType') {
        str += '\t' + key + ' = {' + val + '},\n';
      }
    }
  }
  // removing the last coma
  if (str.length > 1) {
    str = str.substr(0, str.length - 2);
  }
  return `@${bibObject.bibType}{${bibObject.id},
    ${str}
}`;
};

