/**
 * Parses a list of authors as they are formatted in bbiTeX statements
 * @param {string} str - the bibTeX string to parse
 * @return {array} authors - the javascript representation of authors as an array
 */
export const parseBibAuthors = (str) => {
  const authors = str.split(/;|and|et/);
  const additionalInfo = /\(([^)]*)?\)?\(?([^)]*)?\)?/;
  let match;
  return authors.filter((inputStr) =>{
    return inputStr.trim().length > 0;
  }).map((inputAuthorStr) =>{
    let workingStr = inputAuthorStr;
    let authorStr = '';
    let firstName;
    let lastName;
    let role = 'author';
    let information;
    match = inputAuthorStr.match(additionalInfo);
    if (match) {
      workingStr = workingStr.replace(match[0], '');
      if (match[1]) {
        role = match[1].trim();
      }
      if (match[2]) {
        information = match[2];
        information = information.trim();
      }
    }
    const lastNameMatch = workingStr.match(/{([^}]*)}/);
    if (lastNameMatch) {
      lastName = lastNameMatch[1].trim();
      authorStr = [workingStr.substr(0, lastNameMatch.index), workingStr.substr(lastNameMatch.index + lastNameMatch[0].length)].join('');
      firstName = authorStr.replace(',', '').trim();
    } else {
      let vals = workingStr.split(',');
      if (vals.length > 1) {
        firstName = vals[1].trim();
        lastName = vals[0].trim();
      } else if (workingStr.trim().indexOf(' ') > -1) {
        vals = workingStr.trim().split(' ');
        firstName = vals.shift().trim();
        lastName = vals.join(' ').trim();
      } else {
        lastName = workingStr.trim();
        firstName = '';
      }
    }
    const id = (role + '-' + firstName + lastName).toLowerCase().replace(' ', '-');
    return {firstName, lastName, role, information, id};
  });
};

/**
 * Parses a bibTeX-like contextualization statement given as string and returns an object
 * @param {string} inputStr - the bibTeX string to parse
 * @return {Object} contextualization - the contextualization object
 */
export const parseBibContextualization = (inputStr) => {
  const bracketsRE = /([^=^,]+)=(?:{)([^}]*)(?:}),?/g;
  const quoteRE = /([^=^,]+)="([^"]*)",?/g;
  const paramsObject = {};


  const str = inputStr.substr(1, inputStr.length - 2).replace(/&quot;/g, '"');
  let match;
  let key;
  let expression;
  let subObject;
  let newObj;

  while ((match = bracketsRE.exec(str)) !== null) {
    key = match[1].trim();
    expression = match[2].trim().split(',');
    // simple
    if (expression.length === 1) {
      paramsObject[key] = expression[0];
    // nested
    } else {
      subObject = {};

      let isArray;

      expression = expression.map((exp) =>{
        const split = exp.split('=');
        if (split.length === 2) {
          isArray = false;
          return {
            key: split[0],
            value: split[1]
          };
        }
        isArray = true;
        return exp;
      });

      if (!isArray) {
        subObject = expression.reduce((obj, exp)=>{
          obj[exp.key] = exp.value;
          return obj;
        }, subObject);
      }

      newObj = Object.assign({}, subObject);

      if (paramsObject[key] === undefined) {
        paramsObject[key] = newObj;
      } else if (Array.isArray(paramsObject[key])) {
        paramsObject[key].push(newObj);
      } else paramsObject[key] = [paramsObject[key], newObj];
    }

  }

  while ((match = quoteRE.exec(str)) !== null) {
    key = match[1].trim();
    expression = match[2].trim();
    paramsObject[key] = expression;
  }

  return paramsObject;
};


const resolveNested = (subVal) => {
  if (typeof subVal !== 'string') {
    return subVal;
  }
  let expression = subVal.split(',');
  let subObject;
  let newObj;
  // simple
  if (expression.length === 1) {
    return subVal;
  // nested
  }
  subObject = {};
  let isArray;
  expression = expression.map((exp) =>{
    const split = exp.split('=');
    if (split.length === 2) {
      isArray = false;
      return {
        key: split[0],
        value: split[1]
      };
    }
    isArray = true;
    return exp;
  });

  if (!isArray) {
    subObject = expression.reduce((obj, exp)=>{
      obj[exp.key] = exp.value;
      return obj;
    }, subObject);
  }

  newObj = Object.assign({}, subObject);
  return newObj;
};

/**
 * Parses bibTeX nested values inside a bibObject
 * @param {Object} bibObject - the object to parse
 * @return {Object} newObject - the resulting updated object
 */
export const parseBibNestedValues = (bibObject) => {
  const newObject = Object.assign({}, bibObject);
  let subVal;
  for (const index in newObject) {
    if (newObject[index]) {
      subVal = newObject[index];
      if (Array.isArray(subVal)) {
        newObject[index] = bibObject[index].map(resolveNested);
      }else {
        newObject[index] = resolveNested(newObject[index]);
      }
    }
  }
  return newObject;
};

/**
 * This module cleans resources and metadata from a naive (resource concatenated) tree
 * @module converter/documentConverter/cleanNaiveTree
 */
import slug from 'slug';

/**
 * Resolves resource and metadata statements from a naive representation of a section
 * @param {Object} params - the cleaning params
 * @param {array} params.errors - the inherited parsing errors to pass along to the next step
 * @param {Object} params.validTree - the tree to clean
 * @param {Object} models - the models to parse the resources with
 * @return {errors: array, validTree: Object} - the possible error, a list of parsing minor errors, and the resulting tree
 */
export const cleanNaiveTree = ({errors = [], validTree}, models) =>{
  const contextualizers = [];
  const naiveTree = Object.assign({}, validTree);
  let metadata;
  const hasResources = naiveTree && naiveTree.resources;
  // has it a resource file
  if (hasResources) {
    naiveTree.resources = naiveTree.resources.filter(function(res) {
      // catch metadata
      let validated;
      // extract contextualizer descriptions
      if (res.bibType === 'contextualizer') {
        contextualizers.push(res);
        return false;
      }
      for (const type in models.sectionTypeModels.acceptedTypes) {
        if (res.bibType === 'peritext' + type) {
          metadata = res;
          return false;
        }
      }

      if (!validated) {
        // verify that the resource type are known
        for (const otherType in models.resourceModels.individual) {
          if (res.bibType === otherType) {
            return true;
          }
        }
      }
      // if not validated, record error and don't take resource
      if (!validated) {
        errors.push({
          type: 'error',
          preciseType: 'invalidResource',
          resourceId: res.id,
          message: 'could not find resource type ' + res.bibType + ' for Resource ID ' + res.id
        });
        return false;
      }
      return true;
    });
  } else naiveTree.resources = [];
  // populate automatic metadata
  if (metadata === undefined && naiveTree.name.charAt(0) !== '_') {
    errors.push({
      type: 'warning',
      preciseType: 'metadataNotFound',
      message: 'no metadata specified for the folder ' + naiveTree.name + ' so it was not taken into account'
    });
    // bootstrap a minimal metadata
    metadata = {
      bibType: 'peritextinherits',
      id: slug(naiveTree.name.toLowerCase()),
      title: naiveTree.name
    };
  }
  // parse children if present
  if (naiveTree.children && naiveTree.name.charAt(0) !== '_') {
    naiveTree.children = naiveTree.children.map(child => {
      return cleanNaiveTree({validTree: child}, models);
    }).filter((result)=>{
      return result.validTree !== undefined;
    })
    .map((result) =>{
      return result.validTree;
    });
  }
  const newErrors = (errors.length > 0) ? errors.reverse() : null;
  return {
    errors: newErrors,
    validTree: Object.assign({}, naiveTree, {metadata}, {contextualizers})
  };
};

/**
 * This module concatenates fsTree contents according to include statements and resolves cases in which several markdown files are in the same directory
 * @module converter/documentConverter/concatTree
 */
import {parseMarkdown as extractIncludes} from './../../converters/markdownIncludesParser';
import {map as asyncMap, waterfall} from 'async';

let resolveFileIncludes;

// Extracts md includes and inline resources descriptions from a file object
const populateElementWithIncludes = (child, params, callback) =>{
  extractIncludes(child.stringContents,
    {
      includeWrappingChars: params.templateWrappingCharacters,
      resWrappingChars: params.inlineResourceDescriptionWrappingCharacters
    }, function(err, {extracted, cleanStr}) {
      if (!err) {
        child.extracted = extracted;
        child.stringContents = cleanStr;
      }
      callback(err, child);
    });
};


// Produces a nested structure of the included files in a given file
const include = (ex, file, mdFilesWithIncludes) =>{
  let fileIndex;
  const fileToInclude = mdFilesWithIncludes
      .find((otherFile, index)=>{
        if (otherFile.name === ex.statement) {
          fileIndex = index;
          return true;
        }
      });
  if (fileToInclude !== undefined) {
    const fileHasIncludes = fileToInclude
                              .extracted
                                .filter((rawExtracted)=>{
                                  return rawExtracted.type === 'includeStatement';
                                }).length > 0;
    if (fileHasIncludes) {
      resolveFileIncludes(fileToInclude, mdFilesWithIncludes);
    }
    file.includes.push(fileToInclude);
    mdFilesWithIncludes.splice(fileIndex, 1);
  }
};

// Monitors the nested including structure population process of a file
const doResolveFilesWithIncludes = (file, mdFilesWithIncludes) =>{
  file.includes = [];
  file.includeStatements = [];
  for (let index = file.extracted.length - 1; index >= 0; index--) {
    const ex = file.extracted[index];
    if (ex.type === 'includeStatement') {
      include(ex, file, mdFilesWithIncludes);
      file.extracted.splice(index, 1);
      file.includeStatements.push(ex);
    }
  }
};

resolveFileIncludes = doResolveFilesWithIncludes;

// Turns non nested file object into include-based nested file objects
const nestIncludes = ({resourcesStr, mdFilesWithIncludes, params}, cb) =>{

  mdFilesWithIncludes.forEach((mdfile) =>{
    resolveFileIncludes(mdfile, mdFilesWithIncludes);
  });
  cb(null, {resourcesStr, mdFilesWithIncludes});
};


// Consumes the nested property 'include' by populating its string contents with includes contents,
// resolved recursively
const buildFinalMdContent = (file) =>{
  let content = file.stringContents;
  const hasIncludes = file.includes && file.includes.length && file.includes.length > 0;
  if (hasIncludes) {
    file.includeStatements
      .filter((ex)=>{
        return ex.type === 'includeStatement';
      })
      .forEach((ex)=>{
        const targetIncluded = file.includes.find((file2)=>{
          return file2.name === ex.statement;
        });
        const contentToInclude = buildFinalMdContent(targetIncluded);
        content = [content.substr(0, ex.index), contentToInclude, content.substr(ex.index)].join('');
      });
  }

  return content;
};

// Turns an array of nested file objects into one single string
const resolveNestedIncludes = ({resourcesStr, mdFilesWithIncludes, params}, cb) =>{
  const contentStr = mdFilesWithIncludes
                      .reduce((str, file) => {
                        return str + buildFinalMdContent(file);
                      }, '');
  cb(null, {contentStr, resourcesStr, params});
};

const concatCustomizers = (newTree) =>{
  return newTree.children
      .filter(child => {
        return child.type === 'directory' && child.name.charAt(0) === '_';
      })
      .map(child => {
        const contents = {};
        child.children.forEach((subChild) =>{
          contents[subChild.name] = subChild.stringContents;
        });
        return {
          type: child.name.substr(1),
          contents
        };
      });
};

/**
 * Turns a fsTree into a dumTree, that is to say a tree which presents .bib resources and .md files contents concatenated by folder (according to inner 'include' statements and then automatically)
 * @param {Object} tree - the fsTree to concatenate
 * @param {Object} params - the language-related parameters
 * @return {errors: Array, dumbTree: Object}  - error and the concatenated tree
 */
export const concatTree = (tree, params) =>{
  const newTree = Object.assign({}, tree);
  // concat .bib res files
  const resources = newTree.children
                    .filter(child => {
                      return child.type === 'file' && child.extname === '.bib';
                    })
                    .reduce((str, child) => {
                      if (child.stringContents !== undefined) {
                        return str + '\n' + child.stringContents;
                      }
                      return str;
                    }, '');

  const mdContents = newTree.children
                    .filter(child => {
                      return child.type === 'file' && child.extname === '.md';
                    });
  const childrenDirs = newTree.children
                    .filter(child => {
                      return child.type === 'directory' && child.name.charAt(0) !== '_';
                    });
  const childrenCustomizers = concatCustomizers(newTree);
  waterfall([
    // extract md files elements includes statements
    (cb) =>{
      asyncMap(mdContents, (child, cback) =>{
        populateElementWithIncludes(child, params, cback);
      }, (err, mdFilesWithIncludes) =>{
        // concat extracted bibtex resources
        const resourcesStr = mdFilesWithIncludes
                      .reduce((str, mdFile) => {
                        let newStr = str;
                        mdFile.extracted
                          .filter((ex)=>{
                            return ex.type === 'resourceStatement';
                          }).forEach((ex)=>{
                            newStr += ex.statement;
                          });
                        return newStr;
                      }, resources);
        cb(null, {resourcesStr, mdFilesWithIncludes, params});
      });
    },
    nestIncludes,
    resolveNestedIncludes
  ], (err, {resourcesStr, contentStr}) =>{
    newTree.resourcesStr = resourcesStr;
    newTree.contentStr = contentStr;
    if (childrenCustomizers.length > 0) {
      newTree.customizers = childrenCustomizers;
    }
    // recursively repeat dat stuff with children dirs
    newTree.children = childrenDirs.map(dir => {
      return concatTree(dir, params).dumbTree;
    });
  });
  return {dumbTree: newTree, errors: []};
};

/**
 * This module converts an fsTree flatfile abstraction to a peritext document representation
 * And it converts aperitext document representation to an fsTree flatfile abstraction
 * @module converters/documentConverter
 */

import {concatTree} from './concatTree';
import {parseTreeResources} from './parseTreeResources';
import {organizeTree} from './organizeTree';
import {propagateData} from './propagateData';
import {cleanNaiveTree} from './cleanNaiveTree';
import {getResourceModel, serializePropAgainstType} from './../../utils/modelUtils';
import {resolveSectionAgainstModels} from './../../resolvers/resolveSectionAgainstModels';
import {resolveResourcesAgainstModels} from './../../resolvers/resolveResourcesAgainstModels';
import {resolveBindings} from './../../resolvers/resolveContextualizations';
import {markdownToJsAbstraction} from '../markdownConverter';
import {serializeBibTexObject, parseBibNestedValues} from '../bibTexConverter';

const sectionListToFsTree = (inputSectionList, basePath) =>{
  const sectionList = inputSectionList.map((section)=>{
    const folderTitle = section.id;
    const relPath = (section.root) ? '' : folderTitle;
    const children = [
      {
        type: 'file',
        extname: '.md',
        name: 'contents.md',
        path: relPath + '/contents.md',
        'stringContents': section.markdownContent
      },
      {
        type: 'file',
        extname: '.bib',
        name: 'resources.bib',
        path: relPath + '/resources.bib',
        'stringContents': section.bibResources
      }
      // todo: customizers
    ];
    const folder = {
      type: 'directory',
      name: section.id,
      extname: '',
      path: relPath + '/',
      root: section.root,
      children
    };
    return folder;
  });

  const root = sectionList.find((section)=>{
    return section.root;
  });
  const children = sectionList.filter((section)=>{
    return !section.root;
  });
  delete root.root;
  root.children = root.children.concat(children);
  root.name = basePath.split('/').pop();
  return root;
};

const serializeMetadata = (metadata, models) => {
  const obj = {};
  Object.keys(metadata).forEach(domain => {
    Object.keys(metadata[domain]).forEach(gKey => {
      const prop = metadata[domain][gKey];
      if (gKey === 'bibType' || !prop.inheritedVerticallyFrom && !prop.inheritedHorizontallyFrom) {
        // format prop name like twitter_title
        const key = domain === 'general' ? gKey : domain + '_' + gKey;
        const model = models.metadataModels[domain][gKey];
        if (model) {
          obj[key] = serializePropAgainstType(prop.value, model.valueType, model);
        } else obj[key] = prop.value;
      }
    });
  });
  obj.bibType = 'peritext' + obj.bibType;
  return serializeBibTexObject(obj);
};

const concatSection = (section, models) =>{
  const metadataStr = serializeMetadata(section.metadata, models);
  return {
    markdownContent: section.markdownContents,
    bibResources: metadataStr,
    customizers: section.customizers,
    id: section.metadata.general.id.value
  };
};

// from documentSectionsList to fsTree
/**
 * Converts a sections' list to a fsTree representation of the resulting sourcedata
 * @param {Object} param - serializing params
 * @param {array} param.sectionList - the list of sections to serialize
 * @param {Object} param.models - the models to use for serializing
 * @param {string} param.basePath - the path to use as basis for determining serializing output paths
 * @return {Object} - returns the filesystem representation of the data
 */
export const serializeDocument = ({document, models, basePath}, callback) =>{
  const sections = Object.keys(document.sections).map(key => document.sections[key]);
  // collect resources and prepare for serialization
  const resources = Object.keys(document.resources)
    .map(key => document.resources[key])
    .filter((resource)=>{
      return !resource.inheritedVerticallyFrom;
    })
    .map((resource) =>{
      const modelList = getResourceModel(resource.bibType, models.resourceModels);
      if (modelList) {
        let model;
        return Object.keys(resource).reduce((obj, key) =>{
          if (resource[key] !== undefined) {
            model = modelList.properties.find((thatModel)=>{
              return thatModel.key === key;
            });
            if (model) {
              obj[key] = serializePropAgainstType(resource[key], model.valueType, model);
            } else obj[key] = resource[key];
          }

          return obj;
        }, {});
      }
      return resource;
    });
  // collect contextualizers and prepare for serialization
  const contextualizers = Object.keys(document.contextualizers)
    .map(key => document.contextualizers[key])
    .filter((contextualizer)=>{
      return contextualizer && !contextualizer.describedInline;
    }).map((contextualizer)=>{
      const modelList = getResourceModel(contextualizer.type, models.contextualizerModels);
      if (modelList) {
        let model;
        const cont = Object.keys(contextualizer).reduce((obj, key) =>{
          if (contextualizer[key] !== undefined) {
            model = modelList.properties.find((thatModel)=>{
              return thatModel.key === key;
            });

            if (model) {
              obj[key] = serializePropAgainstType(contextualizer[key], model.valueType, model);
            } else obj[key] = contextualizer[key];
          }
          return obj;
        }, {});
        cont.bibType = 'contextualizer';
        return cont;
      }
      contextualizer.bibType = 'contextualizer';
      return contextualizer;
    });
  // merge resources and contextualizers in a collection of entities to be rendered as bibTeX
  const bibResources = resources.concat(contextualizers).map(serializeBibTexObject);
  // const globalMetadata = serializeMetadata(document.metadata, models);
  const fsSections = sections.map(section => {
    return concatSection(section, models);
  });
  // adding resources and contextualizers to first section / root
  fsSections[0].bibResources += bibResources.join('\n\n');
  fsSections[0].root = true;
  return sectionListToFsTree(fsSections, basePath);
};

// from fsTree (returned by any connector) to a documentSectionsList usable in app
/**
 * Parses an fsTree representation and renders a list of sections to be used with peritext exporters or as is
 * @param {Object} params - parsing params
 * @param {Object} params.tree - the input fsTree representation
 * @param {Object} params.parameters - language-related parameters
 * @param {Object} params.parent - a possible existing parent section - to use for inheritance phases - suitable for partial document parsing/re-rendering use cases (like with an editor app)
 * @param {Object} params.models - models to use for parsing the data
 * @param {function(error:error, results: Object)} callback - RCC representation of the contents and parsing errors list for UI
 */
export const parseDocument = ({tree, parameters, parent, models}, callback)=> {
  // concat markdown, resources, styles, templates, components, and resolve includes, producing a clean 'dumb tree'
  const {dumbTree, errors: dumbTreeErrors} = concatTree(tree, parameters);
  // parse bibtext to produce resources and metadata props, producing a 'naive tree' of sections
  const {naiveTree, errors: naiveTreeErrors} = parseTreeResources(dumbTree);
  // validate and resolve metadata against their models for all sections
  const {validTree, errors: validTreeErrors} = cleanNaiveTree({validTree: naiveTree}, models);
  // bootstrap errors list
  let errors = dumbTreeErrors.concat(naiveTreeErrors).concat(validTreeErrors);
  // format objects, normalize metadata, and resolve organization statements
  const {document: organizedDocument, errors: documentErrors} = organizeTree({errors, validTree});
  // propagate resources, metadata and customizers vertically (from parents to children sections), metadata lateraly (from metadata models propagation data)
  const {document: richDocument, errors: richErrors} = propagateData({errors: documentErrors, document: organizedDocument, models, parent});
  // resolve data against their models
  const {newResources, newErrors: resErrors} = resolveResourcesAgainstModels(richDocument.resources, models);
  richDocument.resources = newResources;
  errors = errors.concat(richErrors).concat(resErrors);
  // resolve sections against models
  for (const id in richDocument.sections) {
    if (richDocument.sections[id]) {
      const section = richDocument.sections[id];
      const { newErrors: sectionErrors, newSection } = resolveSectionAgainstModels(section, models);
      errors = errors.concat(sectionErrors);
      delete newSection.resources;
      richDocument.sections[id] = newSection;
    }
  }
  // resolve contextualizers nested values
  for (const id in richDocument.contextualizers) {
    if (richDocument.contextualizers[id]) {
      richDocument.contextualizers[id] = parseBibNestedValues(richDocument.contextualizers[id]);
    }
  }
  // parse markdown contents and organize them as blocks lists, and parse+resolve contextualization objects
  richDocument.contextualizations = {};
  for (const id in richDocument.sections) {
    if (richDocument.sections[id]) {
      const {
        errors: sectionErrors,
        section,
        contextualizations,
        contextualizers
      } = markdownToJsAbstraction(richDocument.sections[id], parameters);
      errors = errors.concat(sectionErrors);
      richDocument.sections[id] = section;
      richDocument.contextualizers = Object.assign(richDocument.contextualizers, contextualizers);
      richDocument.contextualizations = Object.assign(richDocument.contextualizations, contextualizations);
    }
  }
  // resolve contextualizers statements against their models
  const {
    errors: globalErrors,
    document: newDocument
  } = resolveBindings({document: richDocument, models});
  const document = newDocument;
  errors = errors.concat(globalErrors).filter(error => error !== null);
  // update summary and document metadata with root
  document.metadata = Object.assign({}, document.sections[document.summary[0]].metadata);
  document.customizers = document.sections[document.summary[0]].customizers;
  document.forewords = document.sections[document.summary[0]];
  document.summary = document.summary.slice(1);
  callback(null, {
    errors,
    document
  });
};

/**
 * This module organizes relations between sections (order, inheritance, generality level)
 * @module converter/documentConverter/organizeTree
 */

const formatMetadata = (metadataObj) =>{
  const output = {};
  let value;
  let keydetail;
  let domain;
  for (let key in metadataObj) {
    if (metadataObj[key] !== undefined) {
      value = metadataObj[key];
      keydetail = key.split('_');
      domain = (keydetail.length > 1) ? keydetail.shift() : 'general';
      key = keydetail.join('_');
      if (output[domain] === undefined) {
        output[domain] = {};
      }
      output[domain][key] = {
        value
      };
    }
  }
  return output;
};

const flattenSections = (tree) =>{
  if (tree.children) {
    const newChildren = tree.children.map(child=>{
      return Object.assign({}, child, {parent: tree.metadata.id});
    });
    const newTree = Object.assign({}, tree);
    return [
      newTree,
      ...newChildren
    ];
  }
  return tree;
};


const formatSection = (section) =>{
  const metadata = formatMetadata(section.metadata);
  let keyedCustomizers;
  if (section.customizers) {
    keyedCustomizers = {};
    section.customizers.forEach((customizer) => {
      keyedCustomizers[customizer.type] = customizer.contents;
    });
  }
  return {
    metadata,
    contents: section.contentStr,
    resources: section.resources,
    parent: section.parent,
    customizers: keyedCustomizers,
    contextualizers: section.contextualizers
  };
};

const formatSections = (sections) =>{
  const formattedSections = sections.map(section => {
    return formatSection(section);
  });
  return formattedSections;
};

const moveInArray = (inputArray, oldIndex, newIndex)=> {
  const array = [...inputArray];
  if (newIndex >= array.length) {
    let toFill = newIndex - array.length;
    while ((toFill--) + 1) {
      array.push(undefined);
    }
  }
  array.splice(newIndex, 0, array.splice(oldIndex, 1)[0]);
  return array;
};

const makeRelations = (inputSections) =>{
  // find parents and predecessors
  let sections = inputSections.map((inputSection) =>{
    const section = Object.assign({}, inputSection);
    // todo : clean the two ways of defining parents (at object root or in metadata)
    if (section.parent && section.metadata.general.parent === undefined) {
      section.metadata.general.parent = {value: section.parent};
    }
    delete section.parent;
    if (section.after && section.metadata.general.after === undefined) {
      section.metadata.general.after = {value: section.after};
    }
    delete section.after;
    return section;
  });
  // order sections
  // move the "no-after" sections to the begining of array
  const hasAfter = sections.filter(section => section.metadata.general.after);
  const hasNoAfter = sections.filter(section => section.metadata.general.after === undefined);
  sections = [...hasNoAfter, ...hasAfter];
  // resolve after statements
  for (let index = sections.length - 1; index >= 0; index--) {
    const section = sections[index];
    // console.log('parsing ', section.metadata.general.id.value, index);
    if (section.metadata.general.after && section.metadata.general.parent) {
      let indexAfter;
      sections.some((section2, thatIndex) =>{
        const id = section2.metadata.general.id.value;
        if (section.metadata.general.after.value === id) {
          indexAfter = thatIndex;
          return true;
        }
      });
      if (indexAfter !== undefined && index !== indexAfter + 1) {
        // console.log('put ', section.metadata.general.id.value, index, ' after ', section.metadata.general.after.value, indexAfter);
        sections = moveInArray(sections, index, indexAfter + 1);
      } else if (indexAfter === undefined) {
        console.error(section.metadata.general.id.value,
          ' is supposed to be after ',
          section.metadata.general.after.value,
          ' but this section does not exist');
      }
    }
  }
  const summary = sections.map(section => section.metadata.general.id.value);
  const orderedSections = sections.map((section, index) => {
    if (index > 0 ) {
      section.metadata.general.after = {
        value: sections[index - 1].metadata.general.id.value
      };
    } else {
      delete section.metadata.general.after;
    }
    return section;
  });
  const outputSections = orderedSections.reduce((output, section) => {
    return Object.assign(output, {[section.metadata.general.id.value]: section});
  }, {});
  return {outputSections, summary};
};

/**
 * Organizes relations between sections
 * @param {Object} params - the organization params
 * @param {array} params.errors - the inherited parsing errors to pass along to next step
 * @param {Object} params.validTree - the tree to process
 * @return {error: error, results: {errors: array, sections: array} - an updated list of parsing errors and updated sections
 */
export const organizeTree = ({errors = [], validTree}) => {
  const flatSections = flattenSections(validTree);
  const formattedSections = formatSections(flatSections);
  const {outputSections: sections, summary} = makeRelations(formattedSections);
  return {
    document: {
      sections,
      summary
    },
    errors
  };
};

/**
 * This module parses resources expressions in .bib files representations
 * @module converter/documentConverter/parseTreeResources
 */
import {parseBibTexStr} from '../bibTexConverter';

/**
 * Converts dumbTree resources representations (as string) to js objects - recursively do the same for tree's children
 * @param {Object} dumbTree - a semi-parsed representation inbetween an fsTree and a more structured representation
 * @return {errors: Array, naiveTree: Object}  - the output representation
 */
export const parseTreeResources = (dumbTree) => {
  const naiveTree = Object.assign({}, dumbTree);
  const errors = [];
  if (naiveTree.resourcesStr) {
    parseBibTexStr(naiveTree.resourcesStr, function(err, resources) {
      naiveTree.resources = resources;
    });
  }
  if (naiveTree.children) {
    naiveTree.children = naiveTree.children.map(child => {
      return parseTreeResources(child).naiveTree;
    });
  }
  return {
    naiveTree,
    errors
  };
};

/**
 * This module handles metadata propagation within and between sections
 * @module converter/documentConverter/propagateData
 */
import {metaStringToCouple} from './../../utils/sectionUtils';

const inheritMetadataFromParent = (section, sectionTypeModels, sections, parentMetadata) => {
  if (parentMetadata === undefined) {
    return section;
  }
  // set final bibType
  if (section.metadata.general.bibType.value === 'section') {
    const parentBibType = parentMetadata.general.bibType.value;
    const parentModel = sectionTypeModels.acceptedTypes[parentBibType];
    if (parentModel) {
      section.metadata.general.bibType = {
        value: parentModel.childrenType,
        inheritedVerticallyFrom: parentMetadata.general.id.value
      };
    }
  }
  // set hierarchical level (parent + 1 or parent + own level)
  const parentLevel = parentMetadata.general.generalityLevel;
  const ownLevel = section.metadata.general.generalityLevel;
  if (parentLevel && ownLevel) {
    section.metadata.general.generalityLevel.value = +parentLevel.value + ownLevel.value;
  } else if (parentLevel) {
    section.metadata.general.generalityLevel = {value: +parentLevel.value + 1};
  } else {
    section.metadata.general.generalityLevel = {value: parentLevel.value};
  }
  // inherit the rest
  for (const domain in parentMetadata) {
    if (parentMetadata[domain]) {
      for (const key in parentMetadata[domain]) {
        if (parentMetadata[domain][key]) {
          // inherit if property does not exist in child
          if (section.metadata[domain] === undefined || section.metadata[domain][key] === undefined) {
            if (section.metadata[domain] === undefined) {
              section.metadata[domain] = {};
            }
            section.metadata[domain][key] = {
              value: parentMetadata[domain][key].value,
              inheritedVerticallyFrom: parentMetadata.general.id.value
            };
          }
        }
      }
    }
  }
  // mark metadata as done
  section.metadataInherited = true;
  return section;
};


const doInheritMetadataFromParent = (inputSection, sectionTypeModels, sections) => {
  let section = Object.assign({}, inputSection);
  let parent = section.metadata.general.parent;
  if (parent && !section.metadataInherited) {
    section.metadataInherited = true;
    parent = sections[parent.value];
    // first, make your parent inherit from its parent
    if (!parent.metadataInherited) {
      parent = doInheritMetadataFromParent(parent, sectionTypeModels, sections);
    }
    // then inherit yourself from your parent
    section = inheritMetadataFromParent(section, sectionTypeModels, sections, parent.metadata);
  }
  section.metadataInherited = true;
  return section;
};


const inheritCustomizersFromParent = (section, sections, parentCustomizers, parentKey) => {
  if (parentCustomizers === undefined) {
    return section;
  }else if (section.customizers === undefined) {
    section.customizers = Object.assign({}, parentCustomizers);
  }else {
    for (const index in section.customizers) {
      if (parentCustomizers[index] !== undefined) {
        // if customizer is a string (e.g. : css data) append child data after parent data
        if (typeof section.customizers[index] === 'string') {
          section.customizers[index] = parentCustomizers[index] + '\n\n\n' + section.customizers[index];
        }else {
          for (const jindex in parentCustomizers[index]) {
            // add customizer from parent (e.g. : template) if not defined in child
            if (section.customizers[index][jindex] === undefined) {
              section.customizers[index][jindex] = parentCustomizers[index][jindex];
            }
          }
        }
      }
    }
  }
  return section;
};

const doInheritCustomizersFromParent = (inputSection, sections) => {
  let section = Object.assign({}, inputSection);
  let parent = section.metadata.general.parent;
  if (parent && !section.customizersInherited) {
    parent = sections[parent.value];
    // first, make your parent inherit from its parent
    if (!parent.customizersInherited) {
      parent = doInheritCustomizersFromParent(parent, sections);
    }
    // then inherit yourself from your parent
    section = inheritCustomizersFromParent(section, sections, parent.customizers, parent.metadata.general.id.value);
  }
  section.customizersInherited = true;
  return section;
};

const populateLaterally = (section, models) => {
  // const toInclude = [];
  for (const domain in section.metadata) {
    if (section.metadata[domain]) {
      for (const key in section.metadata[domain]) {
        if (section.metadata[domain][key]) {
          const model = models[domain][key];
          if (model) {
            const spreaded = model.propagatesTo.map(metaStringToCouple);
            spreaded.forEach(sp => {
              if (section.metadata[sp.domain] === undefined) {
                section.metadata[sp.domain] = {};
              }
              if (section.metadata[sp.domain][sp.key] === undefined) {
                section.metadata[sp.domain][sp.key] = {
                  value: section.metadata[domain][key].value,
                  inheritedHorizontallyFrom: {domain, key}
                };
              }
            });
          }
        }
      }
    }
  }

  return section;
};

/**
 * Populate the metadatas of a list of sections, by applying propagation from parents or inbetween metadata values (e.g. : from twitter domain to open graph domain)
 * @param {Object} params - the params of propagation
 * @param {array} params.errors - the list of errors possibly inherited from previous steps
 * @param {Object} params.document - the document in its current shape (sections litteral object + sumamry)
 * @param {Object} params.models - the models to parse the sections with
 * @param {Object} params.parent - if specified, sections that don't have a parent will all be considered as children of this one (but it won't be parsed itself)
 * @return {errors: array, document: Object} - the new transformation errors and updated document representation
 */
export const propagateData = ({
  errors,
  document: inputDocument,
  models
}) => {

  const document = Object.assign({}, inputDocument);
  document.resources = {};
  document.contextualizers = {};

  for (const key in document.sections) {
    if (document.sections[key]) {
      const section = document.sections[key];
      // catch root and desactivate inheritance for this one
      if (section.metadata.general.parent === undefined) {
        section.metadata.general.generalityLevel = {value: 1};
        section.resourcesInherited = true;
        section.metadataInherited = true;
        section.customizersInherited = true;
        section.contextualizersInherited = true;
      }
      // clean bibType
      let newBibType = section.metadata.general.bibType.value;
      newBibType = newBibType ? newBibType.split('peritext') : [];
      newBibType = newBibType.length > 1 ? newBibType[1] : newBibType[0];
      section.metadata.general.bibType.value = newBibType;
      // add resources to general resources list
      section.resources.forEach(resource => {
        if (document.resources[resource.id]) {
          errors.push({
            type: 'error',
            preciseType: 'redundantResource',
            resourceId: resource.id,
            message: 'Resource ' + resource.bibType + ' is described more than once'
          });
        }
        document.resources[resource.id] = resource;
      });
      // add contextualizers to general resources list
      section.contextualizers.forEach(contextualizer => {
        if (document.contextualizers[contextualizer.id]) {
          errors.push({
            type: 'error',
            preciseType: 'redundantContextualizer',
            resourceId: contextualizer.id,
            message: 'Contextualizer ' + contextualizer.bibType + ' is described more than once'
          });
        }
        document.contextualizers[contextualizer.id] = contextualizer;
      });
      delete section.contextualizers;
    }
  }
  // vertical inheritance process
  // -> fill with parent metadata, append parent customizers
  for (const keyVert in document.sections) {
    if (document.sections[keyVert]) {
      document.sections[keyVert] = doInheritMetadataFromParent(document.sections[keyVert], models.sectionTypeModels, document.sections);
      document.sections[keyVert] = doInheritCustomizersFromParent(document.sections[keyVert], document.sections);
    }
  }
  // lateral inheritance process
  // e.g. twitter ==> dublincore
  for (const keyLat in document.sections) {
    if (document.sections[keyLat]) {
      document.sections[keyLat] = populateLaterally(document.sections[keyLat], models.metadataModels);
    }
  }

  // cleaning control properties
  for (const keyClean in document.sections) {
    if (document.sections[keyClean]) {
      const section = document.sections[keyClean];
      delete section.metadataInherited;
      delete section.customizersInherited;
    }
  }
  return {errors, document};
};

/**
 * This module resolves markdown contents + peritext-specific assertions (notes, contextualizations, contextualizers)
 * It returns a representation of a section's content as an object containing arrays of: DOM children as js representation, notes, contextualizations, contextualizers
 * @module converters/markdownConverter
 */
import marked from 'marked';
import {v4} from 'uuid';
import {html2json} from 'html2json';
import {XmlEntities} from 'html-entities';
const entities = new XmlEntities();
import {parseBibContextualization} from './bibTexConverter';

// basic marked parser settings
marked.setOptions({
  renderer: new marked.Renderer(),
  gfm: true,
  tables: true,
  breaks: false,
  pedantic: false,
  sanitize: true,
  smartLists: true,
  smartypants: false
});

const eatParamsObject = (str)=> {
  let index = 0;
  let wrappingLevel = 0;
  let paramsObject = '';
  let inObject = false;
  let ch;
  while (index < str.length) {
    ch = str.charAt(index);
    if (ch === '{') {
      wrappingLevel++;
    }else if (ch === '}') {
      wrappingLevel--;
    }

    if (!inObject && wrappingLevel > 0) {
      inObject = true;
      paramsObject += ch;
    } else if (inObject && wrappingLevel === 0) {
      paramsObject += ch;
      return paramsObject;
    } else if (inObject) {
      paramsObject += ch;
      // not in object, character is neither a wrapper nor a whitespace, leave
    } else if (!inObject && ch.match(/([\s])/) === null) {
      return undefined;
    }
    index++;
  }
  return undefined;
};

const parseParamsObject = (paramsObject)=> {
  /*
   * analyse contextualizer statement
  */
  let overloading;
  let contextualizerKey;
  // case : explicit call to a contextualizer ==> explicit contextualizer overload case
  if (paramsObject && paramsObject.indexOf('@') === 1) {
    contextualizerKey = paramsObject.match(/^\{(@[^,}]+)/)[1];
    overloading = contextualizerKey;
  }
  // else case : no explicit call to a contextualizer ==> inline implicit contextualization, determine id automatically
  contextualizerKey = 'contextualization-' + v4();

  let formattedParams;
  if (paramsObject !== undefined) {
    formattedParams = parseBibContextualization(paramsObject);
    const emptyParams = JSON.stringify(formattedParams).length <= 2;
    // not empty = params present, so inline (implicit new contextualizer, or contextualizer overloading)
    if (!emptyParams) {
      formattedParams.describedInline = true;
    // case : no additionnal params ==> no parameters
    } else {
      formattedParams = {};
    }
  // case : no mention of a contextualizer fully implicit contextualizer (no contextualizer specified)
  } else {
    formattedParams = {};
    formattedParams.describedInline = true;
    formattedParams.fullyImplicit = true;
  }
  // case : a contextualizer has been mentionned, with additionnal contextualization params --> contextualizer overloading
  if (overloading) {
    formattedParams.overloading = overloading;
  }
  formattedParams.id = contextualizerKey;
  return formattedParams;
};

const parseContextualizations = (section)=> {
  let replaced = section.contents;
  const contextualizations = {};
  const newContextualizers = {};
  const orderedContextualizations = [];
  const statementsRE = /(\!)?\[([^\]]*)\]\(([^\)]+)\)/g;
  let match;
  let type;
  let resources;
  let paramsObject;

  while ((match = statementsRE.exec(replaced)) !== null) {
    /*
     * retrieve data from markdown expressions
     */
    // hyperlink markdown syntax stands for inline contextualization
    // image markdown syntax stands for block contextualization
    // ()[] => inline, !()[] => block
    type = match[1] ? 'block' : 'inline';
    // contents
    // quoted resources
    resources = match[3].split(',').map(resKey => resKey.substr(1));
    const contextualizationId = 'contextualization-' + v4();
    // following parameters
    paramsObject = eatParamsObject(replaced.substr(match.index + match[0].length));
    // UPDATE TEXT
    // delete paramsObject from text
    if (paramsObject) {
      replaced = replaced.replace(replaced.substring(match.index + match[0].length, match.index + match[0].length + paramsObject.length), '');
    }
    // update reference in <a> link or <image>
    replaced = replaced.replace(replaced.substring(match.index + match[0].indexOf(match[3]), match.index + match[0].indexOf(match[3]) + match[3].length), contextualizationId);
    // UPDATE DATA
    const contextualizer = parseParamsObject(paramsObject);
    if (contextualizer) {
      newContextualizers[contextualizer.id] = contextualizer;
    }
    contextualizations[contextualizationId] = {
      'id': contextualizationId,
      contextualizer: contextualizer.id,
      resources,
      type
    };
    orderedContextualizations.push(contextualizationId);
  }
  return {md: replaced, orderedContextualizations, contextualizations, contextualizers: newContextualizers};
};


// this module does not use a regex-based method
// because it must catch possible nested content-related "{" brackets symbols
// e.g. : this is an {example inside brackets}
const parseNotes = (md, sectionId)=> {
  const notes = [];
  let noteNumber = 1;
  let index = 0;
  let displace = 0;
  let beginIndex;
  let nestingLevel = 0;
  let ch;
  let newMd = md;
  let noteContent;
  while (newMd.substr(displace).indexOf('[^]{') > -1) {
    index = displace + newMd.substring(displace).indexOf('[^]{') + 4;
    beginIndex = index;
    nestingLevel = 1;

    while (index < newMd.length && nestingLevel > 0) {
      ch = newMd.charAt(index);
      if (ch === '{') {
        nestingLevel++;
      } else if (ch === '}') {
        nestingLevel--;
      }
      index++;
    }

    noteContent = newMd.substring(beginIndex, index - 1);
    const id = 'note-' + sectionId + '-' + v4();// noteNumber;
    const placeholder = `[footnote](note_${id})`;
    const initialLength = index - beginIndex + 4;
    const lengthDif = initialLength - placeholder.length;
    newMd = newMd.replace(newMd.substring(beginIndex - 4, index), placeholder);
    notes.push({
      noteNumber,
      markdownContents: noteContent,
      id
    });
    noteNumber++;
    displace = index - lengthDif;
  }
  return {
    notes,
    newMd
  };
};
// for recursivity matter
let mapMdJsonToPJson = ()=>{return undefined;};
let representContents = ()=>{return undefined;};

mapMdJsonToPJson = (inputElement, contextualizations, elementPath) =>{
  const element = Object.assign({}, inputElement);
  if (element.text) {
    element.text = entities.decode(element.text);
  }
  if (element.tag === 'a') {
    if (element.attr.href.indexOf('note_') === 0) {
      element.tag = 'note';
      element.target = element.attr.href.substr(5);
    // case of an inline contextualization
    } else {
      element.tag = 'inlineC';
      const contextualizationId = element.attr.href;
      const contextualization = contextualizations[contextualizationId];
      contextualization.nodePath = elementPath;
    }
  // case of a block contextualization
  } else if (element.tag === 'img') {
    element.tag = 'blockC';
    const contextualizationId = element.attr.src;
    const contextualization = contextualizations[contextualizationId];
    contextualization.nodePath = elementPath;
    let contents = (element.attr && element.attr.alt) ? element.attr.alt : '';
    contents = contents.join !== undefined ? contents.join(' ') : contents;
    element.children = [representContents(contents, contextualizations, elementPath)[0]];
    delete element.attr;
  }
  if (element.child) {
    element.children = element.child.map((child, elementIndex)=>{
      return mapMdJsonToPJson(child, contextualizations, elementPath.concat(['children', elementIndex]));
    });
    delete element.child;
  }
  return element;
};

representContents = (mdContent, contextualizations, elementPath) =>{
  return html2json(marked(mdContent)).child.map((child, blockIndex)=> {
    return mapMdJsonToPJson(child, contextualizations, elementPath.concat(blockIndex));
  });
};

/**
 * Parses markdown contents in order to update section data with new contents (as a pseudo-DOM nested javascript object representation), notes, contextualizers and contextualizations
 * @param {Object} section - the section to parse
 * @param {Object} parameters - deprecated - rendering parameters (not used at this step)
 * @return {errors: array, section: Object} - the resulting conversion errors and updated section
 */
export const markdownToJsAbstraction = (section, parameters)=> {
  const errors = [];

  const sectionId = section.metadata.general.id.value;
  // save original markdown expression of contents (for further possible serializing)
  section.markdownContents = section.contents;
  // first extract contextualizations statements
  const {
    md,
    contextualizers,
    orderedContextualizations,
    contextualizations
  } = parseContextualizations(section);
  // then extract notes statements
  const {
    notes,
    newMd
  } = parseNotes(md, sectionId);
  section.contextualizations = orderedContextualizations;
  // convert cleaned markdown contents to js representation
  section.contents = representContents(newMd, contextualizations, [sectionId, 'contents']);
  section.notes = notes.map((note, noteIndex) =>{
    const contents = html2json(marked(note.markdownContents)).child.map((child, blockIndex)=> {
      return mapMdJsonToPJson(child, contextualizations, [sectionId, 'notes', noteIndex]);
    });
    return Object.assign(note, {
      children: [
        // this is dirty, done for matching contextualization nodePath which is displaced otherwise
        // other dirty solution : handle that in mapMdJsonToPJson with a path check
        // (if in notes prop => decrement contextualization target)
        { node: 'text',
          text: ''
        },
        ...contents[0].children
      ]
    });
  });
  return {errors, section, contextualizers, contextualizations};
};

/**
 * This module analyses special markdown insertions (includes and resources)
 * Takes a peritext-markdown string + syntax params as input
 * Parses inline resources expressions
 * Parses ``include`` statements
 * Returns cleaned string, plus a list of include statements and resources to parse
 * @module converters/markdownIncludesParser
 */

const regexEscape = function(str) {
  return str.replace(/[\\^$*+?.()|[\]{}]/g, '\\$&');
};

/**
 * Parses markdown string for markdown files includes statements and inline resources descriptions
 * @param {string} str - the string to parse
 * @param {Object} expressions - the wrapping expressions to use in order to parse includes and inline resources
 * @param {function(error: error, results: {extracted: array, cleanStr: string})} - callback - returns error, results as an array of extracted statements and clean string (without statements)
 */
export const parseMarkdown = (str, {includeWrappingChars, resWrappingChars}, callback) => {
  const includesRegexp = new RegExp(regexEscape(includeWrappingChars[0]) + 'include:([^\\\}]+)' + regexEscape(includeWrappingChars[1]), 'g');
  const resourcesRegexp = new RegExp(regexEscape(resWrappingChars[0]) + '\\\n([\\\w\\\W]+)\\\n' + regexEscape(resWrappingChars[1]), 'g');
  let extracted = [];
  let cleanStr = str;
  let match;
  let extr;

  // recursively check for one of the two substitution expressions
  while (((match = resourcesRegexp.exec(cleanStr)) !== null) || ((match = includesRegexp.exec(cleanStr)) !== null)) {
    // if match, swipe out original statement
    cleanStr = [cleanStr.substr(0, match.index - 1), cleanStr.substr(match.index + match[0].length)].join('');
    // save statement position and type
    extr = {
      index: match.index,
      statement: match[1]
    };
    if (match[0].match(resourcesRegexp)) {
      extr.type = 'resourceStatement';
    }else if (match[0].match(includesRegexp)) {
      extr.type = 'includeStatement';
    }
    // update other statements' position that would be further in string
    extracted = extracted.map((ex) => {
      if (ex.index > extr.index) {
        ex.index -= match[0].length + 1;
      }
      return ex;
    });
    extracted.push(extr);
  }

  // reverse sort (for further string substitution operations)
  extracted = extracted.sort((one, two) =>{
    if (one.index > two.index) {
      return -1;
    }
    return 1;
  });
  return callback(null, {extracted, cleanStr});
};

export const getDocument = (document) => Object.assign({}, document);

export const getDocumentMetadata = (document) => Object.assign({}, document.metadata);

const packSection = (document, section) => {
  const mappedContextualizations = section.contextualizations.map(key =>
    document.contextualizations[key]
  );

  const contextualizations = mappedContextualizations.reduce((total, item) =>
    Object.assign(total, {[item.id]: item}), {});

  const contextualizers = mappedContextualizations.map(cont =>
    document.contextualizers[cont.contextualizer]
  ).reduce((total, item) =>
    Object.assign(total, {[item.id]: item}), {});
  const resources = mappedContextualizations.reduce((res, contextualization) => {
    return res.concat(contextualization.resources.map((resKey)=>{
      return document.resources[resKey];
    }));
  }, []).reduce((total, item) =>
      Object.assign(total, {[item.id]: item}), {});

  return Object.assign({},
    section,
    {contextualizations},
    {contextualizers},
    {resources}
  );
};

export const getSection = (document, id) => {
  const section = document.sections[id];
  return packSection(document, section);
};

export const getForewords = (document) => {
  const section = document.forewords;
  return packSection(document, section);
};

export const getTableOfSections = (document) =>
  document.summary.map(sectionKey =>{
    const metadata = document.sections[sectionKey].metadata;
    return {
      id: metadata.general.id.value,
      generalityLevel: metadata.general.generalityLevel.value,
      title: metadata.general.title.value,
      parent: metadata.general.parent ? metadata.general.parent.value : undefined
    };
  });

export const getTableOfFigures = (document) =>
  Object.keys(document.contextualizations)
  .map(key => document.contextualizations[key])
  .filter(contextualization => contextualization.figureId)
  .map(contextualization => ({
    figureId: contextualization.figureId,
    figureNumber: contextualization.figureNumber,
    contextualization
  }));

export const getResourceContextualizations = (document, resourceId) =>
  Object.keys(document.contextualizations)
    .map(key => document.contextualizations[key])
    .filter(contextualization =>
      contextualization.resources.indexOf(resourceId) > -1
    );

export const getContextualizerContextualizations = (document, contextualizerId) =>
  Object.keys(document.contextualizations)
    .map(key => document.contextualizations[key])
    .filter(contextualization =>
      contextualization.contextualizer === contextualizerId
    );

export const getGlossary = (document) => {
  const entitiesTypes = ['person', 'place', 'subject', 'concept', 'organization', 'technology', 'artefact'];
  const sections = Object.keys(document.sections).map(key => document.sections[key]);
  // get all glossary contextualizations
  const glossaryPointers = sections.reduce((results, thatSection)=>{
    const sectionCitekey = thatSection.metadata.general.id.value;
    return results.concat(
      thatSection.contextualizations
      .filter((thatContextualization)=> {
        return document.contextualizations[thatContextualization].contextualizerType === 'glossary';
      })
      .reduce((localResults, contextualizationKey)=> {
        const contextualization = document.contextualizations[contextualizationKey];
        const targetBlockPath = contextualization.nodePath.slice(0, 3);
        return localResults.concat({
          mentionId: '#peritext-content-entity-inline-' + sectionCitekey + '-' + contextualization.id,
          entity: document.resources[contextualization.resources[0]].id,
          alias: document.contextualizers[contextualization.contextualizer].alias,
          targetBlockPath
        });
      }, []));
  }, []);
  // regroup related resources
  const glossaryResources = [];
  Object.keys(document.resources)
  .map(refKey => {
    const thatResource = document.resources[refKey];
    if (thatResource.inheritedVerticallyFrom === undefined
        && entitiesTypes.indexOf(thatResource.bibType) > -1
        ) {
      glossaryResources.push(thatResource);
    }
  });
  // reduce to have a one-entry-per-entity glossary
  const glossaryData = glossaryResources.map((inputGlossaryEntry)=> {
    const glossaryEntry = Object.assign({}, inputGlossaryEntry);
    // retroup resources
    glossaryEntry.aliases = glossaryPointers.filter((pointer)=> {
      return pointer.entity === glossaryEntry.id;
    }).reduce((aliases, entry)=> {
      const alias = entry.alias || 'no-alias';
      aliases[alias] = aliases[alias] ? aliases[alias].concat(entry) : [entry];
      return aliases;
    }, {});
    return glossaryEntry;
  }).sort((entry1, entry2)=> {
    return (entry1.name || entry1.lastname) > (entry2.name || entry2.lastname) ? 1 : -1;
  });
  return glossaryData;
};

