/**
 * Resolver dedicated to resolve metadata templates against their data
 * @module resolvers/htmlMetaTemplateSerializer
 */

// templates have two variables : ${key} and ${value}
// if value is an array
// modificators are specifed after commas
// modificators : join(), personsToString

/**
 * Consumes a metadata object against an html metadata node template
 * @param {Object} metadata - the metadata object
 * @param {string} template - the template to consumes
 * @return {string} htmlMeta - the representation of the html metadata as string
 */
export const serializeHtmlMeta = (metadata, key, template) => {
  let transformationAction = template.match(/\${value:([^}]*)/);
  let transformationArgument;
  let value;
  if (transformationAction) {
    transformationAction = transformationAction[1];
    if (transformationAction.indexOf('join(') === 0) {
      try {
        transformationArgument = transformationAction.match(/join\(([^\)]*)\)/)[1];
      }catch (exception) {
        transformationArgument = ', ';
      }
    }
  }

  if (transformationAction === 'join' && Array.isArray(metadata.value)) {
    value = metadata.value.join(transformationArgument);
  }else if (transformationAction === 'personsToString' && Array.isArray(metadata.value)) {
    const persons = metadata.value.map((person)=>{
      return (person.firstName) ? person.firstName + ' ' + person.lastName : person.lastName;
    });
    value = persons.join(', ');
  }else value = metadata.value;
  let output = template;
  while (output.indexOf('${value') > -1) {
    output = output.replace('${key}', key).replace(/\${value:?([^}]*)?}/, value);
  }
  return output;
};

/**
 * Resolver dedicated to resolve  contextualization statements
 * @module resolvers/resolveContextualizations
 */

import {getResourceModel, getContextualizerModel, resolvePropAgainstType} from '../utils/modelUtils';
import * as contextualizerLibs from '../../contextualizers';
/**
 * Transforms 1, 2, 3 ordinally used number into a, b, c ordinally used letters
 * @param {number} num - the number to transform
 * @return {string} letter - the output letter
 */
export const numbersToLetters = (num) =>{
  const mod = num % 26;
  let pow = num / 26 | 0;
  const out = mod ? String.fromCharCode(64 + mod) : (--pow, 'Z');
  return pow ? numbersToLetters(pow) + out : out.toLowerCase();
};

/**
 * Resolves a contextualizer object against its model and context, and records errors
 * @param {object} contextualizer - the contextualizer to resolve
 * @param {object} contextualization - the contextualization to use as a clue-giver if the contextualizer is implicit
 * @param {object} section - the section to which the contextualizer belongs
 * @param {object} models - the models to parse the contextualizer against
 * @return {{err: error, contextualizer: Object}} result - parsing errors and final contextualizer
 */
const resolveContextualizer = (contextualizer, contextualization, contextualizers, resources, models) =>{
  const err = [];
  let newContextualizer = Object.assign({}, contextualizer);
  // if overloading, first fetch the existing contextualizer
  if (newContextualizer.overloading) {
    const overload = newContextualizer.overloading.replace(/^@/, '');
    // find original
    const original = contextualizers[overload];
    // resolve original first
    if (original) {
      const originalFormatted = resolveContextualizer(original, contextualization, contextualizers, resources, models).finalContextualizer;
      // overload inherited params with new ones
      newContextualizer = Object.assign(originalFormatted, newContextualizer);
    } else {
      // no original found ==> overloading reference error
      err.push({
        type: 'error',
        preciseType: 'invalidContextualizer',
        message: 'overloading reference error: contextualizer ' + newContextualizer.id + ' should overload ' + overload + ' but the original contextualizer does not exist'
      });
    }
  }
  // guess contextualizer type if needed
  if (!newContextualizer.type) {
    if (contextualization.resources.length > 0) {
      const sourceKey = contextualization.resources[0];
      const source = resources[sourceKey];
      if (source === undefined) {
        err.push({
          type: 'error',
          preciseType: 'invalidContextualizer',
          message: 'contextualizer ' + newContextualizer.id + ' (' + newContextualizer.type + ') does not provide a valid resource'
        });
        return {err, undefined};
      }

      const sourceModel = getResourceModel(source.bibType, models.resourceModels);
      newContextualizer.type = sourceModel.defaultContextualizer;
    }
  }
  // resolve contextualizer object against its model
  const contextualizerModel = getContextualizerModel(newContextualizer.type, models.contextualizerModels);
  const finalContextualizer = contextualizerModel.properties.reduce((obj, thatModel) =>{
    obj[thatModel.key] = resolvePropAgainstType(newContextualizer[thatModel.key], thatModel.valueType, thatModel);
    // record error if required field is undefined
    if (obj[thatModel.key] === undefined && thatModel.required === true) {
      err.push({
        type: 'error',
        preciseType: 'invalidContextualizer',
        message: 'contextualizer ' + newContextualizer.id + ' (' + newContextualizer.type + ') does not provide required type ' + thatModel.key
      });
    }
    return obj;
  }, {});
  return {err, finalContextualizer};
};

/**
 * Resolves contextualizations' contextualizer and resource, verifying that contextualization will be possible
 * @param {object} contextualizer - the contextualizer to resolve
 * @param {object} section - the section to resolve
 * @param {object} models - the models to use to validate data
 * @return {errors: array, newSection: Object} callback - returns updated section and possible errors as an array
 */
export const resolveBindings = ({document, models}) =>{
  let errors = [];
  // find implicit contextualizers types
  for (const id in document.contextualizations) {
    if (document.contextualizations[id]) {
      const contextualization = document.contextualizations[id];
      // resolve contextualizer (in case of overloading)
      const {err, finalContextualizer} = resolveContextualizer(document.contextualizers[contextualization.contextualizer], contextualization, document.contextualizers, document.resources, models);
      if (err.length) {
        errors = errors.concat(err);
      } else {
        contextualization.contextualizer = finalContextualizer.id;
        document.contextualizers[finalContextualizer.id] = finalContextualizer;
      }
      // verify that contextualization has an existing contextualizer
      if (document.contextualizers[contextualization.contextualizer] === undefined) {
        errors.push({
          type: 'error',
          preciseType: 'invalidContextualization',
          message: 'contextualizer was not found for contextualization ' + contextualization.id
        });
        delete document.contextualizations[contextualization.id];
      // verify that contextualization uses valid resources (they exist and their type is compatible with contextualizer)
      } else {
        let toKeep = true;
        const contextualizer = document.contextualizers[contextualization.contextualizer];
        const contextualizerModel = getContextualizerModel(contextualizer.type, models.contextualizerModels);
        if (contextualizerModel === undefined) {
          errors.push({
            type: 'error',
            preciseType: 'invalidContextualization',
            message: 'No contextualizer model found for ' + contextualization.contextualizer
          });
          toKeep = false;
        } else {
          const acceptedResourceTypes = contextualizerModel.acceptedResourceTypes;
          contextualization.resources.some(resKey =>{
            const res = document.resources[resKey];
            // resource exists, check if it is accepted for the contextualizerType
            if (res !== undefined) {
              let accepted = false;
              acceptedResourceTypes.some(type => {
                if (type === '*' || type === res.bibType) {
                  accepted = true;
                  return true;
                }
              });
              if (!accepted) {
                toKeep = false;
                errors.push({
                  type: 'error',
                  preciseType: 'invalidContextualization',
                  message: 'resource ' + resKey + ' was asked in a contextualization but is not handled by the contextualizer ' + contextualization.contextualizer
                });
              }
            } else {
              toKeep = false;
              errors.push({
                type: 'error',
                preciseType: 'invalidContextualization',
                message: 'resource ' + resKey + ' was asked in a contextualization but was not found'
              });
            }
          });
        }
        if (toKeep === false) {
          delete document.contextualizations[contextualization.id];
        }
      }
    }
  }
  return {errors, document};
};

/**
 * Resolves relations of recurrence, order, and similarity between contextualizations in a section
 * @param {inputDocument} document - the document to resolve
 * @param {object} settings - the rendering settings to apply
 * @return {Object} newDocument - the udpated document
 */
export const resolveContextualizationsRelations = (inputDocument, settings) =>{
  const document = Object.assign({}, inputDocument);
  let opCitIndex;
  let sameResPrint;
  for (const sectionKey in document.sections) {
    if (document.sections[sectionKey]) {
      const sectio = document.sections[sectionKey];
      sectio.contextualizations.forEach((contKey, contIndex) => {
        sameResPrint = undefined;
        opCitIndex = undefined;
        const contextualization = document.contextualizations[contKey];
        contextualization.resPrint = contextualization.resources.join('-');
        // find if another contextualization has exactly the same resources set and same section as scope
        for (const oContKey in document.contextualizations) {
          // check if this other contextualization
          // uses the same resources and belong to the same section
          // and use the same contextualizer type
          if (oContKey !== contKey
            // same resources set
            && document.contextualizations[oContKey].resPrint === contextualization.resPrint
            // same section
            && document.contextualizations[oContKey].nodePath[0] === contextualization.nodePath[0]
            // same contextualizer type
            && document.contextualizations[oContKey].contextualizerType === contextualization.contextualizerType
            ) {
            // if opcit section
           /* sectio.contextualizations.some((thatKey, thatIndex) => {
              if (thatKey === oContKey && thatIndex > contIndex) {
                opCitIndex = thatIndex;
                return true;
              }
              return false;
            });*/
            document.sections[contextualization.nodePath[0]].contextualizations.find((oKey2, oIndex)=> {
              if (oKey2 === oContKey) {
                opCitIndex = oIndex;
                return oKey2;
              }
            });
            contextualization.precursorId = document.contextualizations[oContKey].id;
            sameResPrint = true;
          }
        }
        if (sameResPrint !== undefined) {
          contextualization.sectionOpCit = true;
        }
        // todo opcit document

        // ibid section
        if (opCitIndex) {
          // want to check for an ibid (same resource, same contextualizer, subsequent)
          // must check if opCit and current contextualizations are strictly subsequent
          const substrate = sectio.contextualizations.slice(opCitIndex, contIndex).filter(intContKey=> {
            return document.contextualizers[
                      document.contextualizations[intContKey].contextualizer
                    ].type === document.contextualizers[contextualization.contextualizer].type;
          });
          if (substrate.length === 1) {
            contextualization.sectionIbid = true;
          }
        }

        // todo ibid document
        // citation specifics
        if (document.contextualizers[contextualization.contextualizer].type === 'citation') {
          // same authors but different work in year - section scale
          contextualization.authorsPrint = document.resources[contextualization.resources[0]].author.reduce((str, author)=> {
            return str + author.lastName + '-' + author.firstName;
          }, '');
          for (const oContKey2 in document.contextualizations) {
            if (oContKey2 !== contKey
              // same section
              && document.contextualizations[oContKey2].nodePath[0] === contextualization.nodePath[0]
              // different resources set
              && document.contextualizations[oContKey2].resPrint !== contextualization.resPrint
              // same authors set
              && document.contextualizations[oContKey2].authorsPrint === contextualization.authorsPrint
              // same year
              && (document.contextualizations[oContKey2].year === contextualization.year
                  || document.contextualizations[oContKey2].date === contextualization.date)
              ) {
              // if same authors/year
              const cont2 = document.contextualizations[oContKey2];
              cont2.sameAuthorInYear = cont2.sameAuthorInYear !== undefined ? cont2.sameAuthorInYear ++ : 1;
              contextualization.sameAuthorInYear = cont2.sameAuthorInYear + 1;
              contextualization.yearSuffix = numbersToLetters(contextualization.sameAuthorInYear);
              cont2.yearSuffix = numbersToLetters(cont2.sameAuthorInYear);
            }
          }
          // todo same authors in year but different work - at document scale
        }

        opCitIndex = undefined;
      });
    }
  }
  return document;
};

/**
 * Resolves all the contextualizations of a section by updating its contents pseudo-DOM representation
 * @param {object} contextualization - the contextualization to implement
 * @param {object} document - the document to update
 * @param {string} renderingMode - whether rendering is static or dynamic
 * @param {object} settings - the rendering settings to take into account when resolving contextualizations
 * @return {Object} newDocument - the updated document
 */
export const resolveContextualizationImplementation = (contextualization, document, renderingMode, settings) =>{
  let contextualizer;
  // console.log('resolving ', contextualization.id);
  contextualizer = contextualizerLibs[contextualization.contextualizerType];
  if (contextualizer === undefined) {
    console.log('no contextualizer function was found for type ', contextualization.contextualizerType);
    return document;
  }
  switch (renderingMode) {
  case 'static':
    switch (contextualization.type) {
    case 'inline':
      return contextualizer.contextualizeInlineStatic(document, contextualization, settings);
    case 'block':
      return contextualizer.contextualizeBlockStatic(document, contextualization, settings);
    default:
      break;
    }
    break;
  case 'dynamic':
    switch (contextualization.type) {
    case 'inline':
      return contextualizer.contextualizeInlineDynamic(document, contextualization, settings);
    case 'block':
      return contextualizer.contextualizeBlockDynamic(document, contextualization, settings);
    default:
      break;
    }
    break;
  default:
    break;
  }
  // default return unchanged document
  return document;
};

/**
 * Resolver dedicated to resolve @assets statements and @res statements
 * @module resolvers/resolveDataDependencies
 */
import {map as asyncMap, mapSeries as asyncMapSeries, waterfall} from 'async';
const CsvConverter = require('csvtojson').Converter;
const csvConverter = new CsvConverter({});

/**
 * Resolves interpolations in metadata, resources, and contextualizations,
 * and possibly fetches & stores necessary data for rendering
 * @param {Object} document - the document to resolve
 * @param {Object} assetsController - the assets controller module to use to communicate with assets
 * @param {Object} assetsParams - the params to use for accessing the assets
 * @param {boolean} resolveData - whether to fetch and store necessary data right away or to store methods for later lazy-loading
 * @param {function(err: error, finalSections: array)} callback - callbacks a possible errors and the updated list of sections
 */
export default function resolveDataDependencies(
  inputDocument,
  assetsController,
  assetsParams,
  resolveData,
  callback) {

  const document = Object.assign({}, inputDocument);
  let res;
  let match;
  const data = {};// this object stores resolved and unresolved promises about resources data
  const resRe = /@res([\d]+)?.(.*)/g;
  const assetsRe = /@assets\/([^']+)/g;
  const inputSections = Object.keys(document.sections).map(key => document.sections[key]);

  asyncMap(inputSections, (section, allSectionsCallback)=> {
    waterfall([
      // styles resolution
      (stylesResolutionCallback)=> {
        if (section.customizers && section.customizers.styles) {
          const styles = [];
          for (const prop in section.customizers.styles) {
            if (section.customizers.styles[prop]) {
              styles.push({key: prop, value: section.customizers.styles[prop]});
            }
          }
          // map each style customizer
          asyncMap(styles, (styleCouple, singleStyleCallback)=> {
            let style = styleCouple.value;
            // get all expressions
            const matches = [];
            do {
              match = assetsRe.exec(style);
              if (match) {
                matches.push(match);
              }
            } while (match);
            // some assets interpolations to do
            if (matches.length) {
              // reverse array to begin with last matches and not mess around with indexes
              matches.reverse();
              asyncMap(matches, (thisMatch, matchCallback)=> {
                assetsController.getAssetUri(thisMatch[1], assetsParams, (err4, uri)=> {
                  style = style.substr(0, thisMatch.index) + uri + style.substr(thisMatch.index + thisMatch[0].length);
                  matchCallback(err4, thisMatch);
                });
              }, (matchErrors, theseMatches)=> {
                styleCouple.value = style;
                singleStyleCallback(matchErrors, styleCouple);
              });
            // no interpolations to do
            } else {
              styleCouple.value = style;
              singleStyleCallback(null, styleCouple);
            }
          }, (stylesErrors, styleCouples)=> {
            styleCouples.forEach((styleCouple) =>{
              section.customizers.styles[styleCouple.key] = styleCouple.value;
            });
            stylesResolutionCallback(stylesErrors, section);
          });
        } else stylesResolutionCallback(null, section);
      },
      // resolve metadata
      (sectio, metadataCallback)=> {
        const domains = Object.keys(section.metadata);
        asyncMap(domains, (domain, domainCallback)=> {
          const props = Object.keys(sectio.metadata[domain]);
          asyncMap(props, (propKey, propCallback)=> {
            const prop = sectio.metadata[domain][propKey];
            const propObject = {
              key: propKey,
              data: prop
            };
            if (typeof prop.value === 'string' && prop.value.indexOf('@assets/') === 0) {
              assetsController.getAssetUri(prop.value.split('@assets/')[1], assetsParams, (err4, uri)=> {
                prop.value = uri;
                propObject.data = prop;
                propCallback(err4, propObject);
              });
            } else {
              propCallback(null, propObject);
            }
          }, (propsError, propObjects) => {
            const domainObject = {
              key: domain,
              data: {}
            };
            propObjects.forEach(propObject=> {
              domainObject.data[propObject.key] = propObject.data;
            });
            domainCallback(propsError, domainObject);
          });
        }, (domainErrors, domainObjects)=>{
          domainObjects.forEach((domainObject) => {
            sectio.metadata[domainObject.key] = domainObject.data;
          });
          metadataCallback(domainErrors, sectio);
        });
      }
    // waterfall callback for all sections
    ], (errs, sectio)=> {
      // fill document section keys with updated sections
      document.sections[sectio.metadata.general.id.value] = sectio;
      allSectionsCallback(errs);
    });
  // sections final callback
  }, (err)=> {
    // document wide resolutions
    const resources = Object.keys(document.resources).map(key => document.resources[key]);
    const contextualizations = Object.keys(document.contextualizations).map(key => document.contextualizations[key]);

    waterfall([
      (contextualizationsCallback)=> {
        asyncMap(contextualizations, (contextualization, contextualizationCallback)=> {
          res = undefined;
          const props = [];
          // format props as array for performing an async map
          const contextualizer = document.contextualizers[contextualization.contextualizer];

          for (const prop in contextualizer) {
            if (contextualizer[prop]) {
              props.push({key: prop, value: contextualizer[prop]});
            }
          }
          /**
           * First level props
           */
          // resolve async. props
          asyncMap(props, (prop, contextualizationPropCallback)=> {
            const val = prop.value;
            // if prop is itself an array, need for another nested async resolution
            if (Array.isArray(val) && prop.key !== 'resources') {
              // loop through propArray members
              asyncMap(val, (prop2, propArrayMembersCallback)=> {
                // prepare for asyncMap for propArray member
                const nestedProps = [];
                for (const nestedProp in prop2) {
                  if (prop2[nestedProp]) {
                    nestedProps.push({key: nestedProp, value: prop2[nestedProp]});
                  }
                }
                // Second level props (contextualization.arrayProp[array member].prop)
                asyncMap(nestedProps, (nestedProp, nestedPropCallback)=> {
                  // resolve nested props here
                  const val3 = nestedProp.value;
                  if (('' + val3).indexOf('@res') === 0) {
                    while ((match = resRe.exec(val3)) !== null) {
                      // Identify which resource is targetted (handling multi-resource contextualizations)
                      const rank = match[1] ? (+match[1] - 1) : 0;
                      // find resource data
                      res = document.resources[contextualization.resources[rank]];
                      if (res === undefined) {
                        console.log('res is undefined, expression: ', val3);
                        return nestedPropCallback(undefined, nestedProp);
                      }
                      // case: metadata call
                      const resProp = match[match.length - 1];
                      if (resProp.indexOf('data') !== 0) {
                        // interpolate w/ resource value thanks to resourceProp path
                        nestedProp.value = res[resProp];
                      // case : data call
                      } else {
                        const dataPath = ('' + val3).split('.').filter(path=>{
                          return path.length;
                        });
                        nestedProp.value = {
                          type: 'path',
                          target: 'data',
                          path: dataPath
                        };
                        const defined = data[res.id];
                        if (!defined) {
                          const accessor = res.data || res.url;
                          if (('' + accessor).indexOf('@assets/') === 0) {
                            const toResolve = {};
                            toResolve.params = {
                              path: accessor.split('@assets/')[1],
                              params: assetsParams,
                              acceptedExtensions: '*'
                            };
                            toResolve.read = assetsController.getReader(assetsParams);
                            data[res.id] = toResolve;
                          } else {
                            console.log('unhandled data accessor (1) : ', accessor);
                          }
                        }
                      }
                    }
                    nestedPropCallback(null, nestedProp);
                  } else if (('' + val3).indexOf('@assets/') === 0) {
                    assetsController.getAssetUri(val3.split('@assets/')[1], assetsParams, (err4, uri)=> {
                      nestedProp.value = uri;
                      nestedPropCallback(err4, nestedProp);
                    });
                  } else {
                    nestedPropCallback(null, nestedProp);
                  }
                }, (err3, propsOut2)=> {
                  const newProp2 = propsOut2.reduce((newCont, newContProp) =>{
                    newCont[newContProp.key] = newContProp.value;
                    return newCont;
                  }, {});
                  propArrayMembersCallback(err3, newProp2);
                });
              // resolve arrayed prop
              }, (err2, arrayMembers)=> {
                prop.value = arrayMembers;
                contextualizationPropCallback(null, prop);
              });
            } else if (typeof val !== undefined && prop.key !== 'resources') {
              if (('' + val).indexOf('@res') === 0) {
                while ((match = resRe.exec(val)) !== null) {
                  // Identify which resource is targetted (handling multi-resource contextualizations)
                  const rank = match[1] ? (+match[1] - 1) : 0;
                  // find resource data
                  res = document.resources[contextualization.resources[rank]];
                  if (res === undefined) {
                    console.log('res is undefined, expression: ', val);
                    return contextualizationPropCallback(undefined, prop);
                  }
                  const resProp = match[match.length - 1];
                  // case: metadata call
                  if (resProp.indexOf('data') !== 0) {
                    // interpolate w/ resource value thanks to resourceProp path
                    prop.value = res[resProp];
                  // case : data call
                  } else {
                    const dataPath = ('' + val).split('.').filter(path=>{
                      return path.length;
                    });
                    prop.value = {
                      type: 'path',
                      target: 'data',
                      path: dataPath
                    };
                    const defined = data[res.id];
                    if (!defined) {
                      const accessor = res.data || res.url;
                      if (('' + accessor).indexOf('@assets/') === 0) {
                        const toResolve = {};
                        toResolve.params = {
                          path: accessor.split('@assets/')[1],
                          params: assetsParams,
                          acceptedExtensions: '*'
                        };
                        toResolve.read = assetsController.getReader(assetsParams);
                        data[res.id] = toResolve;
                      } else {
                        console.log('unhandled data accessor (2) : ', accessor);
                      }
                    }
                  }
                }
                contextualizationPropCallback(null, prop);
              } else if (('' + val).indexOf('@assets/') === 0) {
                assetsController.getAssetUri(val.split('@assets/')[1], assetsParams, (err3, uri)=> {
                  prop.value = uri;
                  contextualizationPropCallback(err3, prop);
                });
              } else {
                contextualizationPropCallback(null, prop);
              }
            } else {
              contextualizationPropCallback(null, prop);
            }
          }, (err1, propsOut1)=> {
            const newContextualizer = propsOut1.reduce((newCont, propOut) =>{
              newCont[propOut.key] = propOut.value;
              return newCont;
            }, {});

            // pass contextualizer's resolved values to contextualization object
            newContextualizer.contextualizerType = newContextualizer.type;
            delete newContextualizer.type;// contextualization has a type also
            delete newContextualizer.id;
            const newContextualization = Object.assign(contextualization, newContextualizer);
            contextualizationCallback(err1, newContextualization);
          });
        }, (contextualizationsError, finalContextualizations)=> {
          finalContextualizations.forEach(contextualization => {
            document.contextualizations[contextualization.id] = contextualization;
          });
          contextualizationsCallback(contextualizationsError);
        });
      },
      (resourcesResolutionCallback)=>{
        // resources resolution
        asyncMap(resources, (resource, resourceCallback)=>{
          const resolvedResource = {
            key: resource.id,
            data: resource
          };
          const props = [];
          // format props as array for performing an async map
          for (const prop in resource) {
            if (resource[prop]) {
              props.push({key: prop, value: resource[prop]});
            }
          }
          asyncMap(props, (prop, resourcePropCallback)=> {
            if (typeof prop.value === 'string' && prop.value.indexOf('@assets/') === 0) {
              assetsController.getAssetUri(prop.value.split('@assets/')[1], assetsParams, (err4, uri)=> {
                prop.value = uri;
                resourcePropCallback(err4, prop);
              });
            } else {
              resourcePropCallback(null, prop);
            }
          }, (singleResourceError, newProps)=> {
            const newResource = newProps.reduce((obj, prop) =>{
              obj[prop.key] = prop.value;
              return obj;
            }, {});
            resolvedResource.data = newResource;
            resourceCallback(singleResourceError, resolvedResource);
          });
        }, (resourceErrors, resolvedResources)=>{
          resolvedResources.forEach(resolvedResource => {
            document.resources[resolvedResource.key] = resolvedResource.data;
          });
          resourcesResolutionCallback(resourceErrors);
        });
      }
    ], (documentWideErrors)=>{
      if (resolveDataDependencies) {
        asyncMapSeries(Object.keys(data), (key, dataCallback) =>{
          const toResolve = data[key];
          toResolve.read(toResolve.params, (dataErr, dataResult) =>{
            if (dataErr) {
              data[key] = dataErr;
              dataCallback(null, key);
            } else {
              const raw = dataResult && dataResult.stringContents;
              const ext = dataResult.extname;
              // todo : handle other file formats
              if (raw && ext === '.csv') {
                csvConverter.fromString(raw, (parseErr, json)=>{
                  data[key] = {
                    format: 'json',
                    data: json
                  };
                  dataCallback(parseErr, key);
                });
              }else {
                console.log('unhandled file format ', ext);
                dataCallback(null, key);
              }
            }
          });
        }, (finalErr, keys) =>{
          document.data = data;
          callback(finalErr, document);
        });
      } else {
        document.data = data;
        callback(documentWideErrors, document);
      }
    });
  });
}

import {getResourceModel, resolvePropAgainstType} from '../utils/modelUtils';

export const resolveResourcesAgainstModels = (resources, models)=> {
  const newResources = Object.assign({}, resources);
  const newErrors = [];
  for (const resourceKey in newResources) {
    if (newResources[resourceKey]) {
      const resource = newResources[resourceKey];
      const model = getResourceModel(resource.bibType, models.resourceModels);
      if (model) {
        // populate resource model with input resource data
        newResources[resourceKey] = model.properties.reduce((resolvedResource, propModel) => {
          const key = propModel.key;
          if (propModel.required && !resource[key]) {
            newErrors.push({
              type: 'error',
              preciseType: 'invalidResource',
              message: 'property ' + key + ' is required in resource ' + resource.id + '(bibType: ' + resource.bibType + ') and not present'
            });
          } else if (resource[key]) {
            resolvedResource[key] = resolvePropAgainstType(resource[key], propModel.valueType, propModel);
          } else if (propModel.default) {
            resource[key] = propModel.default;
          }
          return resolvedResource;
        }, {});
      } else {
        newErrors.push({
          type: 'error',
          preciseType: 'invalidResource',
          message: 'Could not find suitable data model for resource ' + resource.id
        });
      }
    }
  }
  return {newResources, newErrors};
};

/**
 * Resolver dedicated to resolve sections against metadata models
 * @module resolvers/resolveSectionAgainstModels
 */
import {resolvePropAgainstType} from '../utils/modelUtils';
import {serializeHtmlMeta} from '../resolvers/htmlMetaTemplateSerializer';

/**
 * Consumes and normalize the metadata of a section
 * @param {Object} section - the section to resolve
 * @param {Object} models - the models to use for resolution
 * @return {newErrors: array, newSection: Object} results - the new errors and section generated by resolution
 */
export const resolveSectionAgainstModels = (section, models) => {
  const errors = [];

  // validate metadata
  const sectionId = section.metadata.general.id.value;
  for (const domain in section.metadata) {
    if (section.metadata[domain]) {
      for (const key in section.metadata[domain]) {
        if (section.metadata[domain][key]) {
          const metadata = section.metadata[domain][key];
          const model = models.metadataModels[domain][key];
          if (model) {
            // resolve unicity
            const uniquePb = model.unique === true && Array.isArray(metadata.value) && metadata.value.length > 1;
            if (uniquePb) {
              errors.push({
                type: 'error',
                preciseType: 'invalidMetadata',
                sectionId,
                message: key + ' value was set more than once for section ' + sectionId
              });
              metadata.value = metadata.value[0];
            }

            if (Array.isArray(metadata.value)) {
              metadata.value = metadata.value.map((val)=> {
                if (typeof metadata.value === 'string') {
                  return resolvePropAgainstType(val, model.valueType, model);
                }
                return val;
              });

            }else if (typeof metadata.value === 'string') {
              metadata.value = resolvePropAgainstType(metadata.value, model.valueType, model);
            }

            if (model.headTemplate) {
              metadata.htmlHead = serializeHtmlMeta(metadata, key, model.headTemplate);
            }

          } else {
            errors.push({
              type: 'warning',
              preciseType: 'invalidMetadata',
              sectionId,
              message: domain + ' metadata property ' + key + ' is invalid in section ' + sectionId
            });
          }
        }
      }
    }
  }

  // set defaults
  for (const key in models.metadataModels.general) {
    if (models.metadataModels.general[key].default) {
      const present = section.metadata.general[key];
      if (present === undefined) {
        section.metadata.general[key] = {
          value: models.metadataModels.general[key].default
        };
      }
    }
  }
  return {newErrors: errors, newSection: section};
  // return callback(null, {newErrors: errors, newSection: section});
};

