export function renderObjectMetadata(document, inputMetaHead) {
  let metaHead = inputMetaHead;
  return Object.keys(document.metadata).map(domain => {
    return Object.keys(document.metadata[domain]).map(key => {
      if (document.metadata[domain][key] && document.metadata[domain][key].htmlHead) {
        metaHead += document.metadata[domain][key].htmlHead;
      }
    }).join('\n');
  }).join('\n');
}


/**
 * Renders a section representation as a string representation of an html page
 * @param {Object} params - The params of the rendering
 * @param {Object} params.document - the document to render
 * @param {Object} params.settings - the specific rendering settings to use in order to produce the output
 * @param {string} params.destinationFolder - where to output the file
 * @params {Object} assetsController - the module to use in order to communicate with assets
 * @param {Object} assetsParams - the assets parameters to use while communicating with assetsController
 * @param {function(err:error, result:string)} rendererCallback - the possible errors encountered during rendering, and the resulting html data as a string
 */
export const renderDocument = ({
  document,
  settings = {}
}, assetsController, assetsParams, rendererCallback) =>{

  // populate rendering params with defaults if needed
  const finalSettings = resolveSettings(settings, document.metadata.general.bibType.value, settingsModels);
  // let style = '';

  waterfall([
    // load default css rules
    /*
    (cback) =>{
      readFile(resolve(__dirname + defaultStylesPath + 'global.css'), (err, contents)=> {
        if (!err) {
          style += contents;
        }
        cback(err);
      });
    // load default @paged-related css rules
    },
    */
    (depCallback) =>{
      resolveDataDependencies(document, assetsController, assetsParams, false, depCallback);
    },
    // build html code
    (inputDocument,
      cback) =>{
      let renderedDocument = Object.assign({}, document); // inputDocument
      // build final css code (default + user-generated customizers)
      let style = '';
      const cssCustomizers = renderedDocument.customizers && renderedDocument.customizers.styles;
      if (cssCustomizers !== undefined) {
        for (const name in cssCustomizers) {
          if (name !== 'screen.css') {
            style += '\n\n' + cssCustomizers[name];
          }
        }
      }
      // build metadata html head at document level
      // todo : check if react-helmet renders dublincore metadata properly
      // (reason why I don't use it at first)
      let metaHead = '';
      Object.keys(document.metadata).forEach(domain => {
        Object.keys(document.metadata[domain]).forEach(key => {
          if (renderedDocument.metadata[domain][key] && renderedDocument.metadata[domain][key].htmlHead) {
            metaHead += renderedDocument.metadata[domain][key].htmlHead;
          }
        });
      });
      renderedDocument.metaHead = renderObjectMetadata(document, metaHead);

      // order contextualizations (ibid/opCit, ...)
      renderedDocument = resolveContextualizationsRelations(renderedDocument, finalSettings);

      // this renders react components, it is not good
      renderedDocument = Object.keys(renderedDocument.contextualizations).reduce((doc, contId)=>{
        return resolveContextualizationImplementation(doc.contextualizations[contId], doc, 'dynamic', finalSettings);
      }, renderedDocument);

      cback(null, renderedDocument);
    }
  ], rendererCallback);
};

export function resolveDocumentContextualizationsRelations(document, settings = []) {
  const finalSettings = resolveSettings(settings, document.metadata.general.bibType.value, settingsModels);
  return resolveContextualizationsRelations(document, finalSettings);
}

export function renderSectionContents(section, settings) {
  return Object.assign(section, {
    contents: setDynamicSectionContents(section, 'contents', settings)
  }, {
    notes: setDynamicSectionContents(section, 'notes', settings)
  });
}

/**
 * Render to static html
 * @module renderers/renderToStaticHtml
 */

import {waterfall} from 'async';
import {readFile} from 'fs';
import {resolve} from 'path';
import React from 'react';
import ReactDOMServer from 'react-dom/server';
import {IntlProvider} from 'react-intl';

import resolveDataDependencies from './../../core/resolvers/resolveDataDependencies';
import {resolveSettings} from './../../core/utils/modelUtils';
import {settingsModels} from './../../core/models';
import {
  resolveContextualizationImplementation,
  resolveContextualizationsRelations
} from './../../core/resolvers/resolveContextualizations';
import {
  composeRenderedSections,
  setStaticSectionContents
} from './../renderingUtils';
import {
  StaticDocument
} from './../../core/components';

const defaultStylesPath = './../../config/defaultStyles/';

/**
 * Renders a section representation as a string representation of an html page
 * @param {Object} params - The params of the render
 * @param {Object} params.document - the document to render
 * @param {Object} params.settings - the specific rendering settings to use in order to produce the output
 * @param {string} params.destinationFolder - where to output the file
 * @params {Object} assetsController - the module to use in order to communicate with assets
 * @param {Object} assetsParams - the assets parameters to use while communicating with assetsController
 * @param {function(err:error, result:string)} rendererCallback - the possible errors encountered during rendering, and the resulting html data as a string
 */
export const renderDocument = ({
  document,
  settings = {}
}, assetsController, assetsParams, rendererCallback) =>{

  // populate rendering params with defaults if needed
  const finalSettings = resolveSettings(settings, document.metadata.general.bibType.value, settingsModels);
  let style = '';

  waterfall([
    // load default css rules
    (cback) =>{
      readFile(resolve(__dirname + defaultStylesPath + 'global.css'), (err, contents)=> {
        if (!err) {
          style += contents;
        }
        cback(err);
      });
    // load default @paged-related css rules
    }, (cback) =>{
      readFile(resolve(__dirname + defaultStylesPath + 'page.css'), (err, contents)=> {
        if (!err) {
          style += contents;
        }
        cback(err);
      });
    }, (depCallback) =>{
      resolveDataDependencies(document, assetsController, assetsParams, true, depCallback);
    // build html code
    }, (inputDocument, cback) =>{
      let renderedDocument = Object.assign({}, inputDocument);
      // build final css code (default + user-generated customizers)
      const cssCustomizers = renderedDocument.customizers && renderedDocument.customizers.styles;
      if (cssCustomizers !== undefined) {
        for (const name in cssCustomizers) {
          if (name !== 'screen.css') {
            style += '\n\n' + cssCustomizers[name];
          }
        }
      }
      // build metadata (todo : check if react-based helmet lib could cover all metadata props like dublincore ones)
      let metaHead = '<meta name="generator" content="peritext"/>';
      Object.keys(document.metadata).forEach(domain => {
        Object.keys(document.metadata[domain]).forEach(key => {
          if (renderedDocument.metadata[domain][key] && renderedDocument.metadata[domain][key].htmlHead) {
            metaHead += renderedDocument.metadata[domain][key].htmlHead;
          }
        });
      });

      // order contextualizations (ibid/opCit, ...)
      renderedDocument = resolveContextualizationsRelations(renderedDocument, finalSettings);

      // resolve contextualizations js representation according to settings
      renderedDocument.figuresCount = 0;

      renderedDocument = Object.keys(renderedDocument.contextualizations).reduce((doc, contId)=>{
        return resolveContextualizationImplementation(doc.contextualizations[contId], doc, 'static', finalSettings);
      }, renderedDocument);

      // transform input js abstraction of contents to a js abstraction specific to rendering settings
      const sections = renderedDocument.summary.map(sectionKey => {
        const section1 = renderedDocument.sections[sectionKey];
        const contents = setStaticSectionContents(section1, 'contents', finalSettings);
        return Object.assign({}, section1, {contents}, {type: 'contents'});
      });

      // prepare translations
      const lang = renderedDocument.metadata.general.language ? renderedDocument.metadata.general.language.value : 'en';
      const messages = require('./../../../translations/locales/' + lang + '.json');
      // render sections
      const {renderedSections, finalStyle} = composeRenderedSections(sections, renderedDocument, finalSettings, style, messages);
      // render document
      const renderedContents = ReactDOMServer.renderToStaticMarkup(
        <IntlProvider locale={lang} messages={messages}>
          <StaticDocument document={renderedDocument} sections={renderedSections} settings={finalSettings} />
        </IntlProvider>);
      const html = `
<!doctype:html>
<html>
  <head>
    ${metaHead}
    <style>
      ${finalStyle}
    </style>
  </head>
  <body>
    ${renderedContents}
   </body>
</html>`.replace(/itemscope=""/g, 'itemscope');
      cback(null, html);
    }
  ], rendererCallback);
};

/**
 * Shared static rendering utils
 * @module renderers/sharedStaticUtils
 */

import {
  computeReferences
} from './../../core/utils/referenceUtils';
import {
  getGlossary,
} from './../../core/getters';
import {
  StaticFootnote,
  StaticNotePointer
} from './../../core/components';

/**
 * Resolves a sections' list against rendering settings by modifying contents, adding output-related pseudo-sections, and updating css styles
 * @param {array} sections - the sections to render
 * @param {Object} document - the document reference
 * @param {Object} settings - the specific rendering settings to use in order to produce the output
 * @param {string} inputStyle - the css style data to use
 * @param {array} messages - the intl messages to use for some sections localization (e.g. : translation of "Table of contents")
 * @return {Object} results - an object composed of an array of rendered sections and a string with the updated css styles
 */
export const composeRenderedSections = (sections = [], document, settings = {}, inputStyle = '', messages = []) =>{
  const renderedSections = sections.slice();
  let style = typeof inputStyle === 'string' ? inputStyle : '';
  // transform regarding notes display settings
  if (settings.notesPosition === 'footnotes') {
    style += `.peritext-static-note-content-container
            {
                display: prince-footnote;
                counter-increment: footnote;
            }`;
  } else if (settings.notesPosition === 'document-end') {
    let noteNumber = 0;
    const contents = sections.reduce((notes, section2) =>{
      return notes.concat(section2.notes.map(note =>{
        return Object.assign(note, {noteNumber: ++noteNumber});
      }));
    }, []);
    if (contents.length) {
      renderedSections.push({
        type: 'endnotes',
        contents,
        title: messages.end_notes,
        id: 'peritext-end-notes'
      });
    }
  }

  // handle figures
  if (settings.figuresPosition === 'document-end') {
    const figures = sections.reduce((figs, section3)=>{
      if (section3.figures) {
        return figs.concat(section3.figures);
      }
      return figs;
    }, []);
    if (figures.length) {
      renderedSections.push({
        type: 'endfigures',
        contents: figures,
        title: messages.end_figures,
        id: 'peritext-end-figures'
      });
    }
  }

  // build references/bibliography
  if (settings.referenceScope === 'document') {
    const refs = computeReferences(sections, document, settings);
    if (refs.length) {
      renderedSections.push({
        type: 'references',
        contents: refs,
        title: messages.references_title,
        id: 'peritext-end-references'
      });
    }
  }
  // handle glossary
  if (settings.glossaryPosition !== 'none') {
    // prepare glossary
    const glossaryData = getGlossary(document);
    const glossary = {
      type: 'glossary',
      contents: glossaryData,
      title: messages.glossary,
      id: 'peritext-end-glossary'
    };
    if (settings.glossaryPosition === 'begining' && glossary.contents.length) {
      renderedSections.splice(0, 0, glossary);
    } else if (glossary.contents.length) {
      renderedSections.push(glossary);
    }
  }

  // handle table of figures
  if (settings.figuresTablePosition !== 'none') {
    // making figures table data
    const figuresTableData = sections.reduce((figures, section4)=> {
      // 1. take numbered figures
      const figuresL = section4.contextualizations.filter((cont)=> {
        return document.contextualizations[cont].figureNumber !== undefined;
      })
      .map(contKey => document.contextualizations[contKey])
      // 2. filter uniques
      .filter((figure, index, self) => self.findIndex((other) => {
        return other.figureNumber === figure.figureNumber;
      }) === index)
      // 3. make table array
      .map((cont)=> {
        return {
          id: 'peritext-figure-' + cont.figureId,
          number: cont.figureNumber
        };
      });
      return figures.concat(figuresL);
    }, []);

    const figuresTable = {
      type: 'table-of-figures',
      contents: figuresTableData,
      title: messages.table_of_figures,
      id: 'peritext-end-table-of-figures'
    };
    if (settings.figuresTablePosition === 'begining' && figuresTable.contents.length) {
      renderedSections.splice(0, 0, figuresTable);
    } else if (figuresTable.contents.length) {
      renderedSections.push(figuresTable);
    }
  }

  // handle print table of contents
  if (settings.contentsTablePosition !== 'none') {
    const tocData = renderedSections.map((thisSection) => {
      return {
        id: thisSection.metadata ? thisSection.metadata.general.id.value : thisSection.id,
        title: thisSection.metadata ? thisSection.metadata.general.title.value : thisSection.title,
        level: thisSection.metadata ? thisSection.metadata.general.generalityLevel.value : 0
      };
    });
    const toc = {type: 'table-of-contents', contents: tocData};
    if (settings.contentsTablePosition === 'begining' && toc.contents.length) {
      renderedSections.splice(0, 0, toc);
    } else if (toc.contents.length) {
      renderedSections.push(toc);
    }
  }
  // handle forewords
  renderedSections.splice(0, 0, Object.assign({}, document.forewords, {type: 'forewords'}));
  // handle cover
  if (settings.showCovers === 'yes') {
    renderedSections.splice(0, 0, {
      type: 'front-cover',
      metadata: document.metadata
    });
    renderedSections.push({
      type: 'back-cover',
      metadata: document.metadata
    });
  }
  return {
    renderedSections,
    finalStyle: style
  };
};

const resolveStaticNode = (inputNode, section, settings) =>{
  const node = Object.assign({}, inputNode);
  if (node.tag === 'note') {
    const note = section.notes.find(thatNote =>{
      return thatNote.id === node.target;
    });
    node.props = {note};
    if (settings.notesPosition === 'footnotes') {
      node.tag = StaticFootnote;
    } else {
      node.tag = StaticNotePointer;
    }
    node.special = true;
  }
  if (node.children) {
    node.children = node.children.map(child =>{
      return resolveStaticNode(child, section, settings);
    });
  }
  return node;
};

export const setStaticSectionContents = (section, key, settings) =>{
  return section[key].map(node => {
    return resolveStaticNode(node, section, settings);
  });
};

const resolveDynamicNode = (inputNode, section, settings) =>{
  const node = Object.assign({}, inputNode);
  if (node.tag === 'note') {
    const note = section.notes.find(thatNote =>{
      return thatNote.id === node.target;
    });
    node.props = {note};
    node.tag = StaticNotePointer;
    node.special = true;
  }
  if (node.children) {
    node.children = node.children.map(child =>{
      return resolveDynamicNode(child, section, settings);
    });
  }
  return node;
};

export const setDynamicSectionContents = (section, key, settings) =>{
  return section[key].map(node => {
    return resolveDynamicNode(node, section, settings);
  });
};
