/**
 * Filesystem connector
 * @module connectors/filesystem
 */

import fs, {
  readFile,
  readdir,
  lstatSync,
  mkdir,
  exists,
  writeFile,
  unlink
} from 'fs';
import {
  resolve,
  extname,
  basename,
  join as joinPath
} from 'path';
import {map as asyncMap, reduce as asyncReduce} from 'async';
// import removeFolderRecursively from 'rmdir';

const removeFolderRecursively = function(path) {
  if ( fs.existsSync(path) ) {
    fs.readdirSync(path).forEach(function(file, index) {
      const curPath = path + '/' + file;
      if (fs.lstatSync(curPath).isDirectory()) { // recurse
        removeFolderRecursively(curPath);
      } else {
        // delete file
        fs.unlinkSync(curPath);
      }
    });
    fs.rmdirSync(path);
  }
};

// I get meta information about an fs element
const analyseElement = (fileName, absPath) =>{
  const path = joinPath(absPath, fileName);
  return {
    name: fileName,
    path,
    type: (lstatSync(path).isDirectory()) ? 'directory' : 'file',
    extname: extname(path)
  };
};

// I get meta information about several fs elements
const analyseContents = (filesList, absPath) =>{
  if (!filesList) {
    return undefined;
  }
  return filesList.map((fileName) => {
    return analyseElement(fileName, absPath);
  });
};


// I recursively parse an fs element
const parseElement = ({path = '', element, parseFiles, depth, actualDepth, acceptedExtensions}, callback) =>{
  // file to parse
  if (element.type === 'file' && parseFiles === true && acceptedExtensions.indexOf(element.extname) > -1) {
    try {
      readFile(path, 'utf8', function(err, str) {
        if (err) {
          return callback(err, undefined);
        }
        return callback(null, Object.assign({}, element, {stringContents: str}));
      });
    } catch (exception) {
      callback(null, element);
    }
  // dir to parse
  } else if (element.type === 'directory' && (actualDepth < depth || depth === true)) {
    readdir(element.path + '/', function(err, files) {
      const children = analyseContents(files, path)
                      .filter((child)=>{
                        return child.type === 'directory' || acceptedExtensions.indexOf(child.extname) > -1;
                      });
      const newDepth = actualDepth + 1;
      asyncMap(children, function(elem, colback) {
        parseElement({element: elem, path: path + '/' + elem.name, parseFiles, depth, actualDepth: newDepth, acceptedExtensions}, colback);
      }, function(error, otherChildren) {
        return callback(error, Object.assign({}, element, {children: otherChildren}));
      });
    });
  // default return element as it was input
  } else {
    return callback(null, Object.assign({}, element));
  }
};

/**
 * Reads a file from a certain path (cRud)
 * @param {Object} requestParams - The object containing request parameters
 * @param {array|string} requestParams.path - The detailed path (as a succession of "folders" or as a plain string) to attain the file
 * @param {Object} requestParams.params - The connection parameters
 * @param {string} requestParams.params.basePath - The path base to use in order to fetch files
 * @param {number} requestParams.depth - if the target is a "folder" that contains other "subfolders", specifies the parsing level
 * @param {boolean} requestParams.parseFiles - Whether to parse files if the target is a "folder"
 * @param {Array|string} obj.acceptedExtensions - The list of accepted files extensions (or "*") if all must be accepted
 * @param {function(error: error)} callback - returns error and a filesystem representation of the file metadata and contents
 */
export const readFromPath = ({
    path = [],
    params,
    depth = 1,
    parseFiles = false,
    acceptedExtensions = ['.md', '.bib', '.css', '.js']
  }, callback) =>{
  const resolvedPath = (Array.isArray(path)) ? path.join('/') : path;
  const finalPath = resolve(params.basePath) + '/' + resolvedPath;
  let element;
  const name = basename(finalPath);

  try {
    element = {
      name: name,
      path: finalPath,
      type: (lstatSync(finalPath).isDirectory()) ? 'directory' : 'file',
      extname: extname(name)
    };
  }catch (err) {
    return callback(err, undefined);
  }

  if (element.type === 'directory') {
    return parseElement({path: finalPath, element, parseFiles, depth, actualDepth: 0, acceptedExtensions}, callback);
  } else if (acceptedExtensions === '*' || acceptedExtensions.indexOf(element.extname) > -1) {
    readFile(finalPath, 'utf8', (err, str) => {
      if (err) {
        return callback(err, undefined);
      }
      return callback(null, Object.assign({}, element, {stringContents: str}));
    });
  } else {
    return callback(new Error('the file extension is not accepted'), undefined);
  }
};

/**
 * Creates a file from a certain path and possibly some contents (Crud)
 * @param {Object} requestParams - The object containing request parameters
 * @param {array|string} requestParams.path - The detailed path (as a succession of "folders" or as a plain string) to attain the file
 * @param {Object} requestParams.params - The connection parameters
 * @param {string} requestParams.params.basePath - The path base to use in order to fetch files
 * @param {string} requestParams.stringContents - the contents to be created
 * @param {boolean} requestParams.overwrite - Whether to overwrite existing entity already present at path
 * @param {function(error: error, returnedPath: string)} callback - returns error and the path of created entity
 */
export const createFromPath = ({path = '', params, type = 'file', stringContents = '', overwrite = false}, callback) =>{
  const resolvedPath = (Array.isArray(path)) ? path.join('/') : path;
  const finalPath = resolve(params.basePath) + '/' + resolvedPath;
  const pathSteps = finalPath.split('/').filter((thatPath)=> {return thatPath.length > 0;});
  // first check-or-create path folders
  const activePath = '/';
  asyncReduce(pathSteps, activePath, (inputMemo, pathStep, cback) =>{
    // case : not end of path, walking through
    if (pathStep !== pathSteps[pathSteps.length - 1]) {
      const memo = inputMemo + pathStep + '/';
      exists(memo, function(isThere) {
        if (isThere) {
          cback(null, memo);
        }else {
          mkdir(memo, function(err) {
            cback(err, memo);
          });
        }
      });
    // case : end of path
    } else {
      cback(null, inputMemo + pathStep);
    }

  }, (err, result) =>{
    // check if element already exists
    exists(finalPath, function(isThere) {
      if ((isThere && overwrite === true) || !isThere) {
        if (type === 'file') {
          writeFile(finalPath, stringContents, 'utf8', function(error) {
            callback(error);
          });
        }else if (type === 'directory') {
          mkdir(finalPath, function(error) {
            callback(error);
          });
        }else {
          callback(new Error('No element type matching'));
        }
      }else {
        callback(new Error('File/directory already exists and overwrite option is set to false'));
      }
    });
  });
};

/**
 * Updates a file from a certain path and possibly some contents (crUd)
 * @param {Object} requestParams - The object containing request parameters
 * @param {array|string} requestParams.path - The detailed path (as a succession of "folders" or as a plain string) to attain the file
 * @param {Object} requestParams.params - The connection parameters
 * @param {string} requestParams.params.basePath - The path base to use in order to fetch files
 * @param {string} requestParams.stringContents - the contents to be created
 * @param {function(error: error)} callback - returns possible error
 */
export const updateFromPath = ({path = '', params, stringContents = ''}, callback) => {
  const resolvedPath = (Array.isArray(path)) ? path.join('/') : path;
  const finalPath = resolve(params.basePath + '/' + resolvedPath);
  exists(finalPath, (isThere) =>{
    if (isThere) {
      const pathSteps = finalPath.split('/').filter((thatPath)=> {return thatPath.length > 0;});
      const elementName = pathSteps.pop();
      const element = analyseElement(elementName, '/' + pathSteps.join('/'));
      if (element.type === 'directory') {
        callback(new Error('cannot update directories'));
      } else if (element.type === 'file') {
        writeFile(finalPath, stringContents, (err) =>{
          callback(err);
        });
      }
    }else {
      callback(new Error('Path does not exists'));
    }
  });
};

/**
 * Deletes a file from a certain path and possibly some contents (cruD)
 * @param {Object} requestParams - The object containing request parameters
 * @param {array|string} requestParams.path - The detailed path (as a succession of "folders" or as a plain string) to attain the file
 * @param {Object} requestParams.params - The connection parameters
 * @param {string} requestParams.params.basePath - The path base to use in order to fetch files
 * @param {function(error: error)} callback - returns potential errors
 */
export const deleteFromPath = ({path = '', params}, callback) => {
  const resolvedPath = (Array.isArray(path)) ? path.join('/') : path;
  const finalPath = resolve(params.basePath) + '/' + resolvedPath;
  exists(finalPath, function(isThere) {
    if (isThere) {
      const pathSteps = finalPath.split('/').filter((thatPath) => {return thatPath.length > 0;});
      const elementName = pathSteps.pop();
      const element = analyseElement(elementName, '/' + pathSteps.join('/'));
      if (element.type === 'directory') {
        removeFolderRecursively(finalPath /*
        , (err) =>{
          callback(err);
        }
        */);
        callback(null);
      }else if (element.type === 'file') {
        unlink(finalPath, (err) =>{
          callback(err);
        });
      }
    }else {
      callback(new Error('Path does not exists'));
    }
  });
};

/**
 * Gets the absolute uri of an asset
 * @param {Object} requestParams - The object containing request parameters
 * @param {array|string} requestParams.path - The detailed path (as a succession of "folders" or as a plain string) to attain the file
 * @param {Object} requestParams.params - The connection parameters
 * @param {string} requestParams.params.basePath - The path base to use in order to fetch files
 * @param {function(error: error, finalPath: string)} callback - returns error and the asset's uri
 * @todo question : should it check for resource availability ?
 */
export const getAssetUri = ({path, params}, callback) => {
  const resolvedPath = (Array.isArray(path)) ? path.join('/') : path;
  const finalPath = resolve(params.basePath) + '/' + resolvedPath;
  return callback(null, finalPath);
};

/**
 * Citation contextualizer that resolve sections data according to contextualization+settings params
 * @module contextualizers/citation
 */

/**
 * Handle an inline contextualization for static outputs
 * @param {Object} inputSection - The representation of the peritext section to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newSection - the updated representation of the peritext section in which the contextualization was made
 */
export const contextualizeInlineStatic = (inputSection, inputContextualization, settings) => {
  const contextualization = Object.assign({}, inputContextualization);
  const formatter = require('./../../referencers/' + settings.citationStyle + '.js');
  const node = contextualization.node;
  const props = {
    contextualization,
    resource: contextualization.resources[0],
    ibid: contextualization.sectionIbid,
    opCit: contextualization.sectionOpCit
  };
  // citation text --> wrap in span > q + citation
  if (node.child) {
    const citation = {
      node: 'element',
      special: true,
      tag: formatter.InlineCitation,
      props
    };
    const child = node.child.slice();
    const quote = {
      attr: {
        class: 'peritext-quote-container',
        id: contextualization.citeKey
      },
      node: 'element',
      tag: 'q',
      child
    };
    node.node = 'element';
    node.tag = 'span';
    node.child = [
      quote,
      {
        node: 'text',
        text: ' ('
      },
      citation,
      {
        node: 'text',
        text: ')'
      }
    ];
  } else {
    node.special = true;
    node.tag = formatter.InlineCitation;
    node.props = props;
  }
  return Object.assign({}, inputSection);
};

/**
 * Handle a block contextualization for static outputs
 * @param {Object} inputSection - The representation of the peritext section to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newSection - the updated representation of the peritext section in which the contextualization was made
 */
export const contextualizeBlockStatic = (inputSection, inputContextualization, settings) => {
  const contextualization = Object.assign({}, inputContextualization);
  const formatter = require('./../../referencers/' + settings.citationStyle + '.js');
  const node = contextualization.node;
  const props = {
    contextualization,
    resource: contextualization.resources[0],
    ibid: contextualization.sectionIbid,
    opCit: contextualization.sectionOpCit
  };
  node.special = true;
  node.tag = formatter.BlockCitation;
  node.props = props;
  return Object.assign({}, inputSection);
};

/**
 * Handle an inline contextualization for dynamic outputs
 * @param {Object} inputSection - The representation of the peritext section to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newSection - the updated representation of the peritext section in which the contextualization was made
 */
export const contextualizeInlineDynamic = (section, contextualization, settings) => {
  return section;
};

/**
 * Handle an block contextualization for dynamic outputs
 * @param {Object} inputSection - The representation of the peritext section to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newSection - the updated representation of the peritext section in which the contextualization was made
 */
export const contextualizeBlockDynamic = (section, contextualization, settings) => {
  return section;
};

import React, {PropTypes} from 'react';
import Radium from 'radium';
// let styles = {};
import {bibToSchema} from './../../core/utils/microDataUtils';

/**
 * dumb component and placeholder for rendering the structured representation of an entity long citation (in a glossary for example)
 */
@Radium
export default class StaticEntityBlock extends React.Component {

  /**
   * propTypes
   * @property {object} entity - the entity resource to contextualize
   * @property {object} contextualizer - the contextualizer params to use for contextualization
   * @property {object} contextualization - the contextualization object
   * @property {object} settings - the set of settings to use for rendering
   */
  static propTypes = {
    entity: PropTypes.object,
    contextualizer: PropTypes.object,
    contextualization: PropTypes.object,
    settings: PropTypes.object
  };

  static defaultProps = {
    contextualizer: {
      showdescription: 'yes'
    }
  };

  /**
   * render a structured representation of the entities mentions, sorted by aliases
   * @return {ReactElement} markup
   */
  renderMentions() {
    const self = this;
    return Object.keys(this.props.entity.aliases).map(function(alias, aliasIndex) {
      return (<p key={alias} className="peritext-static-entity-block-page-mentions-container">
        <span>{alias === 'no-alias' ? '' : alias + ' : '}</span>
        {self.props.entity.aliases[alias].map((entry, index)=> {
          return (<span key={entry.mentionId}>
              p. <a className="peritext-static-entity-block-page-pointer" href={entry.mentionId}></a>
            </span>);
        }).reduce((accu, elem) => {
          return accu === null ? [elem] : [...accu, ', ', elem];
        }, null)}
      </p>);
    });
  }

  /**
   * render component
   * @return {ReactElement} markup
   */
  render() {
    const itemType = bibToSchema(this.props.entity.bibType);
    return (
      <section
        className="peritext-static-entity-block-container"
        name={'peritext-static-entity-block-' + this.props.entity.citeKey}
        id={'peritext-static-entity-block-' + this.props.entity.citeKey}
        itemProp="mentions"
        value="mentions"
        itemScope
        itemType={'http://schema.org/' + itemType}
        typeof={itemType}
        resource={this.props.entity.citeKey}
      >
        <h5
          className="peritext-static-entity-block-name">
          {itemType === 'Person' ?
            [<span
              itemProp="familyName"
              property="familyName"
              key="familyName"
            >{this.props.entity.lastname}</span>,
            <span key="separator1"> (</span>,
            <span
              itemProp="givenName"
              property="givenName"
              key="givenName"
            >{this.props.entity.firstname}</span>,
            <span key="separator2">)</span>]
            :
            <span
              property="name"
              itemProp="name"
            >{this.props.entity.name}</span>
          }
        </h5>

        {this.props.entity.aliases ?
            this.renderMentions()
            : ''}

        {this.props.entity.description && this.props.contextualizer.showdescription === 'yes' ?
          <p className="peritext-static-entity-block-description">
            {this.props.entity.description}
          </p>
          : ''}
      </section>
    );
  }
}

import React, {PropTypes} from 'react';
import Radium from 'radium';

import {bibToSchema} from './../../core/utils/microDataUtils';
// let styles = {};
import renderContents from './../../core/utils/componentsFactory';

/**
 * dumb component and placeholder for rendering the structured representation of an entity citation
 */
@Radium
export default class StaticEntityInline extends React.Component {

  /**
   * propTypes
   * @property {object} entity - the entity resource to contextualize
   * @property {string} sectionCiteKey - the host section citeKey (used for identifying the element)
   * @property {object} contextualization - the contextualization object
   * @property {array} contents - the pseudo-dom js representation of contextualization's pointer contents
   */
  static propTypes = {
    entity: PropTypes.object,
    sectionCiteKey: PropTypes.string,
    contextualization: PropTypes.object,
    contents: PropTypes.array
  };

  static defaultProps = {
  };

  /**
   * render
   * @return {ReactElement} markup
   */
  render() {
    const itemType = bibToSchema(this.props.entity.bibType);
    return (
      <a
        className="peritext-static-entity-container-inline"
        name={'peritext-content-entity-inline-' + this.props.sectionCiteKey + '-' + this.props.contextualization.citeKey}
        id={'peritext-static-entity-inline-' + this.props.sectionCiteKey + '-' + this.props.contextualization.citeKey}
        href={'#peritext-static-entity-block-' + this.props.entity.citeKey}
        itemProp="mentions"
        value="mentions"
        itemScope
        itemType={'http://schema.org/' + itemType}
        typeof={itemType}
        resource={this.props.entity.citeKey}
      >
        {itemType === 'Person' ?
          [<span
            style={{display: 'none'}}
            itemProp="givenName"
            property="givenName"
            key="givenName"
          >{this.props.entity.firstname}</span>,
          <span
            style={{display: 'none'}}
            itemProp="familyName"
            property="familyName"
            key="familyName"
          >{this.props.entity.lastname}</span>]
          :
          <span
            style={{display: 'none'}}
            property="name"
            itemProp="name"
          >{this.props.entity.name}</span>
        }
        <span>{renderContents(this.props.contents)}</span>
      </a>
    );
  }
}

/**
 * Glossary contextualizer that resolve sections data according to contextualization+settings params
 * @module contextualizers/glossary
 */

import StaticEntityInline from './StaticEntityInline.js';
import StaticEntityBlock from './StaticEntityBlock.js';
import {getMetaValue} from './../../core/utils/sectionUtils';

/**
 * Handle an inline contextualization for static outputs
 * @param {Object} inputSection - The representation of the peritext section to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newSection - the updated representation of the peritext section in which the contextualization was made
 */
export const contextualizeInlineStatic = (inputSection, inputContextualization, settings) => {
  const section = Object.assign({}, inputSection);
  const contextualization = Object.assign({}, inputContextualization);
  const node = contextualization.node;
  const sectionCiteKey = getMetaValue(section.metadata, 'general', 'citeKey');
  const entity = contextualization.resources[0];
  let contents = node.child;
  if (!contents || (contents[0] && contents[0].text.trim().length === 0)) {
    contents = contextualization.contextualizer.alias || entity.name || entity.firstname + ' ' + entity.lastname;
    contents = [{
      node: 'text',
      text: contents
    }];
  }
  node.props = {
    contextualization,
    entity,
    contents,
    sectionCiteKey
  };
  node.special = true;
  node.tag = StaticEntityInline;
  return Object.assign({}, inputSection);
};

/**
 * Handle a block contextualization for static outputs
 * @param {Object} inputSection - The representation of the peritext section to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newSection - the updated representation of the peritext section in which the contextualization was made
 */
export const contextualizeBlockStatic = (inputSection, inputContextualization, settings) => {
  const contextualization = Object.assign({}, inputContextualization);
  const node = contextualization.node;
  const entity = contextualization.resources[0];
  node.props = {
    entity,
    contextualization,
    settings
  };
  node.special = true;
  node.tag = StaticEntityBlock;
  return Object.assign({}, inputSection);
};

/**
 * Handle an inline contextualization for dynamic outputs
 * @param {Object} inputSection - The representation of the peritext section to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newSection - the updated representation of the peritext section in which the contextualization was made
 */
export const contextualizeInlineDynamic = (section, contextualization, settings) => {
  return section;
};

/**
 * Handle a block contextualization for dynamic outputs
 * @param {Object} inputSection - The representation of the peritext section to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newSection - the updated representation of the peritext section in which the contextualization was made
 */
export const contextualizeBlockDynamic = (section, contextualization, settings) => {
  return section;
};

import React, {PropTypes} from 'react';
import {StaticImageFigure} from './../../core/components';
// let styles = {};

import renderContents from './../../core/utils/componentsFactory';
import Radium from 'radium';

/**
 * dumb static-oriented component for displaying an image gallery
 */
@Radium
export default class StaticImageGallery extends React.Component {
  /**
   * propTypes
   * @property {string} schematype - html schema type of the element
   * @property {array} resources - array of resources used
   * @property {string} captionContent - what to display as caption
   * @property {number} figureNumber - in static mode, the number of the figure
   * @property {string} id - the id to use in order to label the figure
   */
  static propTypes = {
    schematype: PropTypes.string,
    resources: PropTypes.array,
    captionContent: PropTypes.array,
    figureNumber: PropTypes.number,
    id: PropTypes.string
  };

  static defaultProps = {
    schematype: 'ImageGallery',
    comment: ''
  };

  /**
   * render
   * @return {ReactElement} markup
   */
  render() {
    return (
            <figure
              role="group"
              className="peritext-static-image-gallery-container peritext-figure-container"
              itemScope
              itemProp="citation"
              itemType={'http://schema.org/' + this.props.schematype}
              typeof={this.props.schematype}
              resource={this.props.figureNumber ? 'peritext-figure-' + this.props.figureNumber : undefined }
              id={'peritext-figure-' + this.props.id}
            >
              <div className="peritext-static-image-gallery-figures-wrapper">
                {this.props.resources.map((resource)=>{
                  return <StaticImageFigure resource={resource} key={resource.citeKey} />;
                })}
              </div>
              <figcaption
                itemProp="description"
                property="description">
                  <span className="peritext-figure-marker">
                    Figure <span className="peritext-figure-number">{this.props.figureNumber}</span>
                  </span>
                  <span>  </span>
                  <span className="peritext-figure-caption-content">
                    {renderContents(this.props.captionContent)}
                  </span>
              </figcaption>
            </figure>
          );
  }
}

/**
 * Image gallery contextualizer that resolve sections data according to contextualization+settings params
 * @module contextualizers/imagegallery
 */

import StaticImageGallery from './StaticImageGallery.js';
import {getMetaValue} from './../../core/utils/sectionUtils';

/**
 * Handle an inline contextualization for static outputs
 * @param {Object} inputSection - The representation of the peritext section to update
 * @param {Object} inputContextualization - The representation of the contextualization to resolve
 * @param {Object} settings - the specific rendering settings to use for resolving the contextualization
 * @return {Object} newSection - the updated representation of the peritext section in which the contextualization was made
 */
export const contextualizeInlineStatic = (inputSection, inputContextualization, settings) => {
  const section = Object.assign({}, inputSection);
  const contextualization = Object.assign({}, inputContextualization);
  const citeKey = getMetaValue(section.metadata, 'general', 'citeKey');
  const node = contextualization.node;
  const blockIndex = node.blockIndex;
  let figureId;
  let number;
  const contents = node.child;
  // figure is not there yet, add it
  if (!contextualization.sectionOpCit) {
    section.figuresCount ++;
    figureId = citeKey + '-' + contextualization.citeKey;
    contextualization.figureId = figureId;
    contextualization.figureNumber = section.figuresCount;
    const figure = {
