  StructuredPerson
} from './../index.js';

/**
 * dumb component for back cover page of a static publication of document
 */
@Radium
export default class StaticBackCover extends React.Component {

  /**
   * propTypes
   * @property {array} metadata - a section metadata to parse in order to fill cover template
   */
  static propTypes = {
    metadata: PropTypes.array
  };

  static defaultProps = {
  };

  /**
   * Util for returning the value of a "general" type metadata
   * @param {array} list - the list of metadata
   * @param {string} key - the key of the metadata prop
   * @return {string} value
   */
  getGeneralProp(list, key) {
    const obj = list.find((meta) => {
      return meta.domain === 'general' && meta.key === key;
    });
    if (obj) {
      return obj.value;
    }
    return undefined;
  }

  /**
   * render
   * @return {ReactElement} markup
   */
  render() {
    return (
      <section
        id="peritext-static-back-cover"
        className="peritext-static-back-cover"
      >
        <h2>{this.getGeneralProp(this.props.metadata, 'title')}</h2>
        <h3 className="peritext-static-authors">
          {this.getGeneralProp(this.props.metadata, 'author').map((person) =>{
            return <StructuredPerson key={person.citeKey} resource={person}/>;
          })}
        </h3>
        <p>
          {this.getGeneralProp(this.props.metadata, 'abstract')}
        </p>
      </section>
    );
  }
}

import React, {PropTypes} from 'react';
import Radium from 'radium';
import { intlShape } from 'react-intl';
// let styles = {};
import {getMetaValue} from './../../utils/sectionUtils';
import {bibToSchema} from './../../utils/microDataUtils';
import {
  StaticBackCover,
  StaticEndNotes,
  StaticEndFigures,
  StaticFrontCover,
  StaticGlossary,
  StaticReferencesList,
  StaticSection,
  StaticTableOfContents,
  StaticTableOfFigures,
  StructuredMetadataPlaceholder
} from './../index.js';


/**
 * dumb component for rendering the structured representation of a static document
 */
@Radium
class StaticDocument extends React.Component {

  /**
   * propTypes
   * @property {Object} rootSection - the root/first/mother section of the document
   * @property {array} sections - the list of rendering sections to include - warning -> can include cover, table of contents, ... sections
   * @property {Object} settings - the rendering settings to use
   */
  static propTypes = {
    rootSection: PropTypes.object,
    sections: PropTypes.array,
    settings: PropTypes.object
  };

  static defaultProps = {
  };

  /**
   * render
   * @return {ReactElement} markup
   */
  render() {
    const bibType = bibToSchema(getMetaValue(this.props.rootSection.metadata, 'general', 'bibType'));
    const citeKey = getMetaValue(this.props.rootSection.metadata, 'general', 'citeKey');
    return (
        <section
          itemScope
          itemType={'http://schema.org/' + bibType}
          typeof={bibType}
          vocab="http://schema.org/"
          resource={'#' + citeKey}
        >
          <StructuredMetadataPlaceholder section={this.props.rootSection} />

          {this.props.sections.map((section, index)=> {
            switch (section.type) {
            case 'table-of-contents':
              return (section.contents.length) ? <StaticTableOfContents id={section.id} key={index} contents={section.contents} /> : '';
            case 'table-of-figures':
              return (section.contents.length) ? <StaticTableOfFigures id={section.id} key={index} contents={section.contents} /> : '';
            case 'front-cover':
              return <StaticFrontCover key={index} metadata={section.metadata} />;
            case 'back-cover':
              return <StaticBackCover key={index} metadata={section.metadata} />;
            case 'endnotes':
              return (section.contents.length) ? <StaticEndNotes id={section.id} key={index} notes={section.contents} classSuffix="document-end" /> : '';
            case 'endfigures':
              return (section.contents.length) ? <StaticEndFigures id={section.id} key={index} contents={section.contents} classSuffix="document-end" /> : '';
            case 'references':
              return (section.contents.length) ? <StaticReferencesList id={section.id} key={index} references={section.contents} settings={this.props.settings} /> : '';
            case 'glossary':
              return (section.contents.length) ? <StaticGlossary id={section.id} key={index} elements={section.contents} /> : '';
            case 'contents':
              return <StaticSection key={index} section={section} settings={this.props.settings} />;
            default:
              break;
            }
          })}
        </section>
    );
  }
}

StaticDocument.contextTypes = { intl: intlShape };
export default StaticDocument;

