/**
 * Assets controller - a set of pure functions for interacting with an assets source (through a connector)
 * @module controllers/assetsController
 */

let connector;
let connectorName;
let tempConnectorName;

/**
* Updates the active connector module according to connection params
* @param {Object} params - connection params
*/
const updateConnector = (params)=> {
  tempConnectorName = params.connector;
  if (tempConnectorName !== connectorName) {
    connectorName = tempConnectorName;
    connector = require('./../../../connectors/' + params.connector);
  }
};

/**
 * Provides the URI of a specific assets file from connector
 * @param {array|string} path - the path of the assets file to look for
 * @param {Object} params - connection params
 * @return {function(connectorError: error, uri: string)} callback - possible errors and the uri
*/
export const getAssetUri = (path, params, callback)=> {
  updateConnector(params);
  connector.getAssetUri({path, params}, callback);
};

/**
* Returns an appropriate connector function for directly reading a file from assets source
* @param {Object} params - connection params
* @return {function(err: error, res: Object)} readFunction - the connector function for directly reading a file contents from assets source
*/
export const getReader = (params) =>{
  updateConnector(params);
  return connector.readFromPath;
};

/**
 * Contents controller - a set of pure functions for parsing and serializing fsTree representations to peritextSections representations
 * @module controllers/contentsController
 */

import {waterfall, map as asyncMap} from 'async';
import {parseSection, serializeSectionList} from './../../converters/sectionConverter';
import {diff} from 'deep-diff';
import * as filesystem from './../../../connectors/filesystem';
let connector;
let connectorName;
let tempConnectorName;

/**
* Updates the active connector module according to connection params
* @param {Object} params - connection params
*/
const updateConnector = (params) => {
  tempConnectorName = params.connector;
  if (tempConnectorName !== connectorName) {
    connectorName = tempConnectorName;
    if ( connectorName === 'filesystem' ) {
      connector = filesystem;
    }
    // this method is better but does not work when integrating the lib with webpack
    // connector = require('./../../../connectors/' + params.connector);
  }
};

/**
* Resolves a difference object through Create/Update/Delete operations
* @param {Object} difference - the difference object specifying type of difference and details
* @param {Object} params - connection params
* @param {Object} connect - connector module
* @param {function(connectorError: error, connectorResult)} callback - provided by connector
*/
const applyDifference = (difference, params, connect, callback) => {
  let item;
  switch (difference.kind) {
  // new element
  case 'N':
    console.log('unhandled new element difference ', difference);
    break;
  // delete element
  case 'D':
    connect.deleteFromPath({path: difference.lhs.path, params}, callback);
    break;
  // edit element
  case 'E':
    if (difference.path.pop() === 'path') {
      return callback();
    }
    console.log('unhandled edit difference ', difference);
    break;
  // change in array
  case 'A':
    switch (difference.item.kind) {
    case 'N':
      item = difference.item.rhs;
      waterfall([
        // create root
        function(cb) {
          connect.createFromPath({path: item.path, params, type: item.type, overwrite: true, stringContents: (item.stringContents || '')}, cb);
        },
        // create children
        function(cb) {
          if (item.children) {
            asyncMap(item.children, function(child, cb2) {
              connect.createFromPath({path: child.path, params, type: child.type, overwrite: true, stringContents: (child.stringContents || '')}, cb2);
            }, cb);
          } else cb();
        }
      ], callback);
      break;

    case 'D':
      item = difference.item.lhs;
      connect.deleteFromPath({path: item.path, params}, callback);
      break;
    case 'E':
      item = difference.item.rhs;
      waterfall([
        // create root
        function(cb) {
          connect.updateFromPath({path: item.path, params, stringContents: (item.stringContents || '')}, cb);
        },
        // create children
        function(cb) {
          if (item.children) {
            asyncMap(item.children, function(child, cb2) {
              connect.updateFromPath({path: child.path, params, stringContents: (child.stringContents || '')}, cb2);
            }, cb);
          } else cb();
        }
      ], callback);
      break;
    default:
      console.log('unhandled difference ', difference);
      break;
    }
    break;
  default:
    console.log('unhandled difference ', difference);
    break;
  }
  callback();
};

/**
  * @param {Object} params - connection params
  * @param {Object} models - peritext models to use
  * @param {Object} parametesr - language templates parameters - with which expressions are templates or includes wrapped ?
  * @param {function(parsingErrors: error, results:Object)} callback - block errors and a result object containing section and an array of warnings+parsing errors
  */
export const updateFromSource = (params, models, parameters, callback) => {
  updateConnector(params);
  waterfall([
    function(cb) {
      connector.readFromPath({params, path: [], depth: true, parseFiles: true}, function(err, results) {
        cb(err, results);
      });
    },
    function(tree, cb) {
      parseSection({tree, models, parameters}, cb);
    }
  ],
  function(err, results) {
    callback(err, results);
  });
};

/**
* Serializes the representation of a series of peritext sections to an fsTree representation by "diffing" new fsTree with previous fsTree
* then makes a diff list with deep-diff
* then monitor source tree updating (with C.U.D. operations) accordingly
* @param {Object} params - connection params
* @param {array} sections - the peritext sections to serialize
* @param {Object} models - peritext models to use
* @param {Object} oldFsTree - the previous fsTree representation to compare with the new
* @param {function(serializingErrors: error, sections:array)} callback - errors and input sections
*/
export const updateToSource = (params, sections, models, oldFsTree, callback) => {
  updateConnector(params);
  serializeSectionList({sectionList: sections, models, basePath: params.basePath}, function(err, newFsTree) {
    const differences = diff(oldFsTree, newFsTree);
    asyncMap(differences, function(difference, cb) {
      applyDifference(difference, params, connector, cb);
    }, function(errors, res) {
      callback(errors, sections);
    });
  });
};

/**
 * This module parses and serializes bibTex objects
 * @module converters/bibTexConverter
 */

/**
 * homemade bibTeX syntax parser (performance could be improved I guess)
 * it needs a second pass to parse {} in values and analyze them against a model - that should be done elsewhere, as this converter just deals with syntax-to-object conversion
 */
class bibTexParser {
  /**
   * constructor
   */
  constructor() {
    this.STATES = ['bibType', 'citeKey', 'properties'];
  }

  /**
   * Adds a key+value prop to the resource's javascript representation object
   * @param {object} obj - the resource representation
   * @param {string} key - the key to populate
   * @param {string} value - the value to set
   * @return {object} newObj - the updated resource representation
   */
  addValue(obj, key, value) {
    if (obj[key]) {

      if (Array.isArray(obj[key])) {
        obj[key].push(value);
      }else obj[key] = [obj[key], value];

    }else obj[key] = value;

    return obj;
  }

  /**
   * Consumes a string (stored in this.consumable) representation of a bibTeX expression
   * @return {boolean} exit - a simple exit trigger (but internal props will have been mutated)
   */
  consume() {
    const matchBibType = /^@([^{]+)/;
    const matchCiteKey = /^{([^,]+),/;
    const wrappers = [
      ['{', '}'],
      ['"', '"'],
      ["'", "'"]
    ];
    let match;
    if (this.currentState === 'bibType') {
      match = matchBibType.exec(this.consumable);
      if (match) {
        this.currentObject.bibType = match[1];
        this.consumable = this.consumable.substr(match[1].length + 1);
        this.currentState = this.STATES[1];
        return true;
      }
      this.error = {
        type: 'error',
        preciseType: 'bibParsingError',
        message: 'could not find bibType',
        initialString: this.initialStr
      };
      return true;

    }else if (this.currentState === 'citeKey') {
      match = matchCiteKey.exec(this.consumable);
      if (match) {
        this.currentObject.citeKey = match[1];
        this.consumable = this.consumable.substr(match[1].length + 2);
        this.currentState = this.STATES[2];
        return true;
      }
      this.error = {
        type: 'error',
        preciseType: 'bibParsingError',
        message: 'could not find citekey',
        initialString: this.initialStr
      };
      return true;

    /*
     * ``key = value`` structure
     * value is always wrapped inside character couples that vary depending on bibtex implementation (" { ')
     * possibility of nested wrapping (eg {Name {{S}urname}})
     */
    }else if (this.currentState === 'properties') {
      const wrapped = [wrappers[0]];
      let index = 0;
      let mode = 'key';
      let temp = '';
      let tempKey = '';
      let trespassing;
      let character;
      let entering;

      while (wrapped.length > 0) {

        trespassing = index > this.consumable.length - 1;
        character = this.consumable.charAt(index);

        if (trespassing) {
          this.error = {
            type: 'error',
            preciseType: 'bibParsingError',
            message: 'finished to parse bibtex string without finding closing character ' + wrapped[wrapped.length - 1][1],
            initialString: this.initialStr
          };
          return true;
        // end of wrapped expression - if matches with last recorded wrapper's closing character
        }else if (character === wrapped[wrapped.length - 1][1]) {
          wrapped.pop();
          if (wrapped.length > 1) {
            temp += character;
          }
          index = 1;
        // end of key specification, record tempkey and wait to have found value
        }else if (mode === 'key' && character === '=') {
          tempKey = temp.trim();
          temp = '';
          mode = 'value';
          index = 1;
        // end of value specification - add value and reboot temp
        }else if (mode === 'value' && wrapped.length < 2 && character === ',') {
          this.addValue(this.currentObject, tempKey, temp.trim());
          temp = '';
          mode = 'key';
          index = 1;
        // in the middle of some key or value = continue
        }else if (mode === 'value') {
          entering = false;
          // catch wrapper char
          wrappers.some((wrapper) => {
            if (this.consumable.charAt(index) === wrapper[0]) {
              entering = true;
              return wrapped.push(wrapper);
            }
          });
          if (!(entering && wrapped.length <= 2)) {
            temp += character;
          }
          index = 1;
        // default, by security
        }else {
          temp += character;
          index = 1;
        }
        this.consumable = this.consumable.substr(index);
      }
      this.addValue(this.currentObject, tempKey.trim(), temp.trim());

      // add if not empty
      if (Object.keys(this.currentObject).length) {
        this.results.push(Object.assign({}, this.currentObject));
      }
      this.currentObject = {};
      this.consumable = this.consumable.substr(index).trim();
      this.currentState = this.STATES[0];
      return true;
    }
  }


  /**
   * Parses a bibTeX string and callbacks a javascript objet
   * @param {string} str - the string to parse
   * @param {function(error:error, results:Object)} callback
   */
  parse(str, callback) {
    this.currentObject = {};
    this.currentState = this.STATES[0];
    this.results = [];
    this.consumable = str.trim();
    this.initialStr = str.trim();
    this.error = null;
    while (this.error === null && this.consumable.trim().length > 0) {
      this.consume();
    }
    return callback(this.error, this.results);
  }

}

const parser = new bibTexParser();

const validateBibObject = function(bibObject) {
  if (bibObject.citeKey === undefined) {
    return {
      type: 'error',
      preciseType: 'bibObjectValidationError',
      message: 'bibObject must have a citeKey property',
      bibObject: bibObject
    };
  } else if (bibObject.bibType === undefined) {
    return {
      type: 'error',
      preciseType: 'bibObjectValidationError',
      message: 'bibObject must have a bibType property',
      bibObject: bibObject
    };
  }

  for (const key in bibObject) {
    if (typeof bibObject[key] === 'object' && !Array.isArray(bibObject[key])) {
      return {
        type: 'error',
        preciseType: 'bibObjectValidationError',
        message: 'bibObject cannot contain nested objects',
        bibObject: bibObject
      };
    }
  }

  return true;
};

/**
 * Serializes a javascript object representing a resource or a contextualizer, to a bibTeX-formatted string
 * @param {object} bibObject - the resource/contextualizer representation
 * @return {function(error:error, bibStr: string)} callback - error and the bibTeX mention as a string
 */
export const serializeBibTexObject = (bibObject, callback) => {
  const validated = validateBibObject(bibObject);
  if (validated.type === 'error') {
    return callback(validated, undefined);
  }
  let str = '';
  let val;
  for (const key in bibObject) {
    if (bibObject[key]) {
      val = bibObject[key];
      if (Array.isArray(val)) {
        val.forEach((value)=>{
          str += '\t' + key + ' = {' + value + '},\n';
        });
        //  val = val.join(',');
      }else if (key !== 'citeKey' && key !== 'bibType') {
        str += '\t' + key + ' = {' + val + '},\n';
      }
    }
  }
  // removing the last coma
  if (str.length > 1) {
    str = str.substr(0, str.length - 2);
  }
  return callback(null, `@${bibObject.bibType}{${bibObject.citeKey},
    ${str}
}`);
};

/**
 * Parses a bibTeX string and returns an object
 * @param {string} str - the bibTeX string to parse
 * @return {function(error: error, result: Object)} callback - error and the resulting object
 */
export const parseBibTexStr = (str, callback) => {
  if (typeof str === 'string') {
    return parser.parse(str, callback);
  }
  return callback(new Error('must input a string'), undefined);
};

/**
Accepted inputs for authors and persons (to add in doc/spec):
{Martin}, Julia; Coleman
{Jakubowicz}, Andrew
{Charalambos}, D. Aliprantis and Kim C. {Border}
{Martin}, Julia; Coleman
{Jakubowicz}, Andrew
{Charalambos}, D. Aliprantis and Kim C. {Border}
Maskin, Eric S.
{Martin}, Julia; Coleman
{Jakubowicz}, Andrew
{Charalambos}, D. Aliprantis and Kim C. {Border}
Maskin, Eric S.
{Martin}, Julia; Coleman
{Jakubowicz}, Andrew
{Charalambos}, D. Aliprantis and Kim C. {Border}
*/

/**
 * Parses a list of authors as they are formatted in bbiTeX statements
 * @param {string} str - the bibTeX string to parse
 * @return {array} authors - the javascript representation of authors as an array
 */
export const parseBibAuthors = (str) => {
  const authors = str.split(/;|and|et/);
  const additionalInfo = /\(([^)]*)?\)?\(?([^)]*)?\)?/;
  let match;
  return authors.filter((inputStr) =>{
    return inputStr.trim().length > 0;
  }).map((inputAuthorStr) =>{
    let workingStr = inputAuthorStr;
    let authorStr = '';
    let firstName;
    let lastName;
    let role = 'author';
    let information;
    match = inputAuthorStr.match(additionalInfo);
    if (match) {
      workingStr = workingStr.replace(match[0], '');
      if (match[1]) {
        role = match[1].trim();
      }
      if (match[2]) {
        information = match[2];
        information = information.trim();
      }
    }
    const lastNameMatch = workingStr.match(/{([^}]*)}/);
    if (lastNameMatch) {
      lastName = lastNameMatch[1].trim();
      authorStr = [workingStr.substr(0, lastNameMatch.index), workingStr.substr(lastNameMatch.index + lastNameMatch[0].length)].join('');
      firstName = authorStr.replace(',', '').trim();
    } else {
      let vals = workingStr.split(',');
      if (vals.length > 1) {
        firstName = vals[1].trim();
        lastName = vals[0].trim();
      } else if (workingStr.trim().indexOf(' ') > -1) {
        vals = workingStr.trim().split(' ');
        firstName = vals.shift().trim();
        lastName = vals.join(' ').trim();
      } else {
        lastName = workingStr.trim();
        firstName = '';
      }
    }
    const citeKey = (role + '-' + firstName + lastName).toLowerCase().replace(' ', '-');
    return {firstName, lastName, role, information, citeKey};
  });
};

/**
 * Parses a bibTeX-like contextualization statement given as string and returns an object
 * @param {string} inputStr - the bibTeX string to parse
 * @return {Object} contextualization - the contextualization object
 */
export const parseBibContextualization = (inputStr) => {
  const bracketsRE = /([^=^,]+)=(?:{)([^}]*)(?:}),?/g;
  const quoteRE = /([^=^,]+)="([^"]*)",?/g;
  const paramsObject = {};


  const str = inputStr.substr(1, inputStr.length - 2).replace(/&quot;/g, '"');
  let match;
  let key;
  let expression;
  let subObject;
  let newObj;

  while ((match = bracketsRE.exec(str)) !== null) {
    key = match[1].trim();
    expression = match[2].trim().split(',');
    // simple
    if (expression.length === 1) {
      paramsObject[key] = expression[0];
    // nested
    } else {
      subObject = {};

      let isArray;

      expression = expression.map((exp) =>{
        const split = exp.split('=');
        if (split.length === 2) {
          isArray = false;
          return {
            key: split[0],
            value: split[1]
          };
        }
        isArray = true;
        return exp;
      });

      if (!isArray) {
        subObject = expression.reduce((obj, exp)=>{
          obj[exp.key] = exp.value;
          return obj;
        }, subObject);
      }

      newObj = Object.assign({}, subObject);

      if (paramsObject[key] === undefined) {
        paramsObject[key] = newObj;
      } else if (Array.isArray(paramsObject[key])) {
        paramsObject[key].push(newObj);
      } else paramsObject[key] = [paramsObject[key], newObj];
    }

  }

  while ((match = quoteRE.exec(str)) !== null) {
    key = match[1].trim();
    expression = match[2].trim();
    paramsObject[key] = expression;
  }

  return paramsObject;
};


const resolveNested = (subVal) => {
  if (typeof subVal !== 'string') {
    return subVal;
  }
  let expression = subVal.split(',');
  let subObject;
  let newObj;
  // simple
  if (expression.length === 1) {
    return subVal;
  // nested
  }
  subObject = {};
  let isArray;
  expression = expression.map((exp) =>{
    const split = exp.split('=');
    if (split.length === 2) {
      isArray = false;
      return {
        key: split[0],
        value: split[1]
      };
    }
    isArray = true;
    return exp;
  });

  if (!isArray) {
    subObject = expression.reduce((obj, exp)=>{
      obj[exp.key] = exp.value;
      return obj;
    }, subObject);
  }

  newObj = Object.assign({}, subObject);
  return newObj;
};

/**
 * Parses bibTeX nested values inside a bibObject
 * @param {Object} bibObject - the object to parse
 * @return {Object} newObject - the resulting updated object
 */
export const parseBibNestedValues = (bibObject) => {
  const newObject = Object.assign({}, bibObject);
  let subVal;
  for (const index in newObject) {
    if (newObject[index]) {
      subVal = newObject[index];
      if (Array.isArray(subVal)) {
        newObject[index] = bibObject[index].map(resolveNested);
      }else {
        newObject[index] = resolveNested(newObject[index]);
      }
    }
  }
  return newObject;
};

/**
 * This module resolves markdown contents + peritext-specific assertions (notes, contextualizations, contextualizers)
 * It returns a representation of a section's content as an object containing arrays of: DOM children as js representation, notes, contextualizations, contextualizers
 * @module converters/markdownConverter
 */
import marked from 'marked';
import {html2json} from 'html2json';
import {XmlEntities} from 'html-entities';
const entities = new XmlEntities();

import {getMetaValue} from './../../utils/sectionUtils';
import {parseBibContextualization, parseBibNestedValues} from './../bibTexConverter';

// basic marked parser settings
marked.setOptions({
  renderer: new marked.Renderer(),
  gfm: true,
  tables: true,
  breaks: false,
  pedantic: false,
  sanitize: true,
  smartLists: true,
  smartypants: false
});

const eatParamsObject = (str)=> {
  let index = 0;
  let wrappingLevel = 0;
  let paramsObject = '';
  let inObject = false;
  let ch;
  while (index < str.length) {
    ch = str.charAt(index);
    if (ch === '{') {
      wrappingLevel++;
    }else if (ch === '}') {
      wrappingLevel--;
    }

    if (!inObject && wrappingLevel > 0) {
      inObject = true;
      paramsObject += ch;
    } else if (inObject && wrappingLevel === 0) {
      paramsObject += ch;
      return paramsObject;
    } else if (inObject) {
      paramsObject += ch;
      // not in object, character is neither a wrapper nor a whitespace, leave
    } else if (!inObject && ch.match(/([\s])/) === null) {
      return undefined;
    }
    index++;
  }
  return undefined;
};

const parseParamsObject = (paramsObject, impliedResources, contextualizationCount, contextualizers)=> {
  /*
   * analyse contextualizer statement
  */
  let overloading;
  let contextualizerKey;
  // case : explicit call to a contextualizer ==> overload
  if (paramsObject && paramsObject.indexOf('@') === 1) {
    contextualizerKey = paramsObject.match(/^\{(@[^,}]+)/)[1];
    overloading = contextualizerKey;
    let counter = 1;
    let newKey = contextualizerKey + '_' + counter;
    let unique = false;
    // getting a unique citeKey for the overloaded contextualizer
    while (!unique) {
      unique = true;
      contextualizers.forEach((cont) => {
        if (cont.citeKey === newKey) {
          unique = false;
          counter++;
          newKey = contextualizerKey + '_' + counter;
        }
      });
    }
    contextualizerKey = newKey;
  // case : no explicit call to a contextualizer ==> inline implicit contextualization, determine citeKey automatically
  }else {
    contextualizerKey = 'contextualizer_' + contextualizationCount + 1;
  }

  let formattedParams;
  if (paramsObject !== undefined) {
    formattedParams = parseBibContextualization(paramsObject);
    const emptyParams = JSON.stringify(formattedParams).length <= 2;
    // not empty = params present, so inline (implicit new contextualizer, or contextualizer overloading)
    if (!emptyParams) {
      formattedParams.describedInline = true;
    // case : no additionnal params ==> no parameters
    } else {
      formattedParams = {};
    }
  // case : no mention of a contextualizer fully implicit contextualizer (no contextualizer specified)
  } else {
    formattedParams = {};
    formattedParams.describedInline = true;
    formattedParams.fullyImplicit = true;
  }
  // case : a contextualizer has been mentionned, with additionnal contextualization params --> contextualizer overloading
  if (overloading) {
    formattedParams.overloading = overloading;
  }
  formattedParams.citeKey = contextualizerKey;
  return formattedParams;
};

const parseContextualizations = (section)=> {
  let replaced = section.contents;
  const contextualizations = [];
  const newContextualizers = section.contextualizers.slice();
  const statementsRE = /(\!)?\[([^\]]*)\]\(([^\)]+)\)/g;
  let match;
  let type;
  let resources;
  let paramsObject;
  let contextualizationCount = -1;

  while ((match = statementsRE.exec(replaced)) !== null) {
    /*
     * retrieve data from markdown expressions
     */
    // hyperlink markdown syntax stands for inline contextualization
    // image markdown syntax stands for block contextualization
    // ()[] = inline, !()[] = block
    type = match[1] ? 'block' : 'inline';
    // contents
    // children = match[2];
    // quoted resources
    resources = match[3].split(',').map((resKey)=> {
      const key = resKey.substr(1);
      return section.resources.find(res=> {
        return res.citeKey === key;
      });
    }).filter(res=>{
      return res !== undefined;
    });

    contextualizationCount ++;
    // following parameters
    paramsObject = eatParamsObject(replaced.substr(match.index + match[0].length));

    // UPDATE TEXT
    // delete paramsObject from text
    if (paramsObject) {
      replaced = replaced.replace(replaced.substring(match.index + match[0].length, match.index + match[0].length + paramsObject.length), '');
    }
    // update reference in <a> link or <image>
    replaced = replaced.replace(replaced.substring(match.index + match[0].indexOf(match[3]), match.index + match[0].indexOf(match[3]) + match[3].length), 'contextualization_' + contextualizationCount);
    // UPDATE DATA
    const contextualizer = parseParamsObject(paramsObject, resources, contextualizationCount, newContextualizers);
    if (contextualizer) {
      newContextualizers.push(contextualizer);
    }
    contextualizations.push({
      // 'matchIndex': match.index - matchDisplace,
      'citeKey': 'contextualization_' + contextualizationCount,
      contextualizer,
      resources,
      type
    });
  }
  return {md: replaced, contextualizations, contextualizers: newContextualizers};
};


// this module does not use a regex-based method
// because it must catch possible nested content-related "{" brackets symbols
// e.g. : this is an {example inside brackets}
const parseNotes = (md, sectionCiteKey)=> {
  const notes = [];
  let noteNumber = 1;
  let index = 0;
  let displace = 0;
  let beginIndex;
  let nestingLevel = 0;
  let ch;
  let newMd = md;
  let noteContent;
  while (newMd.substr(displace).indexOf('[^]{') > -1) {
    index = displace + newMd.substring(displace).indexOf('[^]{') + 4;
    beginIndex = index;
    nestingLevel = 1;

    while (index < newMd.length && nestingLevel > 0) {
      ch = newMd.charAt(index);
      if (ch === '{') {
        nestingLevel++;
      } else if (ch === '}') {
        nestingLevel--;
      }
      index++;
    }

    noteContent = newMd.substring(beginIndex, index - 1);
    const id = sectionCiteKey + noteNumber;
    const placeholder = `[footnote](note_${id})`;
    const initialLength = index - beginIndex + 4;
    const lengthDif = initialLength - placeholder.length;
    newMd = newMd.replace(newMd.substring(beginIndex - 4, index), placeholder);
    notes.push({
      noteNumber,
      contents: noteContent,
      id
    });
    noteNumber++;
    displace = index - lengthDif;
  }
  return {
    notes,
    newMd
  };
};

let mapMdJsonToPJson = ()=>{return undefined;};
let representContents = ()=>{return undefined;};

mapMdJsonToPJson = (inputElement, contextualizations, blockIndex) =>{
  const element = Object.assign({}, inputElement);
  element.blockIndex = blockIndex;
  if (element.text) {
    element.text = entities.decode(element.text);
  }
  if (element.tag === 'a') {
    if (element.attr.href.indexOf('note_') === 0) {
      element.tag = 'note';
      element.target = element.attr.href.substr(5);
    } else {
      element.tag = 'inlineC';
      const contextualizationCitekey = element.attr.href;
      const contextualization = contextualizations.find(cont =>{
        return cont.citeKey === contextualizationCitekey;
      });
      contextualization.node = element;
    }
  } else if (element.tag === 'img') {
    element.tag = 'blockC';
    const contextualizationCitekey = element.attr.src;
    const contextualization = contextualizations.find(cont =>{
      return cont.citeKey === contextualizationCitekey;
    });
    contextualization.node = element;
    const contents = (element.attr && element.attr.alt) ? element.attr.alt.join(' ') : '';
    element.child = [representContents(contents)[0]];
  }
  if (element.child) {
    element.child = element.child.map((child)=>{
      return mapMdJsonToPJson(child, contextualizations, blockIndex);
    });
  }
  return element;
};

representContents = (mdContent, contextualizations) =>{
  return html2json(marked(mdContent)).child.map((child, blockIndex)=> {
    return mapMdJsonToPJson(child, contextualizations, blockIndex);
  });
};

/**
 * Parses markdown contents in order to update section data with new contents (as a pseudo-DOM nested javascript object representation), notes, contextualizers and contextualizations
 * @param {Object} section - the section to parse
 * @param {Object} parameters - deprecated - rendering parameters (not used at this step)
 * @param {function(error: error, results: {errors: array, section: Object})} callback - the resulting conversion errors and updated section
 */
export const markdownToJsAbstraction = (section, parameters, callback)=> {
  const errors = [];

  const sectionCiteKey = getMetaValue(section.metadata, 'general', 'citeKey');

  section.markdownContents = section.contents;
  section.contextualizers = section.contextualizers.map(parseBibNestedValues);

  const {
    md,
    contextualizers,
    contextualizations
  } = parseContextualizations(section);
  const {
    notes,
    newMd
  } = parseNotes(md, sectionCiteKey);

  section.contextualizations = contextualizations.slice();
  section.contextualizers = contextualizers.slice();
  section.contents = representContents(newMd, section.contextualizations);
  section.notes = notes.map(note =>{
    const contents = representContents(note.contents, section.contextualizations);
    return Object.assign(note, {contents: contents[0].child});
  });

  callback(null, {errors, section});
};

/**
 * This module analyses special markdown insertions (includes and resources)
 * Takes a peritext-markdown string + syntax params as input
 * Parses inline resources expressions
 * Parses ``include`` statements
 * Returns cleaned string, plus a list of include statements and resources to parse
 * @module converters/markdownIncludesParser
 */

const regexEscape = function(str) {
  return str.replace(/[\\^$*+?.()|[\]{}]/g, '\\$&');
};

/**
 * Parses markdown string for markdown files includes statements and inline resources descriptions
 * @param {string} str - the string to parse
 * @param {Object} expressions - the wrapping expressions to use in order to parse includes and inline resources
 * @param {function(error: error, results: {extracted: array, cleanStr: string})} - callback - returns error, results as an array of extracted statements and clean string (without statements)
 */
export const parseMarkdown = (str, {includeWrappingChars, resWrappingChars}, callback) => {
  const includesRegexp = new RegExp(regexEscape(includeWrappingChars[0]) + 'include:([^\\\}]+)' + regexEscape(includeWrappingChars[1]), 'g');
  const resourcesRegexp = new RegExp(regexEscape(resWrappingChars[0]) + '\\\n([\\\w\\\W]+)\\\n' + regexEscape(resWrappingChars[1]), 'g');
  let extracted = [];
  let cleanStr = str;
  let match;
  let extr;

  // recursively check for one of the two substitution expressions
  while (((match = resourcesRegexp.exec(cleanStr)) !== null) || ((match = includesRegexp.exec(cleanStr)) !== null)) {
    // if match, swipe out original statement
    cleanStr = [cleanStr.substr(0, match.index - 1), cleanStr.substr(match.index + match[0].length)].join('');
    // save statement position and type
    extr = {
      index: match.index,
      statement: match[1]
    };
    if (match[0].match(resourcesRegexp)) {
      extr.type = 'resourceStatement';
    }else if (match[0].match(includesRegexp)) {
      extr.type = 'includeStatement';
    }
    // update other statements' position that would be further in string
    extracted = extracted.map((ex) => {
      if (ex.index > extr.index) {
        ex.index -= match[0].length + 1;
      }
      return ex;
    });
    extracted.push(extr);
  }

  // reverse sort (for further string substitution operations)
  extracted = extracted.sort((one, two) =>{
    if (one.index > two.index) {
      return -1;
    }
    return 1;
  });
  return callback(null, {extracted, cleanStr});
};

/**
 * This module cleans resources and metadata from a naive (resource concatenated) tree
 * @module converter/sectionConverter/cleanNaiveTree
 */

import {map as asyncMap} from 'async';

/**
 * Resolves resource and metadata statements from a naive representation of a section
 * @param {Object} params - the cleaning params
 * @param {array} params.errors - the inherited parsing errors to pass along to the next step
 * @param {Object} params.validTree - the tree to clean
 * @param {Object} models - the models to parse the resources with
 * @param {function(error: error, results: {errors: array, validTree: Object})} callback - the possible error, a list of parsing minor errors, and the resulting tree
 */
export const cleanNaiveTree = ({errors = [], validTree}, models, callback) =>{
  const contextualizers = [];
  const naiveTree = Object.assign({}, validTree);
  let metadata;
  const hasResources = naiveTree && naiveTree.resources;
  if (hasResources) {
    naiveTree.resources = naiveTree.resources.filter(function(res) {
      // catch metadata
      let validated;
      // extract contextualizer descriptions
      if (res.bibType === 'contextualizer') {
        contextualizers.push(res);
        return false;
      }
      for (const type in models.sectionTypeModels.acceptedTypes) {
        if (res.bibType === 'peritext' + type) {
          metadata = res;
          return false;
        }
      }

      if (!validated) {
        // verify that the resource type are known
        for (const otherType in models.resourceModels.individual) {
          if (res.bibType === otherType) {
            return true;
          }
        }
      }
      // if not validated, record error and don't take resource
      if (!validated) {
        errors.push({
          type: 'error',
          preciseType: 'invalidResource',
          resourceCiteKey: res.citeKey,
          message: 'could not find resource type ' + res.bibType + ' for Resource ID ' + res.citeKey
        });
        return false;
      }
      return true;
    });
  }
  if (metadata === undefined && naiveTree.name.charAt(0) !== '_') {
    errors.push({
      type: 'warning',
      preciseType: 'metadataNotFound',
      message: 'no metadata specified for the folder ' + naiveTree.name + ' so it was not taken into account'
    });
    const newErrors = (errors.length > 0) ? errors.reverse() : null;
    return callback(null, {errors: newErrors, validTree: undefined});
  }else if (naiveTree.children) {
    return asyncMap(naiveTree.children, function(child, cb) {
      cleanNaiveTree({validTree: child}, models, cb);
    }, (err, results) =>{
      // filter valid children tree leaves
      const children = results
                      .filter((result)=>{
                        return result.validTree !== undefined;
                      })
                      .map((result) =>{
                        return result.validTree;
                      });

      const newErrors = results.reduce((theseErrors, result)=>{
        return theseErrors.concat(result.errors);
      }, errors);
      return callback(null, {errors: newErrors, validTree: Object.assign({}, naiveTree, {metadata}, {children}, {contextualizers})});
    });
  }
  const newErrors = (errors.length > 0) ? errors.reverse() : null;
  return callback(null, {errors: newErrors, validTree: Object.assign({}, naiveTree, {metadata}, {contextualizers})});
};

/**
 * This module concatenates fsTree contents according to include statements and resolves cases in which several markdown files are in the same directory
 * @module converter/sectionConverter/concatTree
 */
import {parseMarkdown as extractIncludes} from './../../converters/markdownIncludesParser';
import {map as asyncMap, waterfall} from 'async';

let resolveFileIncludes;

// Extracts md includes and inline resources descriptions from a file object
const populateElementWithIncludes = (child, params, callback) =>{
  extractIncludes(child.stringContents,
    {
      includeWrappingChars: params.templateWrappingCharacters,
      resWrappingChars: params.inlineResourceDescriptionWrappingCharacters
    }, function(err, {extracted, cleanStr}) {
      if (!err) {
        child.extracted = extracted;
        child.stringContents = cleanStr;
      }
      callback(err, child);
    });
};


// Produces a nested structure of the included files in a given file
const include = (ex, file, mdFilesWithIncludes) =>{
  let fileIndex;
  const fileToInclude = mdFilesWithIncludes
      .find((otherFile, index)=>{
        if (otherFile.name === ex.statement) {
          fileIndex = index;
          return true;
        }
      });
  if (fileToInclude !== undefined) {
    const fileHasIncludes = fileToInclude
                              .extracted
                                .filter((rawExtracted)=>{
                                  return rawExtracted.type === 'includeStatement';
                                }).length > 0;
    if (fileHasIncludes) {
      resolveFileIncludes(fileToInclude, mdFilesWithIncludes);
    }
    file.includes.push(fileToInclude);
    mdFilesWithIncludes.splice(fileIndex, 1);
  }
};

// Monitors the nested including structure population process of a file
const doResolveFilesWithIncludes = (file, mdFilesWithIncludes) =>{
  file.includes = [];
  file.includeStatements = [];
  for (let index = file.extracted.length - 1; index >= 0; index--) {
    const ex = file.extracted[index];
    if (ex.type === 'includeStatement') {
      include(ex, file, mdFilesWithIncludes);
      file.extracted.splice(index, 1);
      file.includeStatements.push(ex);
    }
  }
};

resolveFileIncludes = doResolveFilesWithIncludes;

// Turns non nested file object into include-based nested file objects
const nestIncludes = ({resourcesStr, mdFilesWithIncludes, params}, cb) =>{

  mdFilesWithIncludes.forEach((mdfile) =>{
    resolveFileIncludes(mdfile, mdFilesWithIncludes);
  });
  cb(null, {resourcesStr, mdFilesWithIncludes});
};


// Consumes the nested property 'include' by populating its string contents with includes contents,
// resolved recursively
const buildFinalMdContent = (file) =>{
  let content = file.stringContents;
  const hasIncludes = file.includes && file.includes.length && file.includes.length > 0;
  if (hasIncludes) {
    file.includeStatements
      .filter((ex)=>{
        return ex.type === 'includeStatement';
      })
      .forEach((ex)=>{
        const targetIncluded = file.includes.find((file2)=>{
          return file2.name === ex.statement;
        });
        const contentToInclude = buildFinalMdContent(targetIncluded);
        content = [content.substr(0, ex.index), contentToInclude, content.substr(ex.index)].join('');
      });
  }

  return content;
};

// Turns an array of nested file objects into one single string
const resolveNestedIncludes = ({resourcesStr, mdFilesWithIncludes, params}, cb) =>{
  const contentStr = mdFilesWithIncludes
                      .reduce((str, file) => {
                        return str + buildFinalMdContent(file);
                      }, '');
  cb(null, {contentStr, resourcesStr, params});
};

const concatCustomizers = (newTree) =>{
  return newTree.children
      .filter((child) => {
        return child.type === 'directory' && child.name.charAt(0) === '_';
      })
      .map((child) => {
        const contents = {};
        child.children.forEach((subChild) =>{
          contents[subChild.name] = subChild.stringContents;
        });
        return {
          type: child.name.substr(1),
          contents
        };
      });
};

/**
 * Turns a fsTree into a dumTree, that is to say a tree which presents .bib resources and .md files contents concatenated by folder (according to inner 'include' statements and then automatically)
 * @param {Object} tree - the fsTree to concatenate
 * @param {Object} params - the language-related parameters
 * @param {function(error: error, newTree: object)} callback - error and the concatenated tree
 */
export const concatTree = (tree, params, callback) =>{
  const newTree = Object.assign({}, tree);
  // concat .bib res files
  const resources = newTree.children
                    .filter((child) => {
                      return child.type === 'file' && child.extname === '.bib';
                    })
                    .reduce((str, child) => {
                      if (child.stringContents !== undefined) {
                        return str + '\n' + child.stringContents;
                      }
                      return str;
                    }, '');

  const mdContents = newTree.children
                    .filter((child) => {
                      return child.type === 'file' && child.extname === '.md';
                    });
  const childrenDirs = newTree.children
                    .filter((child) => {
                      return child.type === 'directory' && child.name.charAt(0) !== '_';
                    });
  const childrenCustomizers = concatCustomizers(newTree);
  waterfall([
    // extract md files elements includes statements
    (cb) =>{
      asyncMap(mdContents, (child, cback) =>{
        populateElementWithIncludes(child, params, cback);
      }, (err, mdFilesWithIncludes) =>{
        // concat extracted bibtex resources
        const resourcesStr = mdFilesWithIncludes
                      .reduce((str, mdFile) => {
                        let newStr = str;
                        mdFile.extracted
                          .filter((ex)=>{
                            return ex.type === 'resourceStatement';
                          }).forEach((ex)=>{
                            newStr += ex.statement;
                          });
                        return newStr;
                      }, resources);
        cb(null, {resourcesStr, mdFilesWithIncludes, params});
      });
    },
    nestIncludes,
    resolveNestedIncludes
  ], (err, {resourcesStr, contentStr}) =>{
    newTree.resourcesStr = resourcesStr;
    newTree.contentStr = contentStr;
    if (childrenCustomizers.length > 0) {
      newTree.customizers = childrenCustomizers;
    }
    // recursively repeat dat stuff with children dirs
    asyncMap(childrenDirs, (dir, cback) =>{
      concatTree(dir, params, cback);
    }, (error, populatedDirs) =>{
      newTree.children = populatedDirs;
      callback(error, newTree);
    });
  });
};

/**
 * This module converts an fsTree flatfile abstraction to a documentTree peritext document abstraction
 * @module converters/sectionConverter
 */

import {waterfall, map as asyncMap} from 'async';
import {concatTree} from './concatTree';
import {parseTreeResources} from './parseTreeResources';
import {organizeTree} from './organizeTree';
import {propagateData} from './propagateData';
import {cleanNaiveTree} from './cleanNaiveTree';
import {validateResources} from './../../validators/sectionValidator';
import {getResourceModel, serializePropAgainstType} from './../../utils/modelUtils';
import {getMetaValue} from './../../utils/sectionUtils';
import {resolveSectionAgainstModels} from './../../resolvers/resolveSectionAgainstModels';
import {resolveBindings} from './../../resolvers/resolveContextualizations';
import {markdownToJsAbstraction} from './../markdownConverter';
import {serializeBibTexObject} from './../../converters/bibTexConverter';

const concatSection = ({section, models}, callback) =>{
  const genuineMeta = section.metadata.filter((metadata)=>{
    return !metadata.inheritedVerticallyFrom && !metadata.inheritedHorizontallyFrom;
  });
  const metadata = genuineMeta.reduce((obj, thatMetadata)=>{
    const key = (thatMetadata.domain === 'general') ? thatMetadata.key : thatMetadata.domain + '_' + thatMetadata.key;
    const model = models.metadataModels[thatMetadata.domain][thatMetadata.key];
    if (model) {
      obj[key] = serializePropAgainstType(thatMetadata.value, model.valueType, model);
    } else obj[key] = thatMetadata.value;
    return obj;
  }, {});
  metadata.bibType = 'peritext' + metadata.bibType;
  let root;
  if (section.parent) {
    metadata.parent = section.parent;
  }else {
    root = true;
  }

  if (section.after) {
    metadata.after = section.after;
  }

  const resources = section.resources.filter((resource)=>{
    return !resource.inheritedVerticallyFrom;
  }).map((resource) =>{
    const modelList = getResourceModel(resource.bibType, models.resourceModels);
    if (modelList) {
      let model;
      return Object.keys(resource).reduce((obj, key) =>{
        if (resource[key] !== undefined) {
          model = modelList.properties.find((thatModel)=>{
            return thatModel.key === key;
          });
          if (model) {
            obj[key] = serializePropAgainstType(resource[key], model.valueType, model);
          } else obj[key] = resource[key];
        }

        return obj;
      }, {});
    }
    return resource;
  });


  const contextualizers = section.contextualizers.filter((contextualizer)=>{
    return contextualizer && !contextualizer.describedInline;
  }).map((contextualizer)=>{
    const modelList = getResourceModel(contextualizer.type, models.contextualizerModels);
    if (modelList) {
      let model;
      const cont = Object.keys(contextualizer).reduce((obj, key) =>{
        if (contextualizer[key] !== undefined) {
          model = modelList.properties.find((thatModel)=>{
            return thatModel.key === key;
          });

          if (model) {
            obj[key] = serializePropAgainstType(contextualizer[key], model.valueType, model);
          } else obj[key] = contextualizer[key];
        }
        return obj;
      }, {});
      cont.bibType = 'contextualizer';
      return cont;
    }
    contextualizer.bibType = 'contextualizer';
    return contextualizer;
  });

  const bibResources = [metadata].concat(resources).concat(contextualizers);

  asyncMap(bibResources, serializeBibTexObject, (err, inputBibStr) =>{
    let bibStr;
    if (inputBibStr) {
      bibStr = inputBibStr.join('\n\n');
    }
    callback(err, {
      markdownContent: section.markdownContents,
      bibResources: bibStr,
      customizers: section.customizers,
      citeKey: getMetaValue(section.metadata, 'general', 'citeKey'),
      root
    });
  });
};

const sectionListToFsTree = (inputSectionList, basePath, callback) =>{
  const sectionList = inputSectionList.map((section)=>{
    const folderTitle = section.citeKey;
    const relPath = (section.root) ? '' : '/' + folderTitle;
    const children = [
      {
        type: 'file',
        extname: '.md',
        name: 'contents.md',
        path: relPath + '/contents.md',
        'stringContents': section.markdownContent
      },
      {
        type: 'file',
        extname: '.bib',
        name: 'resources.bib',
        path: relPath + '/resources.bib',
        'stringContents': section.bibResources
      }
      // todo: customizers
    ];
    const folder = {
      type: 'directory',
      name: section.citeKey,
      extname: '',
      path: relPath + '/',
      root: section.root,
      children
    };
    return folder;
  });

  const root = sectionList.find((section)=>{
    return section.root;
  });
  const children = sectionList.filter((section)=>{
    return !section.root;
  });
  delete root.root;
  root.children = root.children.concat(children);
  root.name = basePath.split('/').pop();
  callback(null, root);
};

// from documentSectionsList to fsTree
/**
 * Converts a sections' list to a fsTree representation of the resulting sourcedata
 * @param {Object} param - serializing params
 * @param {array} param.sectionList - the list of sections to serialize
 * @param {Object} param.models - the models to use for serializing
 * @param {string} param.basePath - the path to use as basis for determining serializing output paths
 * @param {function(error:error, fsTree: Object)} callback - provides the filesystem representation of the data
 */
export const serializeSectionList = ({sectionList, models, basePath}, callback) =>{
  waterfall([
    (cb) =>{
      asyncMap(sectionList, (section, callbck) =>{
        concatSection({section, models}, callbck);
      }, (err, concatSections) =>{
        cb(err, concatSections);
      });
    },
    (sections, cb) =>{
      sectionListToFsTree(sections, basePath, cb);
    }
    // all done - return a fsTree
  ], callback);
};

// from fsTree (returned by any connector) to a documentSectionsList usable in app
/**
 * Parses an fsTree representation and renders a list of sections to be used with peritext exporters or as is
 * @param {Object} params - parsing params
 * @param {Object} params.tree - the input fsTree representation
 * @param {Object} params.parameters - language-related parameters
 * @param {Object} params.parent - a possible existing parent section - to use for inheritance phases - suitable for partial document parsing/re-rendering use cases (like with an editor app)
 * @param {Object} params.models - models to use for parsing the data
 * @param {function(error:error, sections: array)} callback - provides an array containing the resources
 */
export const parseSection = ({tree, parameters, parent, models}, callback)=> {
  waterfall([
    // concat markdown, resources, styles, templates, components, and resolve includes, producing a clean 'dumb tree'
    (cb) =>{
      //  console.log(tree);
      concatTree(tree, parameters, cb);
    },
      // parse bibtext to produce resources and metadata props, producing a 'naive tree' of sections
    (dumbTree, cb) =>{
      parseTreeResources(dumbTree, cb);
    },
    // validate and resolve metadata against their models for all sections
    (naiveTree, cb) =>{
      cleanNaiveTree({validTree: naiveTree}, models, cb);
    },
    // format objects, normalize metadata, and resolve organization statements
    ({errors, validTree}, cb) =>{
      organizeTree({errors, validTree}, cb);
    },
    // propagate resources, metadata and customizers vertically (from parents to children sections), metadata lateraly (from metadata models propagation data)
    ({errors, sections}, cb) =>{
      propagateData({errors, sections, models, parent}, cb);
    },
    // validate each resource against their models to produce errors and warnings from parsing
    ({errors, sections}, cb) =>{

      asyncMap(sections, (section, cback) =>{
        validateResources(section, models, cback);
      }, (err, results) =>{
        const newSections = results.map((result)=>{
          return result.section;
        });
        const newErrors = results.reduce((total, result) =>{
          return errors.concat(result.errors);
        }, errors);
        cb(err, {errors: newErrors, sections: newSections});
      });
    },
    // resolve section resources and metadata against their models
    ({errors, sections}, cb) =>{
      asyncMap(sections, (section, cback) =>{
        resolveSectionAgainstModels(section, models, cback);
      }, (err, results) =>{
        const newSections = results.map((result)=>{
          return result.section;
        });
        const newErrors = results.reduce((total, result) =>{
          return errors.concat(result.errors);
        }, errors);
        cb(err, {errors: newErrors, sections: newSections});
      });
    },
    // parse markdown contents and organize them as blocks lists, and parse+resolve contextualization objects
    ({errors, sections}, cb) =>{
      asyncMap(sections, (section, cback) =>{
        markdownToJsAbstraction(section, parameters, cback);
      }, (err, results) =>{
        const newSections = results.map((result)=>{
          return result.section;
        });
        const newErrors = results.reduce((total, result) =>{
          return errors.concat(result.errors);
        }, errors);
        cb(err, {errors: newErrors, sections: newSections});
      });
    },
    // resolve contextualizers statements with their models
    ({errors, sections}, cb) =>{
      asyncMap(sections, (section, cback) =>{
        resolveBindings({section, models}, cback);
      }, (err, results) =>{
        const newSections = results.map((result)=>{
          return result.section;
        });
        const newErrors = results.reduce((total, result) =>{
          return errors.concat(result.errors);
        }, errors);
        cb(err, {errors: newErrors, sections: newSections});
      });
    }
    // all done - return a documentTree to use as data state in the app
  ], callback);
};

/**
 * This module organizes relations between sections (order, inheritance, generality level)
 * @module converter/sectionConverter/organizeTree
 */
import {map as asyncMap, waterfall} from 'async';

import {getMetaValue, deleteMeta} from './../../utils/sectionUtils';

const formatMetadata = (metadataObj) =>{
  const output = [];
  let value;
  let keydetail;
  let domain;
  for (let key in metadataObj) {
    if (metadataObj[key] !== undefined) {
      value = metadataObj[key];
      keydetail = key.split('_');
      domain = (keydetail.length > 1) ? keydetail.shift() : 'general';
      key = keydetail.join('_');
      output.push({
        domain,
        key,
        value
      });
    }
  }
  return output;
};

const flattenSections = (tree, callback) =>{

  if (tree.children) {
    asyncMap(tree.children, flattenSections, (err, children) =>{
      const newTree = Object.assign({}, tree);
      const newChildren = children.map((child)=>{
        return Object.assign({}, child[0], {parent: tree.metadata.citeKey});
      });

      return callback(null, [newTree, ...newChildren]);
    });
  }else return callback(null, tree);
};


const formatSection = (section) =>{
  const metadata = formatMetadata(section.metadata);
  let keyedCustomizers;
  if (section.customizers) {
    keyedCustomizers = {};
    section.customizers.forEach((customizer) => {
      keyedCustomizers[customizer.type] = customizer.contents;
    });
  }
  return {
    metadata,
    contents: section.contentStr,
    resources: section.resources,
    parent: section.parent,
    customizers: keyedCustomizers,
    contextualizers: section.contextualizers
  };
};

const formatSections = (sections, callback) =>{
  const formatted = sections.map(formatSection);
  return callback(null, formatted);
};

const makeRelations = (inputSections, callback) =>{
  // find parents and predecessors
  const sections = inputSections.map((inputSection) =>{
    const section = Object.assign({}, inputSection);
    const parent = getMetaValue(section.metadata, 'general', 'parent');
    const after = getMetaValue(section.metadata, 'general', 'after');
    if (parent) {
      section.parent = parent;
      section.metadata = deleteMeta(section.metadata, 'general', 'parent');
    }
    if (after) {
      section.after = after;
      section.metadata = deleteMeta(section.metadata, 'general', 'after');
    }
    return section;
  });
  // order sections
  for (let index = sections.length - 1; index >= 0; index--) {
    const section = sections[index];
    if (section.after) {
      let indexAfter;
      sections.some((sec, id) =>{
        const citeKey = sec.metadata.find((meta)=>{
          return meta.domain === 'general' && meta.key === 'citeKey';
        }).value;

        if (section.after === citeKey) {
          indexAfter = id;
          return true;
        }
      });
      sections.splice(indexAfter + 1, 0, section);
      sections.splice(index + 1, 1);
    }
  }

  callback(null, sections);
};

/**
 * Organizes relations betwwen sections
 * @param {Object} params - the organization params
 * @param {array} params.errors - the inherited parsing errors to pass along to next step
 * @param {Object} params.validTree - the tree to process
 * @param {function(error: error, results: {errors: array, sections: array})} callback - an updated list of parsing errors and updated sections
 */
export const organizeTree = ({errors, validTree}, callback) => {

  waterfall([
    function(cb) {
      flattenSections(validTree, cb);
    },
    function(sections, cb) {
      formatSections(sections, cb);
    },
    function(sections, cb) {
      makeRelations(sections, cb);
    },
  ], function(err, sections) {
    callback(err, {sections, errors});
  });
};

/**
 * This module parses resources expressions in .bib files representations
 * @module converter/sectionConverter/parseTreeResources
 */
import {parseBibTexStr} from './../../converters/bibTexConverter/';
import {map as asyncMap} from 'async';

/**
 * Converts dumbTree resources representations (as string) to js objects - recursively do the same for tree's children
 * @param {Object} dumbTree - a semi-parsed representation inbetween an fsTree and a more structured representation
 * @param {function(error: error, dumbTree: Object)} callback - the output representation
 */
export const parseTreeResources = (dumbTree, callback) => {
  if (dumbTree.resourcesStr) {
    parseBibTexStr(dumbTree.resourcesStr, function(err, resources) {
      if (dumbTree.children) {
        asyncMap(dumbTree.children, parseTreeResources, function(error, children) {
          callback(error, Object.assign({}, dumbTree, {resources}, {children}));
        });
      }
    });
  }else callback(null, Object.assign({}, dumbTree));
};

/**
 * This module handles metadata propagation within and between sections
 * @module converter/sectionConverter/propagateData
 */
import {getMetaValue, setMetaValue, hasMeta, findByMetadata, metaStringToCouple, hasResource} from './../../utils/sectionUtils';

const inheritMetadataFromParent = (section, sectionTypeModels, sections, parentMetadata) => {
  if (parentMetadata === undefined) {
    return section;
  }
  // inherit metadata props
  // discriminated inedit propert
  const inherited = parentMetadata.filter((pmeta) =>{
    if (hasMeta(section.metadata, pmeta)) {
      return false;
    }
    return true;
  });

  const parentKey = getMetaValue(parentMetadata, 'general', 'citeKey');
  section.metadata = section.metadata.concat(inherited.map((meta) =>{
    return Object.assign({}, meta, {inheritedVerticallyFrom: {domain: 'general', key: 'citeKey', value: parentKey}});
  }));

  // set final bibType
  const bibType = getMetaValue(section.metadata, 'general', 'bibType');
  if (bibType === 'section') {
    const parentBibType = getMetaValue(parentMetadata, 'general', 'bibType');
    const parentModel = sectionTypeModels.acceptedTypes[parentBibType];
    if (parentModel) {
      section.metadata = setMetaValue(section.metadata, 'general', 'bibType', parentModel.childrenType);
    }
  }

  // set hierarchical level (parent + 1 or parent + own level)
  const parentLevel = getMetaValue(parentMetadata, 'general', 'generalityLevel');
  const ownLevel = getMetaValue(section.metadata, 'general', 'generalityLevel');
  if (parentLevel && ownLevel) {
    section.metadata = setMetaValue(section.metadata, 'general', 'generalityLevel', parentLevel + ownLevel);
  }else if (parentLevel) {
    section.metadata.push({
      domain: 'general',
      key: 'generalityLevel',
      value: parentLevel + 1
    });
  }else {
    section.metadata.push({
      domain: 'general',
      key: 'generalityLevel',
      value: 1
    });
  }
  return section;
};


const doInheritMetadataFromParent = (section, sectionTypeModels, sections) => {
  if (section.parent && !section.metadataInherited) {

    section.metadataInherited = true;

    let parent = findByMetadata(sections, 'general', 'citeKey', section.parent);
    // first, make your parent inherit from its parent
    if (!parent.metadataInherited) {
      parent = doInheritMetadataFromParent(section, sectionTypeModels, sections);
    }
    // then inherit yourself from your parent
    return inheritMetadataFromParent(section, sectionTypeModels, sections, parent.metadata);
  }
  // if(getMetaValue(section.metadata, 'general', 'citeKey') === 'mybook'){
  //   console.log(section.metadata);
  // }
  section.metadataInherited = true;
  return section;
};


const inheritResourcesFromParent = (section, sections, parentResources, parentKey) => {
  if (parentResources === undefined) {
    return section;
  }

  // inherit meta props - take anything that you don't already have
  const inherited = parentResources.filter((presource) =>{
    return !hasResource(section.resources, presource);
  });
  section.resources = section.resources.concat(inherited.map((meta) =>{
    return Object.assign({}, meta, {inheritedVerticallyFrom: parentKey});
  }));
  return section;
};


const doInheritResourcesFromParent = (section, sections) => {
  if (section.parent && !section.resourcesInherited) {
    section.resourcesInherited = true;
    let parent = findByMetadata(sections, 'general', 'citeKey', section.parent);
    // first, make your parent inherit from its parent
    if (!parent.resourcesInherited) {
      parent = doInheritResourcesFromParent(section, sections);
    }
    // then inherit yourself from your parent
    return inheritResourcesFromParent(section, sections, parent.resources, getMetaValue(parent.metadata, 'general', 'citeKey'));
  }
  section.resourcesInherited = true;
  return section;
};

const inheritContextualizersFromParent = (section, sections, parentContextualizers, parentKey) => {
  if (parentContextualizers === undefined) {
    return section;
  }
  // inherit context props - take anything that you don't already have
  const inherited = parentContextualizers.filter((presource) =>{
    return !hasResource(section.contextualizers, presource);
  });

  section.contextualizers = section.contextualizers.concat(inherited.map((meta) =>{
    return Object.assign({}, meta, {inheritedVerticallyFrom: parentKey});
  }));
  return section;
};


const doInheritContextualizersFromParent = (section, sections) => {
  if (section.parent && !section.contextualizersInherited) {
    section.contextualizersInherited = true;
    let parent = findByMetadata(sections, 'general', 'citeKey', section.parent);
    // first, make your parent inherit from its parent
    if (!parent.contextualizersInherited) {
      parent = doInheritResourcesFromParent(section, sections);
    }
    // then inherit yourself from your parent
    return inheritContextualizersFromParent(section, sections, parent.contextualizers, getMetaValue(parent.metadata, 'general', 'citeKey'));
  }
  section.contextualizersInherited = true;
  return section;
};

const inheritCustomizersFromParent = (section, sections, parentCustomizers, parentKey) => {
  if (parentCustomizers === undefined) {
    return section;
  }else if (section.customizers === undefined) {
    section.customizers = Object.assign({}, parentCustomizers);
  }else {
    for (const index in section.customizers) {
      if (parentCustomizers[index] !== undefined) {
        // if customizer is a string (e.g. : css data) append child data after parent data
        if (typeof section.customizers[index] === 'string') {
          section.customizers[index] = parentCustomizers[index] + '\n\n\n' + section.customizers[index];
        }else {
          for (const jindex in parentCustomizers[index]) {
            // add customizer from parent (e.g. : template) if not defined in child
            if (section.customizers[index][jindex] === undefined) {
              section.customizers[index][jindex] = parentCustomizers[index][jindex];
            }
          }
        }
      }
    }
  }
  return section;
};

const doInheritCustomizersFromParent = (section, sections) => {
  if (section.parent && !section.customizersInherited) {
    section.customizersInherited = true;
    let parent = findByMetadata(sections, 'general', 'citeKey', section.parent);
    // first, make your parent inherit from its parent
    if (!parent.customizersInherited) {
      parent = doInheritCustomizersFromParent(section, sections);
    }
    // then inherit yourself from your parent
    return inheritCustomizersFromParent(section, sections, parent.customizers, getMetaValue(parent.metadata, 'general', 'citeKey'));
  }
  section.customizersInherited = true;
  return section;
};

const populateLaterally = (section, models) => {
  const toInclude = [];

  section.metadata.forEach((meta) => {
    const model = models[meta.domain][meta.key];
    if (model) {
      const spreaded = model.propagatesTo.map(metaStringToCouple);
      spreaded.forEach((sp) =>{
        const existantProp = hasMeta(section.metadata, sp);
        if (!existantProp) {
          toInclude.push(Object.assign({}, sp, {value: meta.value}, {inheritedHorizontallyFrom: {domain: meta.domain, key: meta.key}}));
        }
      });
    }
  });
  section.metadata = section.metadata.concat(toInclude);
  return section;
};

/**
 * Populate the metadatas of a list of sections, by applying propagation from parents or inbetween metadata values (e.g. : from twitter domain to open graph domain)
 * @param {Object} params - the params of propagation
 * @param {array} params.errors - the list of errors possibly inherited from previous steps
 * @param {array} params.sections - the list of sections to transform
 * @param {Object} params.models - the models to parse the sections with
 * @param {Object} params.parent - if specified, sections that don't have a parent will all be considered as children of this one (but it won't be parsed itself)
 * @param {function(error: error, result: {errors: array, sections: array})} callback - the new transformation errors and updated sections
 */
export const propagateData = ({errors, sections, models, parent}, callback) => {
  let noParents = sections.filter((section) =>{
    return !section.parent;
  });
  if (parent) {
    // inherit metadata from args
    const inheritedMetadata = parent.metadata;
    const inheritedResources = parent.resources;
    const inheritedContextualizations = parent.contextualizers;
    const parentKey = getMetaValue(inheritedMetadata, 'general', 'citeKey');

    noParents = noParents.map((inputSection) =>{
      let section = Object.assign({}, inputSection);
      section.metadataInherited = true;
      section.resourcesInherited = true;
      section.contextualizersInherited = true;
      section = inheritResourcesFromParent(section, sections, inheritedResources, parentKey);
      section = inheritContextualizersFromParent(section, sections, inheritedContextualizations, parentKey);
      section = inheritMetadataFromParent(section, models.sectionTypeModels, sections, inheritedMetadata);
      return section;
    });
    // inherit resources from arguments
  } else {
    noParents.forEach((section) =>{
      // hierarchical level bootstrapping
      section.metadata.push({
        domain: 'general',
        key: 'generalityLevel',
        value: 1
      });
      section.resourcesInherited = true;
      section.metadataInherited = true;
      section.customizersInherited = true;
      section.contextualizersInherited = true;
    });
  }
  let outputSections = [].concat(sections);
  // clean bibType
  outputSections = sections.map((section) =>{
    let newBibType = getMetaValue(section.metadata, 'general', 'bibType');
    newBibType = newBibType ? newBibType.split('peritext') : [];
    newBibType = newBibType.length > 1 ? newBibType[1] : newBibType[0];
    section.metadata = setMetaValue(section.metadata, 'general', 'bibType', newBibType);
    return section;
  });

  // inherit metadata from parents to children
  outputSections = sections.map((section) =>{
    return doInheritMetadataFromParent(section, models.sectionTypeModels, sections);
  });

  // inherit resources from parents to children
  outputSections = sections.map((section) =>{
    return doInheritResourcesFromParent(section, sections);
  });

  // inherit contextualizers from parents to children
  outputSections = sections.map((section) =>{
    return doInheritContextualizersFromParent(section, sections);
  });

  // inherit customizers form parents to children
  outputSections = sections.map((section) =>{
    return doInheritCustomizersFromParent(section, sections);
  });

  // inherit metadata laterally, from one property to another
  outputSections = sections.map((section) =>{
    return populateLaterally(section, models.metadataModels);
  });

  // cleaning control properties
  outputSections.forEach((section) =>{
    delete section.metadataInherited;
    delete section.customizersInherited;
    delete section.resourcesInherited;
    delete section.contextualizersInherited;
  });

  callback(null, {errors, sections: outputSections});
};

