/**
 * Resolver dedicated to resolve metadata templates against their data
 * @module resolvers/htmlMetaTemplateSerializer
 */

// templates have two variables : ${key} and ${value}
// if value is an array
// modificators are specifed after commas
// modificators : join(), personsToString

/**
 * Consumes a metadata object against an html metadata node template
 * @param {Object} metadata - the metadata object
 * @param {string} template - the template to consumes
 * @return {string} htmlMeta - the representation of the html metadata as string
 */
export const serializeHtmlMeta = (metadata, template) => {
  let transformationAction = template.match(/\${value:([^}]*)/);
  let transformationArgument;
  let value;
  if (transformationAction) {
    transformationAction = transformationAction[1];
    if (transformationAction.indexOf('join(') === 0) {
      try {
        transformationArgument = transformationAction.match(/join\(([^\)]*)\)/)[1];
      }catch (exception) {
        transformationArgument = ', ';
      }
    }
  }

  if (transformationAction === 'join' && Array.isArray(metadata.value)) {
    value = metadata.value.join(transformationArgument);
  }else if (transformationAction === 'personsToString' && Array.isArray(metadata.value)) {
    const persons = metadata.value.map((person)=>{
      return (person.firstName) ? person.firstName + ' ' + person.lastName : person.lastName;
    });
    value = persons.join(', ');
  }else value = metadata.value;
  let output = template;
  while (output.indexOf('${value') > -1) {
    output = output.replace('${key}', metadata.key).replace(/\${value:?([^}]*)?}/, value);
  }
  return output;
};

/**
 * Resolver dedicated to resolve  contextualization statements
 * @module resolvers/resolveContextualizations
 */

import {getMetaValue} from './../../utils/sectionUtils';
import {getResourceModel, getContextualizerModel, resolvePropAgainstType} from './../../utils/modelUtils';
import * as contextualizers from './../../../contextualizers/';

/**
 * Transforms 1, 2, 3 ordinally used number into a, b, c ordinally used letters
 * @param {number} num - the number to transform
 * @return {string} letter - the output letter
 */
export const numbersToLetters = (num) =>{
  const mod = num % 26;
  let pow = num / 26 | 0;
  const out = mod ? String.fromCharCode(64 + mod) : (--pow, 'Z');
  return pow ? numbersToLetters(pow) + out : out.toLowerCase();
};

/**
 * Resolves a contextualizer object against its model and context, and records errors
 * @param {object} contextualizer - the contextualizer to resolve
 * @param {object} contextualization - the contextualization to use as a clue-giver if the contextualizer is implicit
 * @param {object} section - the section to which the contextualizer belongs
 * @param {object} models - the models to parse the contextualizer against
 * @return {{err: error, contextualizer: Object}} result - parsing errors and final contextualizer
 */
const resolveContextualizer = (contextualizer, contextualization, section, models) =>{
  const err = [];
  let newContextualizer = Object.assign({}, contextualizer);
  // if overloading, first fetch the existing contextualizer
  if (newContextualizer.overloading) {
    const overload = newContextualizer.overloading.replace(/^@/, '');
    // find original
    const original = section.contextualizers.find((cont) =>{
      return cont.citeKey === overload;
    });
    // resolve original first
    if (original) {
      const originalFormatted = resolveContextualizer(original, contextualization, section, models).finalContextualizer;
      newContextualizer = Object.assign(originalFormatted, newContextualizer);
    } else {
      // no original found ==> overloading reference error
      err.push({
        type: 'error',
        preciseType: 'invalidContextualizer',
        sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
        message: 'overloading reference error: contextualizer ' + newContextualizer.citeKey + ' should overload ' + overload + ' but the original contextualizer does not exist'
      });
    }
  }
  // guess contextualizer type if needed
  if (!newContextualizer.type) {
    if (contextualization.resources.length > 0) {
      const source = contextualization.resources[0];
      if (source === undefined) {
        err.push({
          type: 'error',
          preciseType: 'invalidContextualizer',
          sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
          message: 'contextualizer ' + newContextualizer.citeKey + ' (' + newContextualizer.type + ') does not provide a valid resource'
        });
        return {err, undefined};
      }
      const sourceModel = getResourceModel(source.bibType, models.resourceModels);
      newContextualizer.type = sourceModel.defaultContextualizer;
    }
  }
  // resolve contextualizer object against its model
  const contextualizerModel = getContextualizerModel(newContextualizer.type, models.contextualizerModels);
  const finalContextualizer = contextualizerModel.properties.reduce((obj, thatModel) =>{
    obj[thatModel.key] = resolvePropAgainstType(newContextualizer[thatModel.key], thatModel.valueType, thatModel);
    // record error if required field is undefined
    if (obj[thatModel.key] === undefined && thatModel.required === true) {
      err.push({
        type: 'error',
        preciseType: 'invalidContextualizer',
        sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
        message: 'contextualizer ' + newContextualizer.citeKey + ' (' + newContextualizer.type + ') does not provide required type ' + thatModel.key
      });
    }
    return obj;
  }, {});
  return {err, finalContextualizer};
};

/**
 * Resolves contextualizations' contextualizer and resource, verifying that contextualization will be possible
 * @param {object} contextualizer - the contextualizer to resolve
 * @param {object} section - the section to resolve
 * @param {object} models - the models to use to validate data
 * @param {function(err: error, results: {errors: array, newSection: Object})} callback - callbacks updated section and possible errors as an array
 */
export const resolveBindings = ({section, models}, cb) =>{
  let errors = [];
  // find implicit contextualizers types
  section.contextualizations = section.contextualizations.map(contextualization =>{
    // populate contextualizers against their models
    const {err, finalContextualizer} = resolveContextualizer(contextualization.contextualizer, contextualization, section, models);
    if (err.length) {
      errors = errors.concat(err);
    } else {
      contextualization.contextualizer = finalContextualizer;
    }
    return contextualization;
  // verify that all required resources exist
  }).filter((contextualization) =>{
    let ok = true;
    if (contextualization.contextualizer === undefined) {
      errors.push({
        type: 'error',
        preciseType: 'invalidContextualization',
        sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
        message: 'contextualizer was not found for contextualization ' + contextualization
      });
      return cb(null, {errors, section});
    }
    const acceptedResourceTypes = getContextualizerModel(contextualization.contextualizer.type, models.contextualizerModels).acceptedResourceTypes;
    // resources compatibility and existence check
    contextualization.resources.some((res) =>{
      // resource exists, check if it is accepted for the contextualizerType
      if (res !== undefined) {
        let accepted = false;
        acceptedResourceTypes.some((type) => {
          if (type === '*' || type === res.bibType) {
            accepted = true;
            return true;
          }
        });
        if (!accepted) {
          ok = false;
          errors.push({
            type: 'error',
            preciseType: 'invalidContextualization',
            sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
            message: 'resource ' + res + ' was asked in a contextualization but is not handled by the contextualizer ' + contextualization.contextualizer
          });
        }
      } else {
        ok = false;
        errors.push({
          type: 'error',
          preciseType: 'invalidContextualization',
          sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
          message: 'resource ' + res + ' was asked in a contextualization but was not found'
        });
      }
      return ok;
    });
    return ok;
  });
  cb(null, {errors, section});
};

/**
 * Resolves relations of recurrence, order, and similarity between contextualizations in a section
 * @param {array} sections - the sections array to resolve
 * @param {object} settings - the rendering settings to apply
 * @return {array} newSections - the udpated sections
 */
export const resolveContextualizationsRelations = (sections, settings) =>{
  let opCitIndex;
  let sameResPrint;
  return sections.reduce((inputSections, sectio, index)=> {
    sectio.contextualizations = sectio.contextualizations.reduce((conts, contextualization, contIndex) => {
      contextualization.resPrint = contextualization.resources.map(res =>{
        return res.citeKey;
      }).join('-');
      // opcit section
      sameResPrint = conts.find((cont2, cont2Index)=> {
        if (cont2.resPrint === contextualization.resPrint) {
          opCitIndex = cont2Index;
          contextualization.precursorCiteKey = cont2.citeKey;
          return true;
        }
      });

      if (sameResPrint !== undefined) {
        contextualization.sectionOpCit = true;
      }
      // todo opcit document

      // ibid section
      if (opCitIndex) {
        const substrate = conts.slice(opCitIndex, contIndex).filter(oCont=> {
          return oCont.contextualizer.type === contextualization.contextualizer.type;
        });
        if (substrate.length === 2) {
          contextualization.sectionIbid = true;
        }
      }

      // todo ibid document

      if (contextualization.contextualizer.type === 'citation') {
        // same authors but different work in year - section scale
        contextualization.authorsPrint = contextualization.resources[0].author.reduce((str, author)=> {
          return str + author.lastName + '-' + author.firstName;
        }, '');

        conts.find((cont2)=> {
          if (cont2.authorsPrint === contextualization.authorsPrint && cont2.resPrint !== contextualization.resPrint && (cont2.year === contextualization.year || cont2.date === contextualization.date)) {
            cont2.sameAuthorInYear = cont2.sameAuthorInYear !== undefined ? cont2.sameAuthorInYear ++ : 1;
            contextualization.sameAuthorInYear = cont2.sameAuthorInYear + 1;
            contextualization.yearSuffix = numbersToLetters(contextualization.sameAuthorInYear);
            cont2.yearSuffix = numbersToLetters(cont2.sameAuthorInYear);
            return true;
          }
        });
        // todo same authors in year but different work - at document scale
      }

      opCitIndex = undefined;
      return conts.concat(contextualization);
    }, []);

    return inputSections.concat(sectio);
  }, []);
};

/**
 * Resolves all the contextualizations of a section by updating its contents pseudo-DOM representation
 * @param {object} section - the section to resolve
 * @param {string} renderingMode - whether rendering is static or dynamic
 * @param {object} settings - the rendering settings to take into account when resolving contextualizations
 * @return {Object} newSection - the updated section
 */
export const resolveContextualizationsImplementation = (section, renderingMode, settings) =>{
  let contextualizer;
  const sectio = section.contextualizations.reduce((inputSection, contextualization) => {
    // rebind resources
    contextualization.resources = contextualization.resources.map(res1 =>{
      return inputSection.resources.find(res2 =>{
        return res1.citeKey === res2.citeKey;
      });
    });
    contextualizer = contextualizers[contextualization.contextualizer.type];
    if (contextualizer === undefined) {
      console.log('no contextualizer function was found for type ', contextualization.contextualizer.type);
      return Object.assign({}, inputSection);
    }
    switch (renderingMode) {
    case 'static':
      switch (contextualization.type) {
      case 'inline':
        return contextualizer.contextualizeInlineStatic(inputSection, contextualization, settings);
      case 'block':
        return contextualizer.contextualizeBlockStatic(inputSection, contextualization, settings);
      default:
        break;
      }
      break;
    case 'dynamic':
      switch (contextualization.type) {
      case 'inline':
        return contextualizer.contextualizeInlineDynamic(inputSection, contextualization, settings);
      case 'block':
        return contextualizer.contextualizeBlockDynamic(inputSection, contextualization, settings);
      default:
        break;
      }
      break;
    default:
      break;
    }
    return Object.assign({}, inputSection);
  }, Object.assign({}, section));

  return sectio;
};

/**
 * Resolver dedicated to resolve @assets statements and @res statements
 * @module resolvers/resolveDataDependencies
 */
import {map as asyncMap, mapSeries as asyncMapSeries, waterfall} from 'async';
const CsvConverter = require('csvtojson').Converter;
const csvConverter = new CsvConverter({});

/**
 * Resolves interpolations ni metadata, resources, and contextualizations, and possibly fetches stores necessary data for rendering
 * @param {array} inputSections - the sections to resolve
 * @param {Object} assetsController - the assets controller module to use to communicate with assets
 * @param {Object} assetsParams - the params to use for accessing the assets
 * @param {boolean} resolveData - whether to fetch and store necessary data right away or to store methods for later lazy-loading
 * @param {function(err: error, finalSections: array)} callback - callbacks a possible errors and the updated list of sections
 */
export default function resolveDataDependencies(inputSections, assetsController, assetsParams, resolveData, callback) {
  let res;
  let match;
  const data = {};// this object stores resolved and unresolved promises about resources data
  const resRe = /@res([\d]+)?.(.*)/g;
  const assetsRe = /@assets\/([^']+)/g;
  asyncMap(inputSections, (section, allSectionsCallback)=> {
    waterfall([
      // styles resolution
      (stylesResolutionCallback)=> {
        if (section.customizers && section.customizers.styles) {
          const styles = [];
          for (const prop in section.customizers.styles) {
            if (section.customizers.styles[prop]) {
              styles.push({key: prop, value: section.customizers.styles[prop]});
            }
          }
          // map each style customizer
          asyncMap(styles, (styleCouple, singleStyleCallback)=> {
            let style = styleCouple.value;
            // get all expressions
            const matches = [];
            do {
              match = assetsRe.exec(style);
              if (match) {
                matches.push(match);
              }
            } while (match);
            // some assets interpolations to do
            if (matches.length) {
              // reverse array to begin with last matches and not mess around with indexes
              matches.reverse();
              asyncMap(matches, (thisMatch, matchCallback)=> {
                assetsController.getAssetUri(thisMatch[1], assetsParams, (err4, uri)=> {
                  style = style.substr(0, thisMatch.index) + uri + style.substr(thisMatch.index + thisMatch[0].length);
                  matchCallback(err4, thisMatch);
                });
              }, (matchErrors, theseMatches)=> {
                styleCouple.value = style;
                singleStyleCallback(matchErrors, styleCouple);
              });
            // no interpolations to do
            } else {
              styleCouple.value = style;
              singleStyleCallback(null, styleCouple);
            }
          }, (stylesErrors, styleCouples)=> {
            styleCouples.forEach((styleCouple) =>{
              section.customizers.styles[styleCouple.key] = styleCouple.value;
            });
            stylesResolutionCallback(stylesErrors, section);
          });
        } else stylesResolutionCallback(null, section);
      },
      // resolve metadata
      (sectio, metadataCallback)=> {
        asyncMap(sectio.metadata, (metadata, singleMetadataCallback)=> {
          if (typeof metadata.value === 'string' && metadata.value.indexOf('@assets/') === 0) {
            assetsController.getAssetUri(metadata.value.split('@assets/')[1], assetsParams, (err4, uri)=> {
              metadata.value = uri;
              singleMetadataCallback(err4, metadata);
            });
          } else {
            singleMetadataCallback(null, metadata);
          }
        }, (metadataErrors, newMetadata)=>{
          sectio.metadata = newMetadata;
          metadataCallback(metadataErrors, sectio);
        });
      },
      // resolve resources
      (sectio, resourcesCallback)=> {
        asyncMap(sectio.resources, (resource, singleResourceCallback)=> {
          const props = [];
          // format props as array for performing an async map
          for (const prop in resource) {
            if (resource[prop]) {
              props.push({key: prop, value: resource[prop]});
            }
          }
          asyncMap(props, (prop, resourcePropCallback)=> {
            if (typeof prop.value === 'string' && prop.value.indexOf('@assets/') === 0) {
              assetsController.getAssetUri(prop.value.split('@assets/')[1], assetsParams, (err4, uri)=> {
                prop.value = uri;
                resourcePropCallback(err4, prop);
              });
            } else {
              resourcePropCallback(null, prop);
            }
          }, (singleResourceError, newProps)=> {
            const newResource = newProps.reduce((obj, prop) =>{
              obj[prop.key] = prop.value;
              return obj;
            }, {});
            singleResourceCallback(singleResourceError, newResource);
          });
        }, (resourcesErrors, resources)=> {
          sectio.resources = resources;
          resourcesCallback(resourcesErrors, sectio);
        });
      },
      (sectio, contextualizationsCallback)=> {
        asyncMap(sectio.contextualizations, (contextualization, contextualizationCallback)=> {
          res = undefined;
          const props = [];
          // format props as array for performing an async map
          for (const prop in contextualization.contextualizer) {
            if (contextualization.contextualizer[prop]) {
              props.push({key: prop, value: contextualization.contextualizer[prop]});
            }
          }
          /**
           * First level props
           */
          // resolve async. props
          asyncMap(props, (prop, contextualizationPropCallback)=> {
            const val = prop.value;
            // if prop is itself an array, need for another nested async resolution
            if (Array.isArray(val) && prop.key !== 'resources') {
              // loop through propArray members
              asyncMap(val, (prop2, propArrayMembersCallback)=> {
                // prepare for asyncMap for propArray member
                const nestedProps = [];
                for (const nestedProp in prop2) {
                  if (prop2[nestedProp]) {
                    nestedProps.push({key: nestedProp, value: prop2[nestedProp]});
                  }
                }
                // Second level props (contextualization.arrayProp[array member].prop)
                asyncMap(nestedProps, (nestedProp, nestedPropCallback)=> {
                  // resolve nested props here
                  const val3 = nestedProp.value;
                  if (('' + val3).indexOf('@res') === 0) {
                    while ((match = resRe.exec(val3)) !== null) {
                      // Identify which resource is targetted (handling multi-resource contextualizations)
                      const rank = match[1] ? (+match[1] - 1) : 0;
                      // find resource data
                      res = contextualization.resources[rank];
                      if (res === undefined) {
                        console.log('res is undefined, expression: ', val3);
                        return nestedPropCallback(undefined, nestedProp);
                      }
                      // case: metadata call
                      const resProp = match[match.length - 1];
                      if (resProp.indexOf('data') !== 0) {
                        // interpolate w/ resource value thanks to resourceProp path
                        nestedProp.value = res[resProp];
                        // contextualizationPropCallback(null, prop);
                      // case : data call
                      } else {
                        const dataPath = ('' + val3).split('.').filter(path=>{
                          return path.length;
                        });
                        nestedProp.value = {
                          type: 'path',
                          target: 'data',
                          path: dataPath
                        };
                        const defined = data[res.citeKey];
                        if (!defined) {
                          const accessor = res.data || res.url;
                          if (('' + accessor).indexOf('@assets/') === 0) {
                            const toResolve = {};
                            toResolve.params = {
                              path: accessor.split('@assets/')[1],
                              params: assetsParams,
                              acceptedExtensions: '*'
                            };
                            toResolve.read = assetsController.getReader(assetsParams);
                            data[res.citeKey] = toResolve;
                          } else {
                            console.log('unhandled data accessor : ', accessor);
                          }
                        }
                      }
                    }
                    nestedPropCallback(null, nestedProp);
                  } else if (('' + val3).indexOf('@assets/') === 0) {
                    assetsController.getAssetUri(val3.split('@assets/')[1], assetsParams, (err4, uri)=> {
                      nestedProp.value = uri;
                      nestedPropCallback(err4, nestedProp);
                    });
                  } else {
                    nestedPropCallback(null, nestedProp);
                  }
                }, (err3, propsOut2)=> {
                  const newProp2 = propsOut2.reduce((newCont, newContProp) =>{
                    newCont[newContProp.key] = newContProp.value;
                    return newCont;
                  }, {});
                  propArrayMembersCallback(err3, newProp2);
                });
              // resolve arrayed prop
              }, (err2, arrayMembers)=> {
                prop.value = arrayMembers;
                contextualizationPropCallback(null, prop);
              });
            } else if (typeof val !== undefined && prop.key !== 'resources') {
              if (('' + val).indexOf('@res') === 0) {
                while ((match = resRe.exec(val)) !== null) {
                  // Identify which resource is targetted (handling multi-resource contextualizations)
                  const rank = match[1] ? (+match[1] - 1) : 0;
                  // find resource data
                  res = contextualization.resources[rank];
                  if (res === undefined) {
                    console.log('res is undefined, expression: ', val);
                    return contextualizationPropCallback(undefined, prop);
                  }
                  const resProp = match[match.length - 1];
                  // case: metadata call
                  if (resProp.indexOf('data') !== 0) {
                    // interpolate w/ resource value thanks to resourceProp path
                    prop.value = res[resProp];
                    // contextualizationPropCallback(null, prop);
                  // case : data call
                  } else {
                    const dataPath = ('' + val).split('.').filter(path=>{
                      return path.length;
                    });
                    prop.value = {
                      type: 'path',
                      target: 'data',
                      path: dataPath
                    };
                    const defined = data[res.citeKey];
                    if (!defined) {
                      const accessor = res.data || res.url;
                      if (('' + accessor).indexOf('@assets/') === 0) {
                        const toResolve = {};
                        toResolve.params = {
                          path: accessor.split('@assets/')[1],
                          params: assetsParams,
                          acceptedExtensions: '*'
                        };
                        toResolve.read = assetsController.getReader(assetsParams);
                        data[res.citeKey] = toResolve;
                      } else {
                        console.log('unhandled data accessor : ', accessor);
                      }
                    }
                  }
                }
                contextualizationPropCallback(null, prop);
              } else if (('' + val).indexOf('@assets/') === 0) {
                assetsController.getAssetUri(val.split('@assets/')[1], assetsParams, (err3, uri)=> {
                  prop.value = uri;
                  contextualizationPropCallback(err3, prop);
                });
              } else {
                contextualizationPropCallback(null, prop);
              }
            } else {
              contextualizationPropCallback(null, prop);
            }
          }, (err1, propsOut1)=> {
            const newContextualizer = propsOut1.reduce((newCont, propOut) =>{
              newCont[propOut.key] = propOut.value;
              return newCont;
            }, {});

            // console.log('new contextualization', Object.assign(contextualization, newContextualizer));
            // pass contextualizer's resolved values to contextualization object
            newContextualizer.contextualizerType = newContextualizer.type;
            delete newContextualizer.type;// contextualization has a type also
            const newContextualization = Object.assign(contextualization, newContextualizer);
            contextualizationCallback(err1, newContextualization);
          });
        }, (contextualizationsError, contextualizations)=> {
          sectio.contextualizations = contextualizations;
          contextualizationsCallback(contextualizationsError, sectio);
        });
      }
    // waterfall callback for all sections
    ], (errs, sectios)=> {
      allSectionsCallback(errs, sectios);
    });
  // global & final callback
  }, (err, sections)=> {
    if (resolveDataDependencies) {
      asyncMapSeries(Object.keys(data), (key, dataCallback) =>{
        const toResolve = data[key];
        toResolve.read(toResolve.params, (dataErr, dataResult) =>{
          if (dataErr) {
            data[key] = dataErr;
            dataCallback(null, key);
          } else {
            const raw = dataResult && dataResult.stringContents;
            const ext = dataResult.extname;
            // todo : handle other file formats
            if (raw && ext === '.csv') {
              csvConverter.fromString(raw, (parseErr, json)=>{
                data[key] = {
                  format: 'json',
                  data: json
                };
                dataCallback(parseErr, key);
              });
            }else {
              console.log('unhandled file format ', ext);
              dataCallback(null, key);
            }
          }
        });
      }, (finalErr, keys) =>{
        const finalSections = sections.map(sectio =>{
          return Object.assign(sectio, {data: data});
        });
        callback(finalErr, finalSections);
      });
    } else {
      const finalSections = sections.map(sectio =>{
        return Object.assign(sectio, {data});
      });
      callback(err, finalSections);
    }
  });
}

/**
 * Resolver dedicated to resolve sections against metadata models
 * @module resolvers/resolveSectionAgainstModels
 */
import {getResourceModel, resolvePropAgainstType} from './../../utils/modelUtils';
import {getMetaValue} from './../../utils/sectionUtils';
import {serializeHtmlMeta} from './../../resolvers/htmlMetaTemplateSerializer';

/**
 * Consumes and normalize the metadata of a section
 * @param {Object} section - the section to resolve
 * @param {Object} models - the models to use for resolution
 * @param {function(err:error, result: {errors: array, section: Object})} callback - the resulting resolution errors and updated section
 */
export const resolveSectionAgainstModels = (section, models, callback) => {
  const errors = [];
  // validate resources
  section.resources = section.resources.map((resource) =>{
    const model = getResourceModel(resource.bibType, models.resourceModels);
    if (model) {
      // populate resource model with input resource data
      return model.properties.reduce((resolvedResource, propModel) => {
        const key = propModel.key;
        if (propModel.required && !resource[key]) {
          errors.push({
            type: 'error',
            preciseType: 'invalidResource',
            sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
            message: 'property ' + key + ' is required in resource ' + resource.citeKey + '(bibType: ' + resource.bibType + ') and not present'
          });
        }else if (resource[key]) {
          resolvedResource[key] = resolvePropAgainstType(resource[key], propModel.valueType, propModel);
        } else if (propModel.default) {
          resource[key] = propModel.default;
        }
        return resolvedResource;
      }, {});
    }
    errors.push({
      type: 'error',
      preciseType: 'invalidResource',
      sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
      message: 'Could not find suitable data model for resource ' + resource.citeKey
    });
    return {};
  });

  // validate metadata
  section.metadata = section.metadata.map((metadata) => {
    // resolve unicity
    const model = models.metadataModels[metadata.domain][metadata.key];
    if (model) {
      const uniquePb = model.unique && Array.isArray(metadata.value) && metadata.value.length > 1;
      if (uniquePb) {
        errors.push({
          type: 'error',
          preciseType: 'invalidMetadata',
          sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
          message: metadata.key + ' value was set more than once for section ' + getMetaValue(section.metadata, 'general', 'title')
        });
        metadata.value = metadata.value[0];
      }

      if (Array.isArray(metadata.value)) {
        metadata.value = metadata.value.map((val)=> {
          if (typeof metadata.value === 'string') {
            return resolvePropAgainstType(val, model.valueType, model);
          }
          return val;
        });

      }else if (typeof metadata.value === 'string') {
        metadata.value = resolvePropAgainstType(metadata.value, model.valueType, model);
      }

      if (model.headTemplate) {
        metadata.htmlHead = serializeHtmlMeta(metadata, model.headTemplate);
      }

    }else {
      errors.push({
        type: 'warning',
        preciseType: 'invalidMetadata',
        sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
        message: metadata.domain + ' metadata property ' + metadata.key + ' is invalid in section ' + getMetaValue(section.metadata, 'general', 'title') + ' and therefore was not taken into account'
      });
    }

    return metadata;
  });

  // defaults
  for (const key in models.metadataModels.general) {
    if (models.metadataModels.general[key].default) {
      const present = getMetaValue(section.metadata, 'general', key);
      if (!present) {
        section.metadata.push({
          domain: 'general',
          key,
          value: models.metadataModels.general[key].default
        });
      }
    }
  }
  return callback(null, {errors, section});
};

