/**
 * Utils - dedicated to consuming peritext contents' pseudo-dom representations by returning React markup
 * @module utils/modelUtils
 */
import React from 'react';

/**
 * Consumes a peritext content's pseudo-dom node (described as a js object) and returns a react component
 * @param {Object} node - the pseudo-dom node
 * @param {number} index - the index of the node (used for providing react keys)
 * @return {ReactElement} markup describing the node
 */
export const jsToComponent = (node, index)=> {
  if (node.node === 'text') {
    return node.text;
  }
  if (node.special) {
    // Component class stored as referenced object
    const Component = node.tag;
    return <Component key={index} {...node.props} />;
  }
  // plain string tag name
  const Tag = node.tag;
  return (<Tag key={index} id={node.attr && node.attr.id} href={node.attr && node.attr.href}>
    {node.child && node.child.map(jsToComponent)}
  </Tag>);
};

/**
 * Consumes a peritext content's pseudo-dom nodes list to produce react markup
 * @param {Object} contents - the pseudo-dom nodes array
 * @return {ReactElement|string} markup or string contents
 */
export default function renderContents(contents) {
  if (Array.isArray(contents)) {
    return contents.map(jsToComponent);
  } else if (typeof contents === 'string') {
    return contents;
  }
  return '';
}

/**
 * Utils - Collection of utils for formatting scholarly citations in html+schema+RDFa+COiNS elements
 * @module utils/microDataUtils
 */

/**
 * Translates a peritext bibType to a schema.org Type
 * @param {string} bib - the peritext bibType
 * @return {string} SchemaType - the corresponding SchemaType
 */
export const bibToSchema = (bib) => {
  switch (bib) {
  case 'book':
    return 'Book';
  case 'article':
    return 'ScholarlyArticle';
  case 'booklet':
    return 'CreativeWork';
  case 'conference':
    return 'Chapter';
  case 'incollection':
    return 'Chapter';
  case 'inherits':
    return 'Chapter';
  case 'inbook':
    return 'Chapter';
  case 'mastersthesis':
    return 'Thesis';
  case 'phdthesis':
    return 'Thesis';
  case 'proceedings':
    return 'Book';
  case 'image':
    return 'ImageObject';
  case 'online':
    return 'WebSite';
  case 'vectorsImage':
    return 'ImageObject';
  case 'tabularData':
    return 'Dataset';
  case 'video':
    return 'VideoObject';
  case 'audio':
    return 'AudioObject';
  case 'person':
    return 'Person';
  case 'place':
    return 'Place';
  case 'organization':
    return 'Organization';
  // not very accurate
  case 'artefact':
    return 'Product';
  // no schematype for these ones (too abstract)
  case 'topic':
  case 'concept':
  case 'technology':
    return 'Thing';
  default:
    return 'CreativeWork';
  }
};

/*
  Context Objects in Spans (COiNS) related functions
*/

const addPropToCOinSData = (key, value) => {
  return {
    key: key,
    value: value
  };
};

const urify = (key, value) => {
  return key + '=' + encodeURIComponent(value);
};

const assembleUri = (infos) => {
  const vals = [];
  infos.forEach(function(info) {
    vals.push(urify(info.key, info.value));
  });
  return vals.join('&amp;');
};

const baseMap = {
  'rft.date': 'date',
  'rft.pages': 'pages',
  'rft.issn': 'ISSN',
  'rft.isbn': 'ISBN',
  'rft_id': 'url',
  'rft.place': 'address',
  'rft.pub': 'publisher'
};

const journalMap = {
  'rft.atitle': 'title',
  'rft.jtitle': 'journal',
  'rft.volume': 'volume',
  'rft.issue': 'issue'
};

const chapterMap = {
  'rft.atitle': 'title',
  'rft.btitle': 'booktitle'
};

const translate = (data, item, map) => {
  for (const key in map) {
    if (item[map[key]]) {
      data.push(addPropToCOinSData(key, item[map[key]]));
    }
  }
  return data;
};

/**
 * Generates an openUrl URI describing the resource, and aimed at being used in a ContextObjectInSpan (COinS)
 * @param {Object} resource - the resource to describe with an openUrl
 * @return {string} uri - the uri describing the resource
 */
export const generateOpenUrl = (resource) => {
  let data = [];
  data.push(addPropToCOinSData('ctx_ver', 'Z39.88-2004'));

  if (resource.author && resource.author.length) {
    resource.author.forEach(function(author) {
      data.push(addPropToCOinSData('rft.au', author.lastName + ', ' + author.firstName));
    });
  }
  data = translate(data, resource, baseMap);
  if (resource.bibType === 'journal' || resource.bibType === 'article') {
    data = translate(data, resource, journalMap);
    data.push(addPropToCOinSData('rft.genre', 'article'));
    data.push(addPropToCOinSData('rft_val_fmt', 'info:ofi/fmt:kev:mtx:journal'));
  }else if (resource.bibType === 'proceedings' || resource.bibType === 'conferencePaper') {
    data = translate(data, resource, journalMap);
    data.push(addPropToCOinSData('rft.genre', 'conference'));
    data.push(addPropToCOinSData('rft_val_fmt', 'info:ofi/fmt:kev:mtx:book'));
  }else if (resource.bibType === 'chapter') {
    data = translate(data, resource, chapterMap);
    data.push(addPropToCOinSData('rft.genre', 'bookitem'));
    data.push(addPropToCOinSData('rft_val_fmt', 'info:ofi/fmt:kev:mtx:book'));
  }else {
    data.push(addPropToCOinSData('rft.genre', 'document'));
  }
  return assembleUri(data);
};

/**
 * Utils - dedicated to everything that deals with models (composing, translating values, ...)
 * @module utils/modelUtils
 */
import {parseBibAuthors} from './../../converters/bibTexConverter';

/**
 * Builds a model object from a specific bibType, composing it according to its inheritance dependencies, from more general models to the specific bibType
 * @param {string} bibType - the bibType of the resource
 * @param {Object} resourceModels - the models describing resources data
 * @return {Object} model - the model corresponding to the input bibType
 */
export const getResourceModel = (bibType, resourceModels) =>{
  const model = resourceModels.individual[bibType];
  if (model) {
    // first set highestly specific props
    let properties = model.properties;
    let defaultContextualizer = model.defaultContextualizer;
    let otherProps;
    let existing;
    // then parse related categories
    model.categories.forEach((category) => {
      otherProps = resourceModels.collective[category]
                    .properties
                    .filter((prop) =>{
                      existing = properties.find((property) =>{
                        return property.key === prop.key;
                      });
                      if (existing) {
                        return false;
                      }
                      return true;
                    });
      properties = properties.concat(otherProps);

      // inherit default contextualizer
      if (resourceModels.collective[category].defaultContextualizer && !defaultContextualizer) {
        defaultContextualizer = resourceModels.collective[category].defaultContextualizer;
      }
    });
    // then finally parse common props
    otherProps = resourceModels.collective.common
                    .properties
                    .filter((prop) =>{
                      existing = properties.find((property) =>{
                        return property.key === prop.key;
                      });
                      if (existing) {
                        return false;
                      }
                      return true;
                    });
    properties = properties.concat(otherProps);

    return Object.assign({}, model, {properties: model.properties.concat(properties)}, {defaultContextualizer});
  }
  return undefined;
};

/**
 * Build a model object from a specific bibType, composing it according to its inheritance dependencies, from more general models to the specific bibType
 * @param {string} bibType - the bibType of the contextualizer
 * @param {Object} contextualizerModels - the models describing contextualizer possible data
 * @return {Object} model - the model corresponding to the input bibType
 */
export const getContextualizerModel = (bibType, contextualizerModels) =>{
  const model = contextualizerModels.individual[bibType];
  if (model) {
    // first set highestly specific props
    let properties = model.properties;
    let otherProps;
    let existing;
    // then parse related categories
    model.categories.forEach((category) => {
      otherProps = contextualizerModels.collective[category]
                    .properties
                    .filter((prop) =>{
                      existing = properties.find((property) =>{
                        return property.key === prop.key;
                      });
                      if (existing) {
                        return false;
                      }
                      return true;
                    });
      properties = properties.concat(otherProps);

    });

    // then finally parse common props
    otherProps = contextualizerModels.collective.common
                    .properties
                    .filter((prop) =>{
                      existing = properties.find((property) =>{
                        return property.key === prop.key;
                      });
                      if (existing) {
                        return false;
                      }
                      return true;
                    });
    properties = properties.concat(otherProps);
    return Object.assign({}, model, {properties});
  }
  return undefined;
};

/**
 * Transforms a (possibly not primitive : array, object, bibAuthor) value to a string-friendly value, thanks to its model's type
 * @param {Object} prop - the prop to serialize
 * @param {string} valueType - the type of the value ('string', 'stringArray', 'bibAuthor', ...)
 * @param {Object} model - the model to parse the prop against
 * @return {string} newValue - the serialized value
 */
export const serializePropAgainstType = (prop, valueType, model) => {
  if (prop === undefined) {
    return undefined;
  }
  switch (valueType) {
  case 'string':
    return prop;

  case 'stringArray':
    return prop.join(', ');

  case 'bibAuthorsArray':
    return prop.map((author) =>{
      if (author.firstName) {
        return author.firstName + ' {' + author.lastName + '}';
      }
      return author.lastName;
    }).join('; ');

  case 'objectArray' : {
    if (model.children && Array.isArray(prop)) {
      const newProp = prop.map((obj)=>{
        if (typeof obj === 'object') {
          const vals = Object.keys(obj).map((key)=>{
            if (obj[key] !== undefined) {
              return key + '=' + obj[key];
            }
            return '';
          });
          return vals.filter((part)=>{return part.length;}).join(',');
        }
        return undefined;
      });
      return newProp;
    }
    break;
  }

  default:
    return prop;
  }

};

/**
 * Transforms a string value to a complex and type-compliant value, thanks to its model's type
 * @param {Object} prop - the prop to resolve
 * @param {string} valueType - the type of the value ('string', 'stringArray', 'bibAuthor', ...)
 * @param {Object} model - the model to parse the prop against
 * @return {string|array|number|Object} newValue - the resolved value
 */
export const resolvePropAgainstType = (prop, valueType, model) => {
  if (prop === undefined) {
    // looking for a default value if no value specified
    if (model.default) {
      return model.default;
    }
    return undefined;
  // looking for restricted values
  } else if (model.values) {
    const validValue = model.values.find((value) => {
      return value === prop;
    });
    // value not allowed
    if (validValue === undefined) {
      // look for default
      if (model.default) {
        return model.default;
      }
      // or return undefined
      return undefined;
    }
  }
  switch (valueType) {
  case 'number':
    return +prop;

  case 'numberArray':
    return prop.split(',').map((number)=> {
      return +number.trim();
    });

  case 'string':
    /*
    if (model.values) {
      // nominal set of possible values
      val = model.values.some((mval)=>{
        return mval === prop;
      });
      console.log('val : ', val);
      return val;
    }
    */
    return prop;

  case 'stringArray':
    return prop.split(/,|;/).map((value)=>{return value.trim();});

  case 'bibAuthorsArray':
    return parseBibAuthors(prop);

  case 'objectArray' : {
    if (model.children && Array.isArray(prop)) {
      const newProp = prop.map((obj)=>{
        if (typeof obj === 'object') {
          const newObj = model.children.reduce((thatObj, childModel) =>{
            thatObj[childModel.key] = resolvePropAgainstType(obj[childModel.key], childModel.valueType, childModel);
            return thatObj;
          }, {});
          return newObj;
        }
        return undefined;
      });
      return newProp;
    }
  }

  default:
    return prop;
  }
};

/**
 * Populate rendering settings according to default bibtype-related settings
 * @param {Object} settings - the settings provided as input
 * @param {string} bibType - the bibType of the root section to render
 * @param {Object} settingsModel - the model to use for populating the settings
 * @return {Object} newSettings - the populated settings
 */
export const resolveSettings = (settings = {}, bibType, settingsModel) =>{
  const typeModel = {};
  for (const param in settingsModel) {
    if (settingsModel[param].default[bibType]) {
      typeModel[param] = settingsModel[param].default[bibType];
    } else {
      typeModel[param] = settingsModel[param].default.default;
    }
  }
  return Object.assign(typeModel, settings);
};

/**
 * Utils - dedicated to representing a reference/bibliography section
 * @module utils/referenceUtils
 */

const bibliographicTypes = [
  'article',
  'book',
  'booklet',
  'collection',
  'conference',
  'inbook',
  'incollection',
  'inproceedings',
  'manual',
  'mastersthesis',
  'phdthesis',
  'proceedings',
  'techreport',
  'unpublished'
];

/**
 * Checks whether a given references belongs to a "traditional" bibliographical reference (journal article, book, ...)
 * @param {string} bibType - the bibType of the resource
 * @return {boolean} isBibliographical
 */
const isBibliographical = (bibType) =>{
  const bibliType = bibliographicTypes.find((type)=> {
    return bibType === type;
  });
  return bibliType !== undefined;
};

/**
 * Filter and order a list of resources against bibliography settings
 * @param {array} sections - the sections to handle for building the list
 * @param {Object} settings - the rendering settings, among which are bibliography-making related settings
 * @return {array} references - the resulting list
 */
export const computeReferences = (sections, settings) =>{
  if (settings.referenceScope === 'document') {
    const references = sections.reduce((refs, section)=> {
      return refs.concat(section.resources.filter((resource)=> {
        return resource.inheritedVerticallyFrom === undefined;
      }));
    }, []);

    // handle filters
    const filters = (settings.referenceFilters || []) && settings.referenceFilters.split(' ');
    const filteredReferences = filters.reduce((outputReferences, filter)=> {
      switch (filter) {
      case 'has-title':
        return outputReferences.filter((ref)=> {
          return ref.title !== undefined;
        });
      case 'bibliographical':
        return outputReferences.filter((ref)=> {
          return isBibliographical(ref.bibType);
        });
      // todo : document filters
      // todo : design new filters
      default:
        return outputReferences;
      }
    }, references);

    // order references
    const order = settings.referenceSortBy;
    const sortedReferences = filteredReferences.sort((ref1, ref2)=> {
      switch (order) {
      case 'title':
        return (ref1.title && ref1.title.toLowerCase()) > (ref2.title && ref2.title.toLowerCase()) ? 1 : -1;
      case 'authors':
        const print1 = ref1.author && ref1.author.reduce((str1, author1)=> {
          return str1 + author1.lastName + '-';
        }, '');
        const print2 = ref2.author && ref2.author.reduce((str2, author2)=> {
          return str2 + author2.lastName + '-';
        }, '');
        return print1 > print2 ? 1 : -1;
      case 'year':
        return ref1.year > ref2.year ? 1 : -1;
      case 'type':
        return ref1.bibType > ref2.bibType ? 1 : -1;
      default:
        return 1;
      }
    });
    return sortedReferences;
  }
};

/**
 * Utils - section metadata & resources utils : access, filter, deletion, ...
 * @module utils/sectionUtils
 */

/**
 * Gets a value in metadata props array by its domain and key
 * @param {array} metaList - the list of metadata objects in which looking for the value
 * @param {string} domain - the domain of the prop, stored in its "domain" prop
 * @param {string} key - the key of the prop, stored in its "key" prop
 * @return {string} value - the value
 */
export const getMetaValue = (metaList, domain, key) =>{
  const prop = metaList.find((meta) =>{
    return meta.domain === domain && meta.key === key;
  });
  if (prop) {
    return prop.value;
  }
  return undefined;
};

/**
 * Sets a value in metadata props array after localized it by its domain and key
 * @param {array} metaList - the list of metadata objects in which looking for the value
 * @param {string} domain - the domain of the prop, stored in its "domain" prop
 * @param {string} key - the key of the prop, stored in its "key" prop
 * @param {string} newValue - the new value to assign to the prop
 * @return {array} newlist - the updated list
 */
export const setMetaValue = (metaList, domain, key, newValue) =>{
  const newMetaList = metaList.map((meta) =>{
    if (meta.domain === domain && meta.key === key) {
      meta.value = newValue;
    }
    return meta;
  });
  return newMetaList;
};

/**
 * Checks if the value has a metadata property
 * @param {array} metaList - the list of metadata objects in which looking for the value
 * @param {string} domain - the domain of the prop, stored in its "domain" prop
 * @param {string} key - the key of the prop, stored in its "key" prop
 * @return {boolean} hasMeta - whether the prop is there
 */
export const hasMeta = (metaList, domain, key) =>{
  if (typeof domain === 'string') {
    return getMetaValue(metaList, domain, key) !== undefined;
  }else if (domain.domain) {
    return getMetaValue(metaList, domain.domain, domain.key) !== undefined;
  }
  throw new Error('error in couple ' + domain + '_' + key + ': hasMeta method needs either a domain+key pair or a metadata prop object');
};

/**
 * Finds a specific section in a sections list through one of its metadata
 * @param {array} sections - the sections array to search in
 * @param {string} domain - the domain of the prop used to search the section, stored in its "domain" prop
 * @param {string} key - the key of the prop used to search the section, stored in its "key" prop
 * @return {Object} section - the resulting section
 */
export const findByMetadata = (sections, domain, key, value) =>{
  return sections.find((section) =>{
    const meta = getMetaValue(section.metadata, domain, key);
    return meta === value;
  });
};

/**
 * Checks if two metadata props have the same scope (domain and key)
 * @param {Object} meta1 - the first metadata prop
 * @param {Object} meta1 - the second metadata prop
 * @return {boolean} sameScope - whether the two props have the same scope
 */
export const sameMetaScope = (meta1, meta2) =>{
  return meta1.domain === meta2.domain && meta1.key === meta2.key;
};

/**
 * Delete a specific metadata prop
 * @param {array} metaList - the list of metadata objects in which looking for the value
 * @param {string} domain - the domain of the prop, stored in its "domain" prop
 * @param {string} key - the key of the prop, stored in its "key" prop
 * @return {array} updatedMetaList - the new metadata list, without the deleted prop
 */
export const deleteMeta = (metaList, domain, key) =>{
  return metaList.filter((meta)=>{
    return !(domain === meta.domain && key === meta.key);
  });
};

/**
 * Converts a bibtex metadata expression (e.g. "title", "twitter_twitter") to an object prop
 * @param {string} str - the bibtex metadata expression
 * @return {Object} metadata - the metadata prop object, without value
 */
export const metaStringToCouple = (str) =>{
  const parts = str.split('_');
  const domain = (parts.length > 1) ? parts.shift() : 'general';
  const key = parts.join('_');
  return {domain, key};
};

/**
 * Checks if a resource list contains a resource, by citeKey
 * @param {array} resourcesList - the resources list in which looking for
 * @param {Object} resource - the resource to look for
 * @return {boolean} hasResource - whether resource is present in the list
 */
export const hasResource = (resourcesList, resource) =>{
  return resourcesList.find((res) =>{
    return resource.citeKey === res.citeKey;
  }) !== undefined;
};

/**
 * Filter resources that have a specific value
 * @param {array} resourcesList - the list of resource sto filter
 * @param {string} key - the key by which filtering the resources
 * @param {string} value - the value by which filtering the resources
 * @return {array} updatedResourceList - the filtered resources list
 */
export const filterResources = (resourcesList, key, value) =>{
  return resourcesList.filter((res) =>{
    return res[key] === value;
  });
};

/**
 * Section-related validation functions
 * @todo lighten sectionConverter by putting more utils there
 * @module validators/sectionValidator
 */

import {getMetaValue, filterResources} from './../../utils/sectionUtils';

/**
 * Validates the resources of a section against resources models
 * @param {Object} section - the section to validate
 * @param {Object} models - the models to use for validation
 * @param {function(err: null, output: Object)} callback - callbacks a list of validation errors descriptions, and unmodified sections
 */
export const validateResources = (section, models, callback) =>{
  const errors = [];

  section.resources.forEach((resource) =>{
    // validate resources unicity
    const other = filterResources(section.resources, 'citeKey', resource.citeKey);
    if (other.length > 1) {
      errors.push({
        type: 'error',
        preciseType: 'invalidResource',
        sectionCiteKey: getMetaValue(section.metadata, 'general', 'citeKey'),
        resourceCiteKey: resource.citeKey,
        message: 'Resource ID ' + resource.citeKey + 'is not unique'
      });
    }
  });
  return callback(null, {errors, section});
};

